    mat3 hr = mat3(0.299,  0.587,  0.114,  0.299,  0.587,  0.114,  0.299,  0.587,  0.114) +
        	  mat3(0.701, -0.587, -0.114, -0.299,  0.413, -0.114, -0.300, -0.588,  0.886) * cs.x +
        	  mat3(0.168,  0.330, -0.497, -0.328,  0.035,  0.292,  1.250, -1.050, -0.203) * cs.y;
    return clamp(p*hr, 0., 1.);
}

float raytraceFloor(vec3 ro, vec3 rd, vec3 n, vec3 o) {
	return dot(o - ro, n) / dot(rd, n);
}





DistInfo marchScene(vec3 ro, vec3 rd) {

    DistInfo di = DistInfo(0., vec3(0.));
    vec3 rp = vec3(0.0); //ray position

    for (int i = 0; i < 65; i++) {
        rp = ro + rd * di.d;
        float ns = dfScene(rp);
        di.d += ns;
        if (ns < EPS || di.d > FAR) break;
        vec3 col = abs(rp);
        di.lc += 0.0001 / (ns * ns) * col; //distance field light contribution
    }
    return di;
}




vec3 rotx(vec3 p, float a){
    float s = sin(a), c = cos(a);
    return vec3(p.x, c*p.y - s*p.z, s*p.y + c*p.z);
}


//From Dave_Hoskins (https://www.shadertoy.com/view/4djSRW)
vec3 hash33(vec3 p)
{
    p = fract(p * vec3(443.8975,397.2973, 491.1871));
    p += dot(p.zxy, p.yxz+19.27);
    return fract(vec3(p.x * p.y, p.z*p.x, p.y*p.z));
}

//Very happy with this star function, cheap and smooth
vec3 stars(in vec3 p)
{
    // 47fps

    vec3 c = vec3(0.);
    float res = iResolution.x*1.5; // too small  and they will flicker

    // more stars?
	for (float i=0.;i<1.;i++)
    {
        vec3 q = fract(p*(.15*res))-0.5;
        vec3 id = floor(p*(.15*res));
        vec2 rn = hash33(id).xy;
        float c2 = 1.-smoothstep(0.,.6,length(q));
        c2 *= step(rn.x,.0005+i*i*0.001);
        c += c2*(mix(vec3(1.0,0.49,0.1),vec3(0.75,0.9,1.),rn.y)*0.25+0.75);
        p *= 1.4;
    }
    return c*c*.7;
}

