//SHAPES------------------------------------------------------------
vec3 polarShape(vec2 uv,float dist, float angle, float radius, float p,vec3 colour)
{
    float value = 1.0 - step(radius + p,dist);

    return vec3(value) * colour;
}

vec3 circle(vec2 uv, vec2 pos, float radius,vec3 colour, float Noise)
{
    float value = step(distance(pos,uv), radius);

    value *= Noise;

    return vec3(value) * colour;
}

//Main Code------------------------------------------------------------
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float ratio = 1.5;
    uv.x *= ratio;

    vec2 pos = vec2(0.5 * ratio,0.5);

    // Noise
    float a = noise(uv * 11. + iTime) * 0.1;
    vec3 color = vec3(0.);
    float value = 0.;

    // Snow
    float r = snoise(uv * 2. + iTime * 0.5) * ratio;
	value = step(0.0 + r ,uv.x) - step(0.4 + r,uv.x);
    color = vec3(value);

    // Circle Surround the person
    color = mix(color, circle(uv,vec2(0.5*ratio,0.5),0.4,vec3(.2,.4,.2),1.), 0.8);

    // Head
    uv -= pos;
    color += circle(uv,vec2(0.,0.2),0.1,vec3(0.2,0.2,1),r);
    color -= circle(uv,vec2(0.04,0.23),0.02,vec3(0.3,2,.2),2.);

    // Body
	uv += pos;
    value = step(0.62 + a,uv.x) - step(0.85,uv.x);
    value *= step(0.25,uv.y) - step(0.55,uv.y);
    value *= r;
    color -= vec3(value) * vec3(0,1,0);

	// Horizontal noise
    value = step(0.54,uv.x) - step(0.84,uv.x);
    value *= step(0.48 + a,uv.y) - step(0.57 + a,uv.y);
    value *= r;
	color -= vec3(value) * vec3(0.1,0.3,.4);

    // Lower body
    value = step(0.76,uv.x)- step(0.79,uv.x);
    value *= step(0.15,uv.y) - step(0.25,uv.y);
    value *= r;
    color += vec3(value) * vec3(0.2,0.2,1);

	fragColor = vec4(color,1.0);
}

