#define sat(x) clamp(x, 0., 1.)

//remaps value to be between 0 and 1
float remap01(float a, float b, float t) 
{
    return sat((t - a) / (b - a));
}

//remaps value to custom value
float remap(float a, float b, float c, float d, float t)
{
    return sat( (t - a) / (b - a) ) *(d - c) + c;
}

//makes bounding box within a set area with its own coordinate system
vec2 within(vec2 uv, vec4 rect)
{
	return (uv - rect.xy) / (rect.zw - rect.xy);    
}

//makes an eye
vec4 Eye(vec2 uv, float side, vec2 mouse, float smile)
{
    uv -= .5; //center uv

    uv.x *= side; //reflect the x axis

    float d = length(uv); //distance to center

    vec4 iris = vec4(1. * smile, .2, .1, 1.); //color of iris

    vec4 color = mix(vec4(1.), iris, smoothstep(.1,.7,d) * .7); //mix in the iris

    color.a = smoothstep(.5, .48, d); //bound the eye to this circle

    color.rgb *= 1. - smoothstep(.45, .5, d) * 0.8 * sat(-uv.y - uv.x); //make eye

	d = length(uv - mouse * 0.2); //newdistance for eye gaze

    color.rgb = mix(color.rgb, vec3(1, 1, 0), smoothstep(.3, .28, d)); //iris outline

    iris.rgb *= 1. + smoothstep(.45, .05, d); //iris color

    color.rgb = mix(color.rgb, iris.rgb, smoothstep(.24, .21, d)); //blend in new iris layer

    d = length(uv - mouse * 0.305); //new distance for pupil

    float pupilSize = mix(.25,.16, (1. - smile)); 

    color.rgb = mix(color.rgb, vec3(0.), smoothstep(pupilSize, pupilSize * .85, d)); //blend in pupils

    float time = iTime;

    vec2 offset = vec2(sin(time+uv.y*25.), sin(time+uv.x*25.)); //rotating highlights

 	offset *= 0.04 * smile;

    uv += offset;

    float highlight = smoothstep(.1, .09, length(uv-vec2(-.15, .15)));

    highlight += smoothstep(.07, .05, length(uv+vec2(-.08, .08)));

    color.rgb = mix(color.rgb, vec3(1.), highlight);			// blend in highlight

    return color;
}


vec4 Mouth(vec2 uv, float smile)
{
    uv -= 0.5;
    vec4 color = vec4(0.5, 0.18, 0.05, 1.0); //dark red
    uv.y *= 1.5; //make smile
    uv.y -= uv.x * uv.x * 2. * smile;
    uv.y -= 0.15 * smile;
    uv.x *= mix(2.5,1.,smile);
    float d = length(uv);
    color.a = smoothstep(.5, .48, d); //bind mouth to this shape

    float tongueD = length(uv + vec2(0., .5));
	color.rgb = mix(color.rgb, vec3(1.,.5,.5), smoothstep(.5,.2,tongueD));

    float teethD = length(uv - vec2(0.35, -.5 - (smile * 0.3)));

    color.rgb = mix(color.rgb, vec3(1.), smoothstep(.26,.24,teethD));
    return color;
}

vec4 Head(vec2 uv)
{
    vec2 xy;
    vec4 texColor = texture(iChannel0,xy);//Get the pixel at xy from iChannel0

    vec4 color = vec4(0.0, 0.8, 0.3, 1.0);

    float d = length(uv); //distance to center

    color.a = smoothstep(.5, .49, d);

    float edgeShadow = remap01(0.35, 0.5, d);

	edgeShadow *= edgeShadow;

    color.rgb *= 1. - edgeShadow * .5;

    color.rgb = mix(color.rgb, vec3(0.0, 0.4, 0.2), smoothstep(.47,.48,d)); //outline

    float highlight = smoothstep(.41,.405,d) * .89;
    highlight *= remap(.41 - .1, .0, .75, .0, uv.y);
    color.rgb = mix(color.rgb, vec3(1.), highlight);

     d = length(uv - vec2(0.25, -.1));
    float leftFreckB = smoothstep(.05, .0025, d) * .8;
    color.rgb = mix(color.rgb, vec3(.0, .7, .1), leftFreckB);

    d = length(uv - vec2(0.27, -.19));
    float rightFreckB = smoothstep(.05, .0025, d) * .8;
    color.rgb = mix(color.rgb, vec3(.0, .7, .1), rightFreckB);

    d = length(uv - vec2(0.19, -.2));
    float midFreckB = smoothstep(.05, .0025, d) * .8;
    color.rgb = mix(color.rgb, vec3(.0, .7, .1), midFreckB);


    return color;
}

vec4 Monster(vec2 uv, vec2 mouse, float smile)
{
    vec4 color = vec4(0.0);
    float side = sign(uv.x); //either -1 or + 1
    uv.x = abs(uv.x);
    vec4 head = Head(uv);
    vec4 eye = Eye(within(uv, vec4(.03,-.1,.26,.35)), side, mouse, smile);
    vec4 mouth = Mouth(within(uv, vec4(-.3,-.4, .3, -.1)), smile);
    color = mix(color, head, head.a);
    color = mix(color, eye, eye.a);
    color = mix(color, mouth, mouth.a);
    return color;
}

