    vec3 mask;

    float dist = 0.0;
    vec3 localpos = pos;
    vec3 localfloor = floorpos;
    float stepsize = 1.0;
    float count = 0.0;


    for (int i = 0; i < 12; i++) {
        float len = map(localfloor);
        if (len > 0.0) {
            localpos *= 3.0;
            localpos = localpos-sign(dir)*1.5+1.5;

            localfloor = floor(localpos-sign(dir)*0.001);
            stepsize /= 3.0;

