            vec3 dists = abs((sign(dir)*0.5+0.5)-(localpos-localfloor))*1.0/abs(dir);

            float m = min(min(dists.x,dists.y),dists.z);

            mask = step(dists,vec3(m));

            pos += m*dir*stepsize;
            dist += m*stepsize;
            localpos += m*dir;
            localfloor += mask*sign(dir);

            floorpos += mask*sign(dir)*stepsize;
        }
    }

