vec3 march(in vec3 ro, in vec3 rd, inout float t){

    vec3 p = ro+rd;
    for(int i = 0; i < STEPS; ++i){
        float d = sdf(p);
        t += d;
        p += rd*d;

        if(d < 0.01 || t > FAR){
            break;
        }

    }
    return p;
}


