    g *= 6.28*10.*cos(6.28*10.*n) * 8./R.y;
  //v = tanh(2.*abs(v) / (abs(g.x)+abs(g.y))); // better antialiasing than smoothstep
    v = tanh(min(2.*abs(v) / (abs(g.x)+abs(g.y)),10.)); // some systems dislike tanh(big)
    n = floor(n*20.)/20.;
	O = v * (.5+.5*cos(12.*n+vec4(0,2.1,-2.1,0)));
}

