/*
   Epicycloid facts:
  ----------------
	- (a) There are four curves which are closely related: epicycloid, epitrochoid, hypocycloid, and
		  the hypotrochoid. They are all traced by a point P on a circle of radius b which rolls
          around a fixed circle of radius a.
	- (b) For an epicycloid, the circle of radius b rolls on the outside of the circle of radius a. The
          point P is on the edge of the circle of radius b.
	- (c) If a = b: cardioid.
    - (d) If a = 2b, nephroid.
*/

#define res_            iResolution
#define time_           iTime
#define trace_len_      222
#define eps_            2.

#define crot(a)  mat2(cos(a),-sin(a),sin(a),cos(a))

vec2  domain(vec2 uv, float s);
vec2  parametric(float t);
float trace(vec2 p, float t);
float _union_soft(float a, float b, float r);

void mainImage(out vec4 o, vec2 f) {
	vec2 p = domain(f, 5.);
  	p *= crot(time_*.2);

    // t -> [0., 5.]
    float t = time_;

    // Astroid traces
    float dtrace = smoothstep(0.08, 0.001, trace(p, t));
    float dtrace_s0 = smoothstep(0.16, 0.001, trace(p, t));

    // Axis
    float ax = min(
        smoothstep(0., 0.02, abs(p.x)),
        smoothstep(0., 0.02, abs(p.y))
    );
    // Origin
    float org = smoothstep(0.09, 0., length(p)-.01);

    // Palette 
    vec3 gray   = vec3(.2);
    vec3 white  = vec3(5.);

    // Shading
    vec3 c  = vec3(0.);
    c = mix(c, gray, fract(1.-ax));
    c = mix(c, white, org);

    c = mix(c, vec3(0., 0.3, 1.), dtrace_s0);
    c = mix(c, gray, dtrace*p.x);
    c = mix(c, vec3(.1, 1.3, .1), dtrace*abs(p.y)*length(p));
    c = mix(c, vec3(.1, 1.3, .1), dtrace*-abs(p.y)*length(p));

    c.b += dtrace_s0;


    o = vec4(c, 1.);
}

float _union_soft(float a, float b, float r) {
	float e = max(r - abs(a - b), 0.);
	return min(a, b) - e*e*0.25/r;
}

vec2 parametric(float t) {
	float a = 2.;
    float b = 1.2;
    return vec2( (a+b)*cos(t) - b*cos((a/b + 1.) * t), (a+b)*sin(t) - b*sin((a/b + 1.) * t) );
}

float trace(vec2 p, float t) {
    float d = 1e10;
    float e = 0.;
    for(int i = 0; i < trace_len_; i++) {
    	d  = _union_soft(d, distance(p, parametric(t-e)), sin(time_)*.5);
        e += eps_; 
    }
    return d;
}

vec2 domain(vec2 uv, float s) {
    return (2. * uv.xy - res_.xy) / res_.y * s;
}

float hash(float i)
{
    //%in 1d, return a slope
	float h = i * 127.1;
	float p = -1. + 2. * fract(sin(h) * 43758.1453123);
    return p;//sin(p*6.283 + iTime);
}
float perlin_noise_1d(float d)
{
    float i = floor(d);
    float f = d - i;

    //%interpolation
    //float y = 6.0 * pow(f, 5.0) - 15.0 * pow(f, 4.0) + 10.0 * pow(f, 3.0);
    float y = f*f*f* (6. * f*f - 15. * f + 10.);

    float slope1 = hash(i);
    float slope2 = hash(i + 1.0);
    float v1 = f;
    float v2 = f - 1.0;

    float r = mix(slope1 * v1, slope2 * v2, y);
    r = r * 0.5 + 0.5; //%map to range 0 ~ 1
    return r;
}
float perlin_noise_1d_sum(float x)
{
    const float persistence = 0.4;
    const float frequence = 1.0;
    float amp = persistence,
        	freq = frequence;
    float total = 0.0;
	for(int i = 0; i < 5; ++i)
    {
    	total += perlin_noise_1d(x * freq) * amp;

        amp *= persistence;
        freq *= 2.0;
    }

    return total;
}
void mainImage(out vec4 fragColor,  in vec2 fragCoord)
{
    float noiseY = perlin_noise_1d(iTime + fragCoord.x * 0.01);
    float cy = noiseY - fragCoord.y / iResolution.y;
    cy = smoothstep(0.0, 1.0 / iResolution.y, cy); //for AA purpose
    fragColor = mix(vec4(1.0), vec4(0.0), 1.0 - cy);
}

float map (vec3 p) {

    vec3 q = fract(p) * 2.0 - 1.0;

    return length(q) - 0.25;

}

float trace(vec3 o, vec3 r) {
    float t = 0.0;
    for(int i = 0; i < 32; ++i) {
       vec3 p = o+r*t;
        float d = map(p);
        t += d*1.0;
    }
    return t;
}

void mainImage ( out vec4 fragColor, in vec2 fragCoord) {

    vec4 col = vec4(0,0,0,1);
    vec2 uv = fragCoord.xy/iResolution.xy;
    uv -= 0.5;

    if(length(uv) < 0.15 && uv.x > -0.05) {

        col = mix(vec4(0.52,0.4,0,1), vec4(0.), length(uv));
        col = mix(col, vec4(0), smoothstep(0.0, 0.2, uv.y));
        col = mix(col, vec4(0), smoothstep(0.0, -0.2, uv.y));
    }
    else {

    uv = uv*2.0-0.4;
    uv.x = iResolution.x/iResolution.y;
    vec3 r = normalize(vec3(uv, 0.8));
    float time = iTime;
    vec3 o = vec3(0.0, 0.0, time);
    float t = trace(o,r);
    float fog = 1.0/ (1.0 + t * t * 0.1);
    vec3 fc =  vec3(fog);

        col = vec4(fc, 2.0);
    }
    fragColor = col;
}

// Cook-Torrance implementation and Schilck/Beckmann/Smith approximation
//
// References:
// Mathematics for 3d Game Programming & Computer Graphics (second edition)
// http://simonstechblog.blogspot.fr/2011/12/microfacet-brdf.html
// http://graphicrants.blogspot.fr/2013/08/specular-brdf-reference.html
// http://renderwonk.com/publications/s2010-shading-course/gotanda/course_note_practical_implementation_at_triace.pdf
// https://de45xmedrsdbp.cloudfront.net/Resources/files/2013SiggraphPresentationsNotes-26915738.pdf
//
// Distance function
// http://iquilezles.org/www/articles/distfunctions/distfunctions.htm

const int MAX_MARCHING_STEPS = 255;
const float MIN_DIST = 0.0;
const float MAX_DIST = 100.0;
const float EPSILON = 0.001;
const float GRAD_STEP = 0.1;
const float PI_4 = 0.78539816339744;
const float PI_2 = 1.57079632679489;
const float PI = 3.14159265358979;
const float PI2 = 6.28318530717958;

// sqrt(2/PI)
const float GSchilkFactor = 0.79788456080286;


struct Ray
{
    vec3 origin;
    vec3 direction;
};

struct MaterialPhong
{
    vec3 Ka;
    vec3 Kd;
    vec3 Ks;
    float e;
};

// For typical dielectric materials, n is between 1.3 and 1.7    
struct MaterialPBR
{
    vec3 Kd;
    vec3 Ks;
    float m; 	// roughness 0.0 - 1.0
    float n;	// reflectance 1.0 20.0
};

struct Light
{
    vec3 P;		// pos
    vec3 Kd;	// diff
    vec3 Ks;	// spec
    float I;	// intensity
};

// Union
float opU( float d1, float d2 )
{
    return min(d1,d2);
}

// Substraction
float opS( float d1, float d2 )
{
    return max(-d1,d2);
}

// Intersection
float opI( float d1, float d2 )
{
    return max(d1,d2);
}

// Twist operator
vec3 opTwist( vec3 p, float sa )
{
    float c = cos(sa*p.y);
    float s = sin(sa*p.y);
    mat2  m = mat2(c,-s,s,c);
    vec3  q = vec3(m*p.xz,p.y);
    return q;
}

// Box
float sdBox( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}

// Sphere
float sdSphere( vec3 p, float r )
{
     return length(p)-r;
}

float sdCircle(vec2 uv, vec2 center, float r)
{
    vec2 c = (uv - center);
    return 1.0-clamp(dot(c,c)/r,0.0,1.0);
}

// Rotation matrix from euler angle (radian)
mat3 fromEuler(vec3 ang) {
	vec2 a1 = vec2(sin(ang.x),cos(ang.x));
    vec2 a2 = vec2(sin(ang.y),cos(ang.y));
    vec2 a3 = vec2(sin(ang.z),cos(ang.z));
    mat3 m;
    m[0] = vec3(a1.y*a3.y+a1.x*a2.x*a3.x,a1.y*a2.x*a3.x+a3.y*a1.x,-a2.y*a3.x);
	m[1] = vec3(-a2.y*a1.x,a1.y*a2.y,a2.x);
	m[2] = vec3(a3.y*a1.x*a2.x+a1.y*a3.x,a1.x*a3.x-a1.y*a3.y*a2.x,a2.y*a3.y);
	return m;
}

// The scene
float sdScene(vec3 p)
{     
    // twist
	//p = fromEuler( vec3(0.0,0.0,p.y) ) * p;
    p = opTwist(p, 0.5);

    //return sdSphere(p,1.0);
    //return sdBox(p,vec3(0.75,0.75,0.5));
    //return opS( sdSphere(p,1.0), sdBox(p,vec3(0.75,0.75,0.5)) );
    //return opS( sdBox(p,vec3(0.8,0.8,0.5)), sdSphere(p,1.0) );
    return opU( sdBox(p,vec3(2.0,2.0,2.0)), sdSphere(p,2.7) );
}

// Compute through gradient
vec3 sdSceneNormal(vec3 p) 
{
    return normalize(vec3(
        sdScene(vec3(p.x + GRAD_STEP, p.y, p.z)) - sdScene(vec3(p.x - GRAD_STEP, p.y, p.z)),
        sdScene(vec3(p.x, p.y + GRAD_STEP, p.z)) - sdScene(vec3(p.x, p.y - GRAD_STEP, p.z)),
        sdScene(vec3(p.x, p.y, p.z  + GRAD_STEP)) - sdScene(vec3(p.x, p.y, p.z - GRAD_STEP))
    ));
}

// Ray marching algorithm
float sdRayMarching(Ray ray, float start, float end)
{
    float depth = start;
    for (int i = 0; i < MAX_MARCHING_STEPS; i++) {
        float dist = sdScene(ray.origin + depth * ray.direction);
        if (dist < EPSILON) {
			return depth;
        }
        depth += dist;
        if (depth >= end) {
            return end;
        }
    }
    return end;
}

// Compute ray
Ray sdRay(vec3 origin, vec2 frag, vec2 res, float fov)
{
    Ray ray;
    ray.origin = origin;
    vec2 xy = frag - res / 2.0;
    float z = res.y / tan(fov / 2.0);
    ray.direction = normalize(vec3(xy, -z));

    return ray;
}


// Ka,Kd,Ks : Ambient/Diffuse/Specular
// Ki : intensity
// e : Specular exponent
//
// E : Eye position
// P : Pixel position
// N : Normal
// LP : Light Position
vec3 Phong(vec3 Ka, vec3 Kd, vec3 Ks, float Ki, float e, vec3 E, vec3 P, vec3 N, vec3 LP)
{
    vec3 color = Ka;
    vec3 L = normalize(LP-P);
    vec3 V = normalize(E-P);
    vec3 R = reflect(-L,N);
    //vec3 H = normalize(V + R);

    float NDotL = dot(N,L);
    if(NDotL < 0.0)
    {
        return color;
    }

    // Diffuse
    color += NDotL * Kd;

    // Specular or NDotH
    float NDotR = max(0.0, dot(V,R));
    color += pow(NDotR, e) * Ks;

    return color;
}

// Fresnel term Cook-Torrance
float FCook(float LDotH, float n)
{
    float gmLdotH = (n-LDotH);
    float gpLdotH = (n+LDotH);
    float a = (LDotH * gpLdotH - 1.0);
    float b = (LDotH * gmLdotH + 1.0);
    float ab = a / b;
    ab *= ab;
    return 0.5 * (gmLdotH*gmLdotH) / (gpLdotH*gpLdotH) * ( ab+1.0 );
}

// Fresnel term Schilk approximation
float FSchilck(float LDotH, float n)
{
    float f0 = (1.0-n)/(1.0+n);
    f0 *= f0;     
    return f0 + (1.0 - f0) * pow(1.0-LDotH,5.0);
}

// Cook-Torrance distribution
// m : roughness
// ref : Book
float DCook(float NDotH, float m)
{
    float NDotH2 = NDotH * NDotH;
    float NDotH2m = NDotH2 * m * m;
    return exp((NDotH2 - 1.0) / NDotH2m) / ( 4.0 * NDotH2 * NDotH2m);
}

// Blinn distribution
float DBlinn(float NDotH, float m)
{
    // Blinn distribution
    float shininess = 2.0 / ( m * m ) - 2.0;
    return ( shininess + 2.0 ) / 2.0 * pow( NDotH, shininess );
}

// Beckmann distribution (isotropic)
// m : roughness
// ref : http://simonstechblog.blogspot.fr/2011/12/microfacet-brdf.html
float DBeck(float NDotH, float m)
{
    float NDotH2 = NDotH * NDotH;
    float NDotH2m = NDotH2 * m * m;
    return exp((NDotH2 - 1.0) / NDotH2m) / (PI * NDotH2 * NDotH2m);
}

// GGX / Trowbridge-Reitz (isotropic)
// [Walter et al. 2007, "Microfacet models for refraction through rough surfaces"]
float DGGX(float NDotH, float m)
{
   	float m2 = m * m;
	float d = ( NDotH * m2 - NDotH ) * NDotH + 1.0;
	return m2 / ( PI*d*d );
}

// Geometric term Smith using Schlick approximation
float GSmith(float LDotH, float m)
{
    float k = m*GSchilkFactor;
    float f = LDotH / (LDotH*(1.0-k)+k);
    return f*f;
}

// Geometry term is used for describing how much the microfacet is blocked by other microfacet
float GCook(float NDotH, float NDotV, float LDotH, float NDotL)
{
    float a = 2.0 * NDotH / LDotH;
    return min(1.0, min(NDotV,NDotL)*a);  
}

// GImplicit
// It is called implicit because when it is used, the microfacet BRDF will only depends on Fresnel equation and distribution function
float GImplicit(float NDotV, float NDotL)
{
	return NDotL * NDotV;
}

// E : Eye position
// P : Pixel position
// N : Normal
// LP : Light Position
vec3 CookTorrance(MaterialPBR material, vec3 E, vec3 P, vec3 N, vec3 LP)
{
    vec3 L = normalize(LP-P);
    float NDotL = dot(N,L);
    if(NDotL <= 0.0)
    {
        return vec3(0.0,0.0,0.0);
    }

    vec3 V = normalize(E-P);
    vec3 H = normalize(L+V);

    float NDotV = min(dot(N,V) + 1e5,1.0);
    float NDotH = dot(N,H);
    float LDotH = dot(L,H); // == dot(V,H)


    // Compute Fresnel factor
    float Fs = FCook(LDotH, material.n);
    float Fd = FCook(NDotL, material.n);
    //float Fd = FSchilck(LDotH, material.n);

    // Compute Distribution Factor
    //float D = DCook(NDotH, material.m);
    //float D = DBeck(NDotH, material.m);
    float D = DGGX(NDotH, material.m);

    // Compute Geometrical Factor
    //float G = GImplicit(NDotV,NDotL);
    //float G = GCook(NDotH, NDotV, LDotH, NDotL);
    float G = GSmith(LDotH,material.m);

    vec3 Spec = material.Ks * Fs * D * G / ( PI * NDotV * NDotL); 
    vec3 Diff = material.Kd * (1.0 - Fd) ;
    return (Spec + Diff) * vec3(1.0) * NDotL;
}


// E : Eye position
// P : Pixel position
// N : Normal
// LP : Light Position
vec3 BRDF2(MaterialPBR material, vec3 E, vec3 P, vec3 N, vec3 LP)
{
    vec3 L = normalize(LP-P);
    float NDotL = dot(N,L);
    if(NDotL <= 0.0)
    {
        return vec3(0.0,0.0,0.0);
    }

    vec3 V = normalize(E-P);
    vec3 H = normalize(L+V);

    float NDotV = min(dot(N,V) + 1e5,1.0);
    float NDotH = dot(N,H);
    float LDotH = dot(L,H); // == dot(V,H)

    // Compute Distribution Factor
    //float D = DCook(NDotH, material.m);
    //float D = DBeck(NDotH, material.m);
    float D = DGGX(NDotH, material.m);

    // Compute Geometrical Factor
    //float G = GImplicit(NDotV,NDotL);
    //float G = GCook(NDotH, NDotV, LDotH, NDotL);
    float G = GSmith(LDotH,material.m);

    // Compute Fresnel factor
    // Smith approx
    vec3 Fs = material.Ks * (1.0 - material.Ks) * pow(1.0 - LDotH, 5.0);
    vec3 Fd = material.Ks * (1.0 - material.Ks) * pow(1.0 - NDotL, 5.0);    

	vec3 Spec = Fs * D * G / ( NDotV * NDotL * PI );
	vec3 Diff = material.Kd * ( 1.0 - Fd );
    return (Spec + Diff) * vec3(1.0) * NDotL;
}

// Unreal fashion Fresnel term
// E : Eye position
// P : Pixel position
// N : Normal
// LP : Light Position
// TriAce : http://renderwonk.com/publications/s2010-shading-course/gotanda/course_note_practical_implementation_at_triace.pdf
vec3 BRDF3(MaterialPBR material, vec3 E, vec3 P, vec3 N, vec3 LP)
{
    vec3 L = normalize(LP-P);
    float NDotL = dot(N,L);
    if(NDotL <= 0.0)
    {
        return vec3(0.0,0.0,0.0);
    }

    vec3 V = normalize(E-P);
    vec3 H = normalize(L+V);

    float NDotV = min(dot(N,V) + 1e5,1.0);
    float NDotH = dot(N,H);
    float LDotH = dot(L,H); // == dot(V,H)

    // Compute Distribution Factor
    //float D = DCook(NDotH, material.m);
    //float D = DBeck(NDotH, material.m);
    float D = DGGX(NDotH, material.m);

    // Compute Geometrical Factor
    //float G = GImplicit(NDotV,NDotL);
    //float G = GCook(NDotH, NDotV, LDotH, NDotL);
    float G = GSmith(LDotH,material.m);

    // Compute Fresnel factor
    // Smith approx
    vec3 Fs = material.Ks * (1.0 - material.Ks) * pow(1.0 - LDotH, 5.0);
    vec3 Fd = material.Ks * (1.0 - material.Ks) * pow(1.0 - NDotL, 5.0);    

	vec3 Spec = Fs * D * G / ( NDotV * NDotL * PI );
	vec3 Diff = material.Kd * ( 1.0 - Fd );
    return (Spec + Diff) * vec3(1.0) * NDotL;
}

vec3 Light1(vec3 E, vec3 P, vec3 N)
{
    // Light position
    vec3 LP = vec3(0.0, 0.0, 6.0);

    mat3 rot = fromEuler( vec3(0.0, iTime*0.5, iTime*0.5) );
