    rp.xy *= rot(IGT * 0.0125);
    //pModPolar(rp.yx, 16.);
    return sdSpikeBox(rp,  

                      vec3(
                          0.0, 
                          0.2 , 
                          0.0), 

                      0.0000);
}

// Used for Fade effect
float mapTo(float x, float minX, float maxX, float minY, float maxY) {
    float a = (maxY - minY) / (maxX - minX);
    float b = minY - a * minX;
    return a * x + b;
}

//Shane - hue rotation. I think I'vereturn sdSpikeBox(rp,  vec3(0.0, 1.5 + smoothstep(0., 1., sound.w), 0.0), w); used this in pretty much all of my shaders so far :)
vec3 rotHue(vec3 p, float a){
    vec2 cs = sin(vec2(1.570796, 0) + a);
    mat3 hr = mat3(0.299,  0.587,  0.114,  0.299,  0.587,  0.114,  0.299,  0.587,  0.114) +
        	  mat3(0.701, -0.587, -0.114, -0.299,  0.413, -0.114, -0.300, -0.588,  0.886) * cs.x +
        	  mat3(0.168,  0.330, -0.497, -0.328,  0.035,  0.292,  1.250, -1.050, -0.203) * cs.y;
    return clamp(p*hr, 0., 1.);
}

