float sqr(float x) {
    return x * x;
}

vec2 screenToWorld(vec2 sp) {
    const float screenSize = 6.;
    vec2 fp = ( sp.xy / iResolution.y );
    fp.x = fp.x * screenSize - (screenSize * iResolution.x / iResolution.y) / 2.;
    fp.y = fp.y * screenSize - screenSize / 2.;

    return fp;
}

vec2 warp(vec2 wp, float f, float s, float ts) {
	vec2 ret;

    vec2 v;

    v.x = wp.x * cos(iTime * ts) + wp.y * sin(iTime * ts);;
    v.y = wp.x * sin(iTime * ts) - wp.y * cos(iTime * ts);;

    ret.x = wp.x + cos(v.y * f) * s;
    ret.y = wp.y + cos(v.x * f) * s;

    return ret;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 fp = screenToWorld( gl_FragCoord.xy );


    const float spd1 =   1.01;
