    float gt1 = spd1 * iTime;
    float gt2 = spd2 * iTime;

    vec2 cp1 = vec2(sin(gt1), cos(gt1)) * r;
    vec2 cp2 = vec2(sin(gt2), cos(gt2)) * r;
    cp2 = screenToWorld(iMouse.xy);

    vec2 fpw1 = warp(fp,  5.0, 0.3,  0.3);
    vec2 fpw2 = warp(fp, 17.0, 0.2, -0.1);

    float d = 0.0;
    float d1 = max(distance(fpw1, cp1) + 0.3 , 0.0);
    float d2 = max(distance(fpw2 ,cp2) + 0.5, 0.0);
    d = d1 * d2 * 0.3;
    d = 1.0 - (d * d);
//    d = sin(d * 0.4) * 4.0;
    if (d > 0.0) {
        float od1 = distance(fp, cp1);
        float od2 = distance(fp, cp2);
        d = sin((od1 * od2 - iTime * 1.0) * 4.0) * 0.1 + 0.24;
        fragColor = vec4(0.75 - d, 0, d, 1.0);
    }
    else if (d < -1.0) {
        float od1 = distance(fp, cp1);
        float od2 = distance(fp, cp2);
        d = sin((od1 * od2 + iTime * 4.0) * 1.7) * 0.1 + 0.9;
        fragColor = vec4(d, 1.7 - d, 0.0, 1.0);
    }
    else {
    	d = abs(d - 0.5);
    	fragColor = vec4(-d);
    }
}

