float map (vec3 p) {

    vec3 q = fract(p) * 2.0 - 1.0;

    return length(q) - 0.25;

}

float trace(vec3 o, vec3 r) {
    float t = 0.0;
    for(int i = 0; i < 32; ++i) {
       vec3 p = o+r*t;
        float d = map(p);
        t += d*0.5;
    }
    return t;
}


