{
   float hypo = distance(vec2(pos.x + mod(time,2.0)* dir.x *xs,
                              pos.y + mod(time,2.0)* dir.y *ys),_uv);

   return size / pow(length (hypo),1.8);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy-iResolution.xy*0.5) / iResolution.xy;    
    uv *= 2.0;

    float col = 0.0;

    for(int i=0; i<LIGHT_CNT;i++)
        {
        col += point(iTime,uv,vec2(sin(iTime + float(i)*0.5)*2.0,4.0), 0.5,4.0,vec2(0.0,-0.8),POINT_SIZE);            
        }        
    col *= BRIGHTNESS;


	fragColor = vec4(1.0*col,1.0*col,1.0*col,1.0);
}

//
// CHANGE THIS MODE DEFINE TO TRY DIFFERENT OPTIONS
//  
// Sorry, only looks right at 1920x1080 non-full-screen.
//

// 0 Red ......... NoAA
// 1 Orange ...... NoAA + 2x2 Programmable Pixel Locations + Gaussian Resolve 
// 2 Gold ........ NoAA + Jittered Pixel Locations + Noise
// 3 Yellow ...... NoAA + Jittered Pixel Locations + Gaussian Resolve + Noise
// 4 Lime ........ 2xSGSSAA + Box Filter
// 5 Green ....... 2xSGSSAA + Jittered Pixel Locations + Gaussian Resolve + Noise
// 6 BlueGreen ... 4xSGSSAA + Box Filter
// 7 Blue ........ 4xSGSSAA + Jittered Pixel Locations + Gaussian Resolve + Noise
#define MODE 1

//================================================================================

#define F1 float
#define F2 vec2
#define F3 vec3
#define F4 vec4

// Convert from linear to sRGB.
F1 Srgb(F1 c){return(c<0.0031308?c*12.92:1.055*pow(c,0.41666)-0.055);}

// Convert from sRGB to linear.
F1 Linear(F1 c){return(c<=0.04045)?c/12.92:pow((c+0.055)/1.055,2.4);}    

//
// Semi-Poor Quality Temporal Noise
//

// Base.
// Ripped ad modified from: https://www.shadertoy.com/view/4djSRW
F1 Noise(F2 p,F1 x){p+=x;
 F3 p3=fract(F3(p.xyx)*10.1031);
 p3+=dot(p3,p3.yzx+19.19);
 return fract((p3.x+p3.y)*p3.z)*2.0-1.0;}

// Step 1 in generation of the dither source texture.
F1 Noise1(F2 uv,F1 n){
 F1 a=1.0,b=2.0,c=-12.0,t=1.0;   
 return (1.0/max(a*4.0+b*4.0,-c))*(
  Noise(uv+F2(-1.0,-1.0)*t,n)*a+
  Noise(uv+F2( 0.0,-1.0)*t,n)*b+
  Noise(uv+F2( 1.0,-1.0)*t,n)*a+
  Noise(uv+F2(-1.0, 0.0)*t,n)*b+
  Noise(uv+F2( 0.0, 0.0)*t,n)*c+
  Noise(uv+F2( 1.0, 0.0)*t,n)*b+
  Noise(uv+F2(-1.0, 1.0)*t,n)*a+
  Noise(uv+F2( 0.0, 1.0)*t,n)*b+
  Noise(uv+F2( 1.0, 1.0)*t,n)*a+
 0.0);}

// Step 2 in generation of the dither source texture.
F1 Noise2(F2 uv,F1 n){
 F1 a=1.0,b=2.0,c=-2.0,t=1.0;   
 return (1.0/(a*4.0+b*4.0))*(
  Noise1(uv+F2(-1.0,-1.0)*t,n)*a+
  Noise1(uv+F2( 0.0,-1.0)*t,n)*b+
  Noise1(uv+F2( 1.0,-1.0)*t,n)*a+
  Noise1(uv+F2(-1.0, 0.0)*t,n)*b+
  Noise1(uv+F2( 0.0, 0.0)*t,n)*c+
  Noise1(uv+F2( 1.0, 0.0)*t,n)*b+
  Noise1(uv+F2(-1.0, 1.0)*t,n)*a+
  Noise1(uv+F2( 0.0, 1.0)*t,n)*b+
  Noise1(uv+F2( 1.0, 1.0)*t,n)*a+
 0.0);}

// Compute temporal dither from integer pixel position uv.
F1 Noise3(F2 uv){return Noise2(uv,fract(iTime));}    

// Energy preserving dither, for {int pixel pos,color,amount}.
F1 Noise4(F2 uv,F1 c,F1 a){
 // Grain value {-1 to 1}.
 F1 g=Noise3(uv)*2.0;
 // Step size for black in non-linear space.
 F1 rcpStep=1.0/(256.0-1.0);
 // Estimate amount negative which still quantizes to zero.
 F1 black=0.5*Linear(rcpStep);
 // Estimate amount above 1.0 which still quantizes to 1.0.
 F1 white=2.0-Linear(1.0-rcpStep);
 // Add grain.
 return clamp(c+g*min(c+black,min(white-c,a)),0.0,1.0);}

//
// Pattern
//

// Dummy shader given pixel position.
F1 Shade(F2 p){
 p.xy+=sin(iTime)*2.0;
 F2 pp=F2(p.x+p.y/16.0,p.y+p.x/16.0);
 pp*=pp;
 F1 x=sin(pp.x/800.0)>0.0?0.5:0.0;
 F1 y=sin(pp.y/1000.0)>0.0?0.5:0.0;
 return x+y;}

// 4xMSAA pattern for quad given integer coordinates.
//
//  . x . . | < pixel
//  . . . x |
//  x . . .
//  . . x .
//
//  01
//  23
//
F2 Quad4(F2 pp){
 int q=(int(pp.x)&1)+((int(pp.y)&1)<<1);
 if(q==0)return pp+F2( 0.25,-0.25);
 if(q==1)return pp+F2( 0.25, 0.25);
 if(q==2)return pp+F2(-0.25,-0.25);
         return pp+F2(-0.25, 0.25);}

// Rotate {0.0,r} by a {-1.0 to 1.0}. 
F2 Rot(F1 r,F1 a){return F2(r*cos(a*3.14159),r*sin(a*3.14159));}

// Rotate {pp} by a {0.0 to 1.0}. 
F2 Rot2(F2 pp,F1 a){
 a*=3.14159*2.0;
 F1 cosA=cos(a);
 F1 sinA=sin(a);
 return F2(dot(pp,F2(cosA,sinA)),dot(pp,F2(-sinA,cosA)));}

//
// SINGLE SAMPLE RESOLVES
//

// Single center sample.
//
//  x
//
F1 ResolveNoAA(F2 pp){return Shade(pp);}

//
// FIXED QUAD RESOLVES
//

// Single center sample using Quad4 sample positions (no filtering).
//
//  x
//
F1 ResolveQuad4(F2 pp){return Shade(Quad4(floor(pp)));}

// Gaussian filtered.
void Quad4Gaus(inout F1 sumC,inout F1 sumW,F2 pp,F2 mm){
 F2 jj=Quad4(pp);
 F1 c=Shade(jj);
 F2 vv=mm-jj;
 F1 w=exp2(-2.0*dot(vv,vv));    
 sumC+=c*w; sumW+=w;}   

// Single center sample using Quad4 sample positions (with guassian filtering).
//
//  . x x x . 
//  x x x x x 
//  x x x x x 
//  x x x x x 
//  . x x x . 
//
F1 ResolveQuad4Gaus(F2 pp){
 pp=floor(pp);
 F1 sumC=0.0;
 F1 sumW=0.0;
 Quad4Gaus(sumC,sumW,pp+F2(-1.0,-2.0),pp);
 Quad4Gaus(sumC,sumW,pp+F2( 0.0,-2.0),pp);
 Quad4Gaus(sumC,sumW,pp+F2( 1.0,-2.0),pp);
 Quad4Gaus(sumC,sumW,pp+F2(-2.0,-1.0),pp);
 Quad4Gaus(sumC,sumW,pp+F2(-1.0,-1.0),pp);
 Quad4Gaus(sumC,sumW,pp+F2( 0.0,-1.0),pp);
 Quad4Gaus(sumC,sumW,pp+F2( 1.0,-1.0),pp);
 Quad4Gaus(sumC,sumW,pp+F2( 2.0,-1.0),pp);
 Quad4Gaus(sumC,sumW,pp+F2(-2.0, 0.0),pp);
 Quad4Gaus(sumC,sumW,pp+F2(-1.0, 0.0),pp);
 Quad4Gaus(sumC,sumW,pp+F2( 0.0, 0.0),pp);
 Quad4Gaus(sumC,sumW,pp+F2( 1.0, 0.0),pp);
 Quad4Gaus(sumC,sumW,pp+F2( 2.0, 0.0),pp);
 Quad4Gaus(sumC,sumW,pp+F2(-2.0, 1.0),pp);
 Quad4Gaus(sumC,sumW,pp+F2(-1.0, 1.0),pp);
 Quad4Gaus(sumC,sumW,pp+F2( 0.0, 1.0),pp);
 Quad4Gaus(sumC,sumW,pp+F2( 1.0, 1.0),pp);
 Quad4Gaus(sumC,sumW,pp+F2( 2.0, 1.0),pp);
 Quad4Gaus(sumC,sumW,pp+F2(-1.0, 2.0),pp);
 Quad4Gaus(sumC,sumW,pp+F2( 0.0, 2.0),pp);
 Quad4Gaus(sumC,sumW,pp+F2( 1.0, 2.0),pp);
 return sumC/sumW;}

// Disc filtered.
void Quad4Disc(inout F1 sumC,inout F1 sumW,F2 pp,F2 mm){
 F2 jj=Quad4(pp);
 F1 c=Shade(jj);
 F2 vv=mm-jj;
 // disc contribution (faster than gaussian)
 //  d=distance
 //  r=radius^2 for 1.0
 //  f=radius^2 to drop to 0.0
 F1 r=0.707*1.75;
 F1 f=r+0.707*0.333;
 r=r*r;f=f*f;
 F1 s=1.0/(r-f);
 F1 t=f/(f-r);    
 F1 w=clamp(dot(vv,vv)*s+t,0.0,1.0);
 sumC+=c*w; sumW+=w;}   

// Single center sample using Quad4 sample positions (with disc filtering).
//
//  . x x x . 
//  x x x x x 
//  x x x x x 
//  x x x x x 
//  . x x x . 
//
F1 ResolveQuad4Disc(F2 pp){
 pp=floor(pp);
 F1 sumC=0.0;
 F1 sumW=0.0;
 Quad4Disc(sumC,sumW,pp+F2(-1.0,-2.0),pp);
 Quad4Disc(sumC,sumW,pp+F2( 0.0,-2.0),pp);
 Quad4Disc(sumC,sumW,pp+F2( 1.0,-2.0),pp);
 Quad4Disc(sumC,sumW,pp+F2(-2.0,-1.0),pp);
 Quad4Disc(sumC,sumW,pp+F2(-1.0,-1.0),pp);
 Quad4Disc(sumC,sumW,pp+F2( 0.0,-1.0),pp);
 Quad4Disc(sumC,sumW,pp+F2( 1.0,-1.0),pp);
 Quad4Disc(sumC,sumW,pp+F2( 2.0,-1.0),pp);
 Quad4Disc(sumC,sumW,pp+F2(-2.0, 0.0),pp);
 Quad4Disc(sumC,sumW,pp+F2(-1.0, 0.0),pp);
 Quad4Disc(sumC,sumW,pp+F2( 0.0, 0.0),pp);
 Quad4Disc(sumC,sumW,pp+F2( 1.0, 0.0),pp);
 Quad4Disc(sumC,sumW,pp+F2( 2.0, 0.0),pp);
 Quad4Disc(sumC,sumW,pp+F2(-2.0, 1.0),pp);
 Quad4Disc(sumC,sumW,pp+F2(-1.0, 1.0),pp);
 Quad4Disc(sumC,sumW,pp+F2( 0.0, 1.0),pp);
 Quad4Disc(sumC,sumW,pp+F2( 1.0, 1.0),pp);
 Quad4Disc(sumC,sumW,pp+F2( 2.0, 1.0),pp);
 Quad4Disc(sumC,sumW,pp+F2(-1.0, 2.0),pp);
 Quad4Disc(sumC,sumW,pp+F2( 0.0, 2.0),pp);
 Quad4Disc(sumC,sumW,pp+F2( 1.0, 2.0),pp);
 return sumC/sumW;}

//
// POOR QUALITY BOX SGSSAA
//

// Box filtered 2xSGSSAA.
//
//  x .
//  . x
//
F1 ResolveBox2xSSAA(F2 pp){return 0.5*Shade(pp-F2(0.25,0.25))+0.5*Shade(pp+F2(0.25,0.25));}

// Box filtered 4xSGSSAA.
//
//  . . x .
//  x . . .
//  . . . x
//  . x . .
//
F1 ResolveBox4xSSAA(F2 pp){return
 Shade(pp+F2( 0.125,-0.375))*0.25+
 Shade(pp+F2(-0.375,-0.125))*0.25+
 Shade(pp+F2( 0.375, 0.125))*0.25+
 Shade(pp+F2(-0.125, 0.375))*0.25;}

//
// POOR QUALITY JITTERED
//

// Jittered position.
F2 Jit(F2 pp){
 // Start with better baseline pattern.
 pp=Quad4(pp);
 // Very poor quality (clumping) move in disc around pixel.
 F1 n=Noise(pp,fract(iTime));    
 F1 m=Noise(pp,fract(iTime*0.333))*0.5+0.5;
 m = sqrt(m);
 return pp+Rot(0.707*0.5*m,n);}

// Single poor quality jittered sample.
//
//  x
//
F1 ResolveJit(F2 pp){return Shade(Jit(floor(pp)));}

// Gaussian filtered jittered tap.
void JitGaus(inout F1 sumC,inout F1 sumW,F2 pp,F2 mm){
 F2 jj=Jit(pp);
 F1 c=Shade(jj);
 F2 vv=mm-jj;
 F1 w=exp2(-4.0*dot(vv,vv));    
 sumC+=c*w; sumW+=w;}   

// Many tap gaussian from poor quality jittered single/sample per pixel
//
//  . x x x . 
//  x x x x x 
//  x x x x x 
//  x x x x x 
//  . x x x . 
//
F1 ResolveJitGaus(F2 pp){
 pp=floor(pp);
 F1 sumC=0.0;
 F1 sumW=0.0;
 JitGaus(sumC,sumW,pp+F2(-1.0,-2.0),pp);
 JitGaus(sumC,sumW,pp+F2( 0.0,-2.0),pp);
 JitGaus(sumC,sumW,pp+F2( 1.0,-2.0),pp);
 JitGaus(sumC,sumW,pp+F2(-2.0,-1.0),pp);
 JitGaus(sumC,sumW,pp+F2(-1.0,-1.0),pp);
 JitGaus(sumC,sumW,pp+F2( 0.0,-1.0),pp);
 JitGaus(sumC,sumW,pp+F2( 1.0,-1.0),pp);
 JitGaus(sumC,sumW,pp+F2( 2.0,-1.0),pp);
 JitGaus(sumC,sumW,pp+F2(-2.0, 0.0),pp);
 JitGaus(sumC,sumW,pp+F2(-1.0, 0.0),pp);
 JitGaus(sumC,sumW,pp+F2( 0.0, 0.0),pp);
 JitGaus(sumC,sumW,pp+F2( 1.0, 0.0),pp);
 JitGaus(sumC,sumW,pp+F2( 2.0, 0.0),pp);
 JitGaus(sumC,sumW,pp+F2(-2.0, 1.0),pp);
 JitGaus(sumC,sumW,pp+F2(-1.0, 1.0),pp);
 JitGaus(sumC,sumW,pp+F2( 0.0, 1.0),pp);
 JitGaus(sumC,sumW,pp+F2( 1.0, 1.0),pp);
 JitGaus(sumC,sumW,pp+F2( 2.0, 1.0),pp);
 JitGaus(sumC,sumW,pp+F2(-1.0, 2.0),pp);
 JitGaus(sumC,sumW,pp+F2( 0.0, 2.0),pp);
 JitGaus(sumC,sumW,pp+F2( 1.0, 2.0),pp);
 return sumC/sumW;}

