//
// ENTRY
//

void mainImage(out F4 fragColor,in F2 fragCoord){
 F2 pp=fragCoord;
 F1 cc=0.0;
 int ss=MODE;
 // Red
 if(ss==0){
  if(pp.y<8.0){fragColor.xyz=F3(1.0,0.0,0.0);return;}
  cc=ResolveNoAA(pp);}
 // Orange
 else if(ss==1){
  if(pp.y<8.0){fragColor.xyz=F3(1.0,0.5,0.0);return;}
  cc=ResolveQuad4Gaus(pp);}
 // Gold
 else if(ss==2){
  if(pp.y<8.0){fragColor.xyz=F3(1.0,0.75,0.0);return;}
  cc=ResolveJit(pp);
  cc=Noise4(pp,cc,0.5);}
 // Yellow
 else if(ss==3){
  if(pp.y<8.0){fragColor.xyz=F3(1.0,1.0,0.0);return;}
  cc=ResolveJitGaus(pp);
  cc=Noise4(pp,cc,0.5);}
 // Lime
 else if(ss==4){
  if(pp.y<8.0){fragColor.xyz=F3(0.5,1.0,0.0);return;}
  cc=ResolveBox2xSSAA(pp);}
 // Green
 else if(ss==5){
  if(pp.y<8.0){fragColor.xyz=F3(0.0,1.0,0.0);return;}
  cc=ResolveJitGaus2(pp);
  cc=Noise4(pp,cc,0.125);}
 // Green Blue
 else if(ss==6){
  if(pp.y<8.0){fragColor.xyz=F3(0.0,1.0,0.5);return;}
  cc=ResolveBox4xSSAA(pp);}
 // Blue
 else if(ss==7){
  if(pp.y<8.0){fragColor.xyz=F3(0.0,0.0,1.0);return;}
  cc=ResolveJitGaus4(pp);
  cc=Noise4(pp,cc,1.0/16.0);}     
 // Output   
 fragColor.x=Srgb(cc);
 fragColor.yxz=fragColor.xxx;}

/* Dumb shader to paint the Justice logo
 * Box and rotate funtions taken from https://thebookofshaders.com/08/
*/

#define PI 3.14159265359

mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle), sin(_angle),cos(_angle));
}

float box(in vec2 _st, in vec2 _size){
    _size = vec2(0.5) - _size*0.5;
    vec2 uv = smoothstep(_size, _size+vec2(0.001), _st);
    uv *= smoothstep(_size, _size+vec2(0.001), vec2(1.0)-_st);
    return uv.x*uv.y;
}

float jcross(in vec2 _st, float _size){
    return  box(_st+vec2(0.0, -0.07), vec2(_size - 0.122,_size/4.7)) + 
            box(_st+vec2(0.0,0.05), vec2(_size/4.5,_size*1.316));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord.xy/min(iResolution.x,iResolution.y);
    st.x -= 0.35;

    float cr = jcross(st,0.6);
    float ax = st.y;

    st -= vec2(0.5);
    st = rotate2d( (4.*sin(iTime)*PI) / 5. ) * st;
    st += vec2(0.5);

    vec3 c1 = vec3(mix(vec3(0.405,0.002,0.405), vec3(0.006,0.700,0.673), st.y));
    vec3 c2 = vec3(mix(vec3(0.006,0.700,0.673), vec3(0.405,0.002,0.405), st.y));
    vec3 bg = vec3(mix(c1, c2, st.x));

	vec3 colorCross = mix(vec3(0.985,0.813,0.191), vec3(1.000,0.010,0.725), ax);
    vec3 colorFinal = mix(bg, colorCross, cr > 0. ? 1.0 : 0.);
    fragColor = vec4(colorFinal,1.0);
}

void mainImage( out vec4 f, vec2 g )
{
    g -= f.xy=iResolution.xy/2.;

    g /= f.y;

    float d = pow(abs(.5 - max(abs(g.x),abs(g.y))), .2);

    g += d;

    g *= g;

    f = vec4(g,d,1) * d * (0.8 + 0.2 * cos(10. * d+iTime*10.));
}

