#define PI 3.14159265359

// Anti-aliasing
float aa(float edge, float val)
{
    return 1.0 - smoothstep(val, val + 0.00999999, edge);
}

vec2 point_shr(vec2 p, float val)
{
    return p - vec2(val, 0.0);
}

vec2 point_shl(vec2 p, float val)
{
    return p + vec2(val, 0.0);
}

vec2 point_up(vec2 p, float val)
{
    return p - vec2(0.0, val);
}

vec2 point_down(vec2 p, float val)
{
    return p + vec2(0.0, val);
}

float square(vec2 c, float d)
{
    return aa(abs(c.x), d) * aa(abs(c.y), d);
}

float circle(vec2 c, float r)
{
    // Calculating the length
    float l = length(c);
    // Calculating the angle
    float t = atan(c.y, c.x);
    // Calculating point coordinates
    vec2 p = vec2(r*cos(t), r*sin(t));
    // Determining the circle
    return aa(length(c), length(p));
}

float triangle(vec2 c, float d)
{
    return aa(c.y, d - abs(c.x)) * aa(abs(c.y), d);
}

float romboid(vec2 c, float d)
{
    return aa(abs(c.x) + abs(c.y), d);
}

float polygon(vec2 c, int n, float s)
{
    // if n = 3, scale down s
    if(n == 3)
        s = s/2.0;
    // Calculating the length of the c vector (c.x, c.y) from the center (0,0)
    //float l = sqrt(pow(c.x,2.0)+pow(c.y,2.0));
    float l = length(c);

    // Calculating the angle at the base (0,0) of the c vector (c.x, c.y)
    //float a = acos(-c.y/l); //using negative c.y to get the first triangle facing down
    float a = atan(c.x, c.y) - PI; //subtracting PI to get the first triangle facing down

    // Calculating the angle at the base (0,0) of each triangle making the polygon
  	float r = (2.0 * PI) / float(n);

    /*
    	Calculating the index (integer) of the nearest multiple angle of r
    	e.g. if N = 6 and a = 135 -> r = 360/6 = 60 -> i = 2 -> i * r = 120
	*/
    float i = floor(0.5 + a / r);

    // Calculating the difference between a and the nearest multiple angle
    float t = i * r - a;

    // Calculating the length of the apothem for the current triangle (hint: index)
    float d = cos(t) * l;

    return aa(d, s);
}

float add_shape(float color, float shape)
{
    return color + shape;
}

float sub_shape(float color, float shape)
{
    return color - shape;
}

float add_border(float size, float shape)
{
    return size + shape;
}

float sub_border(float size, float shape)
{
    return size - shape;
}

float sine(vec2 c, float s, float f, float h)
{
    float d = h*sin(f*c.x);
    return aa(abs(c.y - d), s);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (2.0*(fragCoord.xy / iResolution.xy) - 1.0) * vec2(iResolution.x/iResolution.y, 1.0);

    // Save original pixel coordinates
    vec2 uv_s = uv;
    vec3 color = vec3(0.0);

    // Repetition of the field
    //uv_s.x = mod(uv_s.x, 1.0);
    //uv_s.y = mod(-pow(uv_s.x, 1.1) + uv_s.y, 1.0);

    // Field deformation, power
    //uv_s.y = uv_s.y - pow(uv_s.x, 1.1);

    // Reflect field
    //uv_s.x = abs(uv_s.x);
    //uv_s.y = abs(uv_s.y);

    // Draw a square
    uv = point_shr(uv_s, 1.2);
    uv.y = uv.y - 0.05*sin(iTime);

    // Add a border to the square
    float l = atan(uv.x, uv.y);
   	float border = 0.02*sin(50.0 * l + 10.0*iTime) + 0.02*sin(20.0 * l);
    float size = 0.2;
    size = add_border(size, border);
    color.g = add_shape(color.g, square(uv, size));

    // Draw a square
    uv = point_shl(uv_s, 1.2);
    uv.y = uv.y + 0.15*sin(2.5*iTime);
    uv.x = uv.x - 0.15*cos(2.5*iTime);

    l = atan(uv.x, uv.y);
    border = 0.01*sin(5.0*l + 10.0*iTime);
    size = 0.2;
    size = sub_border(size, border);
    color.g = add_shape(color.g, square(uv, size));

    // Draw a circle
    uv = point_up(uv_s, 0.7);
    uv = point_shl(uv, 1.2);
    l = atan(uv.x, uv.y);
    border = 0.02*sin(50.0 * l + 10.0*iTime) + 0.02*cos(20.0 * l);
    size = 0.2;
    size = add_border(size, border);
    color.g = add_shape(color.g, circle(uv, size));

    // Draw a circle
    uv = point_up(uv_s, 0.7);
    uv = point_shr(uv, 1.2);
    color.g = add_shape(color.g, circle(uv, 0.2));

    // Subtract a square to the circle
    uv = point_up(uv_s, 0.7);
    uv = point_shr(uv, 1.2);
    color.g = sub_shape(color.g, square(uv, 0.14));

    // Add a triangle inside the square
    uv = point_up(uv_s, 0.635);
    uv = point_shr(uv, 1.2);
    color.g = add_shape(color.g, polygon(uv, 3, 0.075));

    // Draw a triangle
    uv = uv_s;
    uv = point_down(uv_s, 0.7);
    uv = point_shl(uv , 1.2);
    l = atan(uv.x, uv.y);
    border = 0.02*sin(50.0 * l + 10.0*iTime) + 0.02*cos(20.0 * l);
    size = 0.16;
    size = add_border(size, border);
    color.g = add_shape(color.g, triangle(uv, size));

    // Draw a romboid
    uv = point_down(uv_s, 0.7);
    uv = point_shr(uv , 1.2);
    color.g = add_shape(color.g, romboid(uv, 0.25));

    // Subtract a circle to the romboid
    uv = point_down(uv_s, 0.7);
    uv = point_shr(uv, 1.2);
    color.g = sub_shape(color.g, circle(uv, 0.175));

    // Draw some polygons
    uv = uv_s;
    uv = point_down(uv, 1.13);
    float shift = 0.25;
    for(int i = 3; i < 11; i++)
    {
        uv = point_up(uv, shift);
    	color.g = add_shape(color.g, polygon(uv, i, 0.1));
    }

    // Draw a sine
    uv = uv_s;
    uv.x = uv.x + 0.5*iTime;
    color.r = add_shape(color.r, sine(uv, 0.01, 5.0, 0.3));
    color.g = add_shape(color.g, sine(uv, 0.01, 5.0, 0.6));
    color.b = add_shape(color.b, sine(uv, 0.01, 5.0, 0.9));

	fragColor = vec4(color,1.0);
}

#define PI 3.14159265359
#define SOFT_STEPS 16
#define RAYS_STEPS 100

// uncomment this to show a closeup of the ant
// #define SHOW_ANT

// hopefully this should compile to a const mat2
#define rot_const(a) (mat2(cos(a), sin(a), -sin(a), cos(a)))

// Fabrice's rotation matrix
mat2 rot( in float a ) {
    vec2 v = sin(vec2(PI*0.5, 0) + a);
    return mat2(v, -v.y, v.x);
}

// Dave Hoskins hash function
vec3 hash33( in vec3 p3 ) {
    #define HASHSCALE3 vec3(.1031, .1030, .0973)
	p3 = fract(p3 * HASHSCALE3);
    p3 += dot(p3, p3.yxz+19.19);
    return fract((p3.xxy + p3.yxx)*p3.zyx);
}

// iq's functions
float sdBox( in vec3 p, in vec3 b ) {
	vec3 d = abs(p) - b;
	return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}
float sdCapsule( in vec3 p, in vec3 a, in vec3 b, in float r ) {
    vec3 pa = p - a, ba = b - a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h ) - r;
}
float sdEllipsoid( in vec3 p, in vec3 r ) {
    return (length( p/r ) - 1.0) * min(min(r.x,r.y),r.z);
}
float smin( in float a, in float b, in float s ) {
    float h = clamp( 0.5 + 0.5*(b-a)/s, 0.0, 1.0 );
    return mix(b, a, h) - h*(1.0-h)*s;
}
float smax( in float a, in float b, in float s ) {
    float h = clamp( 0.5 + 0.5*(a-b)/s, 0.0, 1.0 );
    return mix(b, a, h) + h*(1.0-h)*s;
}

// intersection of 2 circles, by eiffie (used for the legs IKs)
vec2 intersect(vec3 c0, vec3 c1) {
    vec2 dxy = vec2(c1.xy - c0.xy);
    float d = length(dxy);
    float a = (c0.z*c0.z - c1.z*c1.z + d*d)/(2.*d);
    vec2 p2 = c0.xy + (a / d)*dxy;
    float h = sqrt(c0.z*c0.z - a*a);
    vec2 rxy = vec2(-dxy.y, dxy.x) * (h/d);
    return p2 + rxy;
}

// ant's legs distance function
float deAntLeg(in vec3 p, in vec3 anchor, in vec3 legStart,
               in float lenStart, in vec3 legEnd, in float lenEnd) {

	// express coordinates from the starting leg
    vec3 inLeg = p - legStart;
    vec3 legEndInStart = legEnd - legStart;
    // express coordinates in the leg plane
    vec3 xDir = normalize(vec3(legEnd.xy - legStart.xy, 0));
    vec2 planar = vec2(dot(inLeg, xDir), inLeg.z);
    vec2 endInPlanar = vec2(dot(legEndInStart, xDir), legEndInStart.z);

    // get intersection
    vec2 jointPlanar = intersect( vec3(0, 0, lenStart), vec3(endInPlanar, lenEnd) );
    // go back to 3D space
    vec3 joint = legStart + xDir*jointPlanar.x + vec3(0, 0, 1)*jointPlanar.y;

    float d = sdCapsule( p, anchor, legStart, 0.03 );
    d = smin(d, sdCapsule( p, legStart, joint, 0.02 ), 0.02);
    d = smin(d, sdCapsule( p, joint, legEnd, 0.015 ), 0.02);
    return d;

}

// ant distance function, phase is the animation (between 0 and 1)
float deAnt( in vec3 p, in float phase, out vec3 color1, out vec3 color2, out float roughness ) {

    color1 = vec3(0.90, 0.18, 0.01);
    color2 = vec3(0.35, 0.10, 0.01);
    roughness = 12.0;

    p = p.zyx;

    // bounding box optimization
    float bb = sdBox(p, vec3(1.1, 0.5, 0.5));
    if (bb > 0.5) {
        return bb;
    }

    p -= vec3(-0.05, 0, 0.75);

    // 3 parts for the ant
    const vec2 slopeCenter = vec2(0.4, 0.8);
    vec2 inSlope = slopeCenter - p.xz;
    float slope = atan(inSlope.y, inSlope.x);
    slope /= PI;
    float part = clamp(floor(slope * 10.0), 3.0, 5.0);
    slope = (part + 0.5) / 10.0;
    slope *= PI;
    vec2 center = slopeCenter - vec2(cos(slope), sin(slope)) * 1.6;
    vec3 partCenter = vec3(center.x, 0.0, center.y);

    vec3 inPart = p - partCenter;
    float side = sign(inPart.y);
    inPart.y = abs(inPart.y);

    float dist = bb;

    if (part > 4.5) {

        // rotation
        inPart.x += 0.3;
        float r = sin(phase*2.0*PI)*0.05;
        inPart.xz *= rot(r-0.1);
        inPart.x -= 0.3;

        // abdomen
        inPart -= vec3(0.33, 0.0, -0.02);
        float radius = 0.1 + smoothstep(-0.2, 0.7, -inPart.x) * 0.5;

        // add ridges
        float s = cos(-inPart.x*40.0)*0.5+0.5;
        s *= s; s *= s;
        radius -= s*0.005;

        dist = length(inPart) - radius;
        float sMix = pow(s, 8.0);
        color1 = mix(vec3(0.1, 0.05, 0.01), vec3(0.6, 0.5, 0.4), sMix);
        color2 = mix(vec3(0.05, 0.02, 0.01), vec3(0.5, 0.4, 0.2), sMix);
        roughness = 16.0 - s*12.0;

    } else if (part > 3.5) {

        // thorax
        inPart += vec3(0.02, 0.0, -0.05);
        inPart.xz *= rot_const(-0.3);

        // pronotum
        vec3 inThoraxA = inPart - vec3(-0.13, 0.0, 0.05);
        float radiusA = 0.10 + smoothstep(-0.1, 0.5, inThoraxA.x) * 0.2;
        radiusA -= smoothstep(-0.1, 0.2, inThoraxA.z) * 0.1;
        float thoraxDistA = length(inThoraxA) - radiusA;

        // propodeum
        vec3 inThoraxB = inPart - vec3(0.06, 0.0, 0.03);
        float radiusB = 0.05 + smoothstep(-0.1, 0.4, inThoraxB.x) * 0.2;
        radiusB -= smoothstep(-0.1, 0.4, inThoraxA.z) * 0.1;
        float thoraxDistB = length(inThoraxB) - radiusB;
        dist = smin(thoraxDistA, thoraxDistB, 0.05);

        // petiole
        vec3 inThoraxC = inPart - vec3(0.24, 0.0, 0.0);
        float radiusC = 0.05 - smoothstep(-0.1, 0.2, inThoraxC.z-inThoraxC.x*0.3) * 0.04;
        float thoraxDistC = sdCapsule( inThoraxC, vec3(0), vec3(-0.03, 0, 0.11), radiusC);
        dist = smin(dist, thoraxDistC, 0.03);

        // add ridges
        vec3 inRidges = inPart - vec3(0.01, 0.0, 0.1);
        float ridgesDist = abs(length(inRidges) - 0.12);
        dist += smoothstep(0.02, 0.0, ridgesDist) * 0.004;

    } else if (part > 2.5) {

        // head
        inPart -= vec3(-0.09, 0.0, -0.06);
        inPart.xz *= rot_const(0.3);
        float radius = 0.07 + smoothstep(-0.15, 0.4, inPart.x) * 0.3;
        radius -= smoothstep(0.0, 0.4, abs(inPart.z))*0.2;
        dist = length(inPart) - radius;

        // frontal carina
        vec3 inCarina = inPart - vec3(0.02, 0.0, 0.09);
        inCarina.xy *= rot_const(-0.4);
        inCarina.xz *= rot_const(0.1);
        float carina = sdBox( inCarina, vec3(0.1, 0.05, 0.01) ) - 0.01;
        dist = smin(dist, carina, 0.05);

        // antenna
        vec3 inAntenna = inPart - vec3(-0.03, 0.1, 0.1);
        inAntenna.yz *= rot_const(-0.4);
        inAntenna.xz *= rot(sin((phase+side*0.25)*2.0*PI)*0.3-0.2);
        const vec3 funiculusStart = vec3(0, 0, 0.3);
        float scapeRadius = 0.007 + inAntenna.z*0.04;
        float scape = sdCapsule( inAntenna, vec3(0), funiculusStart, scapeRadius );
        vec3 funiculusDir = normalize(vec3(-0.5, 0.0, -0.1));
        funiculusDir.xz *= rot(sin((phase+side*0.25)*4.0*PI)*0.2);
        float funiculusRadius = dot(funiculusDir, inAntenna - funiculusStart);
        funiculusRadius = abs(sin(funiculusRadius*67.0));
        funiculusRadius = 0.01 + funiculusRadius*0.004;
        float funiculus = sdCapsule(inAntenna, funiculusStart, 
                                    funiculusStart+funiculusDir*0.5, funiculusRadius );
        float antennaDist = min(scape, funiculus);
        dist = min(dist, antennaDist);

        // mandibles
        vec3 inMandibles = inPart;
        inMandibles.xy *= rot(sin(phase*4.0*PI)*0.1-0.1);
        float mandiblesOuter = sdEllipsoid( inMandibles, vec3(0.25, 0.14, 0.1) );
        float mandiblesInner = sdEllipsoid( inMandibles, vec3(0.15, 0.1, 0.4) );
        float mandibles = smax(mandiblesOuter, -mandiblesInner, 0.05);
        mandibles = smax(mandibles, 0.005-inMandibles.y+sin(inMandibles.x*300.0)*0.005, 0.01);
        dist = smin(dist, mandibles, 0.05);

        // eyes
        float eyes = sdEllipsoid( inPart - vec3(0.21, 0.15, 0.03), vec3(0.06, 0.05, 0.05 ) );
        if (eyes < dist) {
            color1 = color2 = vec3(0.05);
            roughness = 32.0;
            dist = eyes;
        }

    }

    // add a capsule in the center to connect the parts
    float connector = sdCapsule( p, vec3(-0.15, 0, -0.63), vec3(0.5, 0, -0.83), 0.03);
    dist = min(dist, connector);

    // add legs
    vec3 inLegs = p;
    inLegs.y = abs(inLegs.y);
    phase += side*0.25;

    float angleA = (phase)*PI*2.0;
    vec3 legAOffset = vec3(cos(angleA), 0, max(0.0, sin(angleA)))*0.2;
    float legA = deAntLeg(inLegs, vec3(0.05, 0.0, -0.75),
                          vec3(0.1, 0.1, -0.82), 0.25, 
                          vec3(-0.2, 0.4, -1.2)+legAOffset, 0.5);
    float angleB = (phase+0.33)*PI*2.0;
    vec3 legBOffset = vec3(cos(angleB), 0, max(0.0, sin(angleB)))*0.2;
    float legB = deAntLeg(inLegs, vec3(0.18, 0.0, -0.8),
                          vec3(0.2, 0.1, -0.85), 0.3, 
                          vec3(0.3, 0.5, -1.2)+legBOffset, 0.6);
    float angleC = (phase+0.66)*PI*2.0;
    vec3 legCOffset = vec3(cos(angleC), 0, max(0.0, sin(angleC)))*0.2;
    float legC = deAntLeg(inLegs, vec3(0.25, 0.0, -0.8),
                          vec3(0.3, 0.1, -0.85), 0.4, 
                          vec3(0.6, 0.4, -1.2)+legCOffset, 0.7);

    float distLegs = min(min(legA, legB), legC);
    if (distLegs < dist) {
        color1 = vec3(0.35, 0.10, 0.01);
        color2 = vec3(0.05, 0.02, 0.01);
        dist = distLegs;
    }

    return dist;
}

// main distance function, coordinates in, distance and surface parameters out
float de( in vec3 p, out vec3 color1, out vec3 color2, out float roughness ) {

    color1 = vec3(0.7);
    color2 = vec3(0.7);
    roughness = 4.0;

    #ifdef SHOW_ANT
    return deAnt(p.zxy, fract(iTime), color1, color2, roughness);
    #endif

    // perimeter of the moebius strip is 38
    #define RADIUS (1.0/(PI*2.0)*38.0)

    // cylindrical coordinates
    vec2 cyl = vec2(length(p.xy), p.z);
    float theta = atan(p.x, p.y);
    vec2 inCyl = vec2(RADIUS, 0) - cyl;
    // rotate 180 to form the loop
    inCyl *= rot(theta*1.5-2.0);
    // coordinates in a torus (cylindrical coordinates + position on the stripe)
    vec3 inTor = vec3(inCyl, theta * RADIUS);

    // add the band
    float bandDist = sdBox(inTor, vec3(0.05, 1, 100)) - 0.05;
    float d = bandDist;
    // add holes
    vec3 inHole = vec3(mod(inTor.yz, vec2(0.5)) - vec2(0.25), inTor.x);
    inHole.xyz = inHole.zxy;
    float holeDist = sdBox(inHole, vec3(0.18));
    d = smax(d, -holeDist, 0.05);

    // add ants
    vec3 inTorObj = vec3(abs(inTor.x), inTor.y, inTor.z + iTime*1.3 + sign(inTor.x));
    float ant = floor(inTorObj.z / 4.0);
    vec3 objCenter = vec3(0.6, 0, ant * 4.0 + 2.0);
    float phase = fract(iTime) + mod(ant, 9.0) / 9.0;
    vec3 antColor1 = vec3(0.0);
    vec3 antColor2 = vec3(0.0);
    float antRoughness = 0.0;
    float antDist = deAnt(inTorObj-objCenter, phase, antColor1, antColor2, antRoughness);
    if (antDist < d) {
        color1 = antColor1;
        color2 = antColor2;
        roughness = antRoughness;
        return antDist;
    }

	return d;
}

float de( in vec3 p ) {
    vec3 dummy1 = vec3(0);
    vec3 dummy2 = vec3(0);
    float dummy3 = 0.0;
    return de(p, dummy1, dummy2, dummy3);
}

// normal from backward difference
vec3 computeNormal( in vec3 p, in float d ) {
	const vec3 e = vec3(0.0, 0.01, 0.0);
	return normalize(vec3(
		d-de(p-e.yxx),
		d-de(p-e.xyx),
		d-de(p-e.xxy)));	
}

// cone trace the soft shadows
float computeSoftShadows( in vec3 from, in vec3 dir, in float theta ) {
    float sinTheta = sin(theta);
    float acc = 1.0;
    float totdist = 0.0;
    for (int i = 0 ; i < SOFT_STEPS ; i++) {
        vec3 p = from + totdist * dir;
        float dist = de(p);
        float prox = dist / (totdist*sinTheta);
        acc *= clamp(prox * 0.5 + 0.5, 0.0, 1.0);
        if (acc < 0.01) break;
        totdist += max(0.01, dist*0.85);
    }
    return acc;
}

// compute lighting at this position
vec3 computeColor( in vec3 p, in vec3 dir, in vec2 fragCoord ) {

    // sunlight and ambient
    const vec3 sunLight = vec3(0.9, 0.8, 0.6)*9.0;
    const vec3 sunLightDir = normalize(vec3(-2, -1, 3));
    const vec3 subLight = vec3(0.4, 0.4, 0.8)*3.0;
    const vec3 subLightDir = normalize(vec3(2, -1, -8));
    const vec3 ambLight = vec3(0.7, 0.7, 0.9)*4.0;

    // compute distance to get the surface albedo
    vec3 albedo1 = vec3(0);
    vec3 albedo2 = vec3(0);
    float roughness = 0.0;
    float dist = de(p, albedo1, albedo2, roughness);

    // compute surface normal
    vec3 normal = computeNormal(p, dist);

    float specScale = (roughness+1.0)*0.25;
    float sunLightDiff = max(0.0, dot(normal, sunLightDir));
    float sunLightSpec = pow(max(0.0, dot(sunLightDir, reflect(dir, normal))), roughness);
    sunLightSpec *= specScale;
    float subLightDiff = max(0.0, dot(normal, subLightDir));
    float subLightSpec = pow(max(0.0, dot(subLightDir, reflect(dir, normal))), roughness);
    subLightSpec *= specScale;

   	// soft shadows
    float soft = 0.0;
    if (sunLightDiff > 0.01) {
        soft = computeSoftShadows(p+normal*0.05, sunLightDir, 0.2);
    }

    // fake subsurface scattering
    float subsurface = max(0.0, dot(normal, -dir));
    subsurface = pow(subsurface, 4.0);
    vec3 albedo = mix(albedo2, albedo1, subsurface);

    // do some arty sketchy stuff on the light
    float sun = (sunLightDiff+sunLightSpec)*soft;
    float sub = (subLightDiff+subLightSpec);
    float amb = 1.0;
    vec3 lightValues = vec3(sun, sub, amb);

    // exposition
    lightValues *= 0.06;
    // gamma correction
    lightValues = pow( lightValues, vec3(1.0/2.2) );
    // cel shading
    lightValues = floor(lightValues * 7.0) / 6.0;

    // compose color
    vec3 color = vec3(0);
    color += albedo*sunLight*lightValues.x;
    color += albedo*subLight*lightValues.y;
    color += albedo*ambLight*lightValues.z;

    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    // position of the camera
    vec3 camPos = vec3(-40, 0, 0);
    // user input
    vec2 mouse=(iMouse.xy / iResolution.xy - 0.5) * 0.5;
    mouse *= step(1.0, iMouse.z);
    camPos.xz *= rot(mouse.y*-3.0+0.4);
    camPos.xy *= rot(mouse.x*-10.0+0.2);

    // direction of the camera
    vec3 forward = normalize(vec3(0) - camPos);
    // right and top vector
    vec3 right = normalize(cross(vec3(0, 0, 1), forward));
    vec3 top = cross(forward, right);

    // create direction
    vec2 uv = fragCoord.xy / iResolution.xy * 2.0 - 1.0;
	uv.y *= iResolution.y / iResolution.x;
    uv *= 0.2;
    #ifdef SHOW_ANT
    uv *= 0.2;
    #endif
    vec3 dir = normalize(forward + right*uv.x + top*uv.y);

    // some noise is always useful
    vec3 noise = hash33(vec3(fragCoord.xy, iFrame));

    bool hit = false;
    float prevDist = 0.0;
    float borderAcc = 1.0;

    float totdist = 0.0;
    totdist += de(camPos)*noise.x;

	for (int i = 0 ; i < RAYS_STEPS ; i++) {
		vec3 p = camPos + totdist * dir;
        float dist = de(p);

// if you replace 0.0015 by the sine of the pixel angle, you get the average opacity in a pixel
// by accumulating the opacity front to back you can get an anti-aliased edge
// problem is you have to compute the normal to shade the surface and get the effective color
// computing the normal at every steps is too expensive (unless the normal is analytical)
// nonetheless the border color is constant so we can cone trace it without any trouble

        // cone trace the border
        if (dist > prevDist) {
            float prox = dist / (totdist*0.0015);
            float alpha = clamp(prox * 0.5 + 0.5, 0.0, 1.0);
            borderAcc *= alpha;
        }

        // hit a surface, stop here
        if (dist < 0.01) {
            hit = true;
            break;
        }

        // continue forward
        totdist += min(dist*0.85, 100.0);
        prevDist = dist;
	}

    // color and lights
    if (hit) {
        vec3 p = camPos + totdist * dir;
    	fragColor.rgb = computeColor(p, dir, fragCoord.xy);
    } else {
        fragColor.rgb = vec3(0.8, 0.8, 0.9);
    }

    // add a black border
    borderAcc = pow(borderAcc, 8.0);
    fragColor.rgb = mix(vec3(0), fragColor.rgb, borderAcc);

    // vigneting
    vec2 p = fragCoord.xy / iResolution.xy * 2.0 - 1.0;
    fragColor.rgb = mix(fragColor.rgb, vec3(0), dot(p, p)*0.2);

    // add some noise
    fragColor.rgb += noise * 0.08 - 0.04;

    fragColor.a = 1.0;
}

/**
	Small planet with atmosphere, clouds, shores, water etc.
	---------------------------------------------------------

    Most of the stuff is pretty basic raytracing/marching, there might be couple of things worth mentioning:

	I tried a couple of ways to texture a planet. I first tried cube mapping but that caused
    really ugly seams. I ended up texturing the sphere by mapping the texture onto the planet spherically,
    and then countering the distortion at the poles by changing uv coordinates from polar to rectangular.
	Needs 2 texture lookups, as both poles need to be countered for distortion. 

	Better explanation of the process is here: https://www.youtube.com/watch?v=Xxg7pOIDAqQ

	Atmospheric scattering is not physically accurate at all, but looks good enough and is really fast.

	There are smaller waves around the shores. They are barely visible now in the windowed mode, I started 
    with camera much closer, and ended up with the shore waves barely being visible. Oh well, at least
    they can be seen in fullscreen.

	The nebula is a warped texture that is mapped cylinderically around.

*/

mat3 rotx(float a) { mat3 rot; rot[0] = vec3(1.0, 0.0, 0.0); rot[1] = vec3(0.0, cos(a), -sin(a)); rot[2] = vec3(0.0, sin(a), cos(a)); return rot; }
mat3 roty(float a) { mat3 rot; rot[0] = vec3(cos(a), 0.0, sin(a)); rot[1] = vec3(0.0, 1.0, 0.0); rot[2] = vec3(-sin(a), 0.0, cos(a)); return rot; }
mat3 rotz(float a) { mat3 rot; rot[0] = vec3(cos(a), -sin(a), 0.0); rot[1] = vec3(sin(a), cos(a), 0.0); rot[2] = vec3(0.0, 0.0, 1.0); return rot; }

vec3 lightDir = normalize(vec3(0.0, 1.0, -1.0));
const float SHELL = 0.25;
const float OUTER_SHELL = SHELL + 0.1;
const float H = (OUTER_SHELL - SHELL)*.7;
const float WATERLEVEL = SHELL+H*0.2;
#define PI 3.14159265358
#define PI2 (PI*0.5)

#define DO_CLOUDS
#define SHADOWS


vec2 polar2Rect(in vec2 uv)
{
    vec2 _uv = uv;
    uv.x = cos(_uv.x)*_uv.y;
    uv.y = sin(_uv.x)*_uv.y;
    return uv;
}

// maps a texture to a sphere
vec4 textureSpherical(in sampler2D tex, in vec3 rp, float scale)
{
    float lrp = length(rp);
    vec2 uv1 = vec2(atan(rp.y, rp.x), acos(rp.z/lrp));
    vec2 uv2 = uv1; uv2.y = PI-uv1.y;
    float f=uv1.y;

    uv1=polar2Rect(uv1)*scale;
    uv2=polar2Rect(uv2)*scale;

    vec4 c1 = texture(tex, uv1);
    vec4 c2 = texture(tex, uv2);
    return mix(c1, c2, smoothstep(PI2-0.01, PI2+0.01, f));
}

mat3 g_planetRot;

vec3 planetRotatedVec(in vec3 p)
{
    return p*g_planetRot;
}

float map(in vec3 rp)
{
    float h = textureSpherical(iChannel0, planetRotatedVec(rp), .6).r;
    h = smoothstep(0.1, 1.0, h);
    h*=H*.5;
    return  length(rp) - (SHELL+h);
}


vec3 grad(in vec3 rp)
{
    vec2 off = vec2(0.0005, 0.0);
    vec3 g = vec3(map(rp + off.xyy) - map(rp - off.xyy),
                  map(rp + off.yxy) - map(rp - off.yxy),
                  map(rp + off.yyx) - map(rp - off.yyx));
    return normalize(g);
}

vec3 traceSphere(in vec3 ro, in vec3 rd, float r, out float t1, out float t2)
{
    t1=t2=-1.0;
    vec3 X = ro + rd * (dot(normalize(-ro), rd)) * length(ro);
    float disc = r*r-pow(length(X), 2.0);
    if (disc < 0.0) return vec3(1000000.0);
    disc=sqrt(disc);
    vec3 p=X-disc*rd;
    t1=length(p-ro);t2=t1+disc*2.;
    return p;
}

float getClouds(in vec3 hitp, float T)
{
    hitp *= rotx(-T);
    vec3 hitp3 = hitp*rotz(T*.7);
    vec3 hitp2 = hitp*roty(T);

    if(dot(hitp, hitp) > 10000.) return 0.;
    float S = 1.;
    float c = textureSpherical(iChannel0, hitp, .2*S).r*.75;
    c += textureSpherical(iChannel0, hitp3, .7*S).r*.5*.5;
    c += textureSpherical(iChannel0, hitp2, 1.4*S).r*.5*.5*.5;
    c += textureSpherical(iChannel0, hitp, 2.4*S).r*.5*.5*.5*.5;
    c += textureSpherical(iChannel0, hitp3, 4.4*S).r*.5*.5*.5*.5*.5;
    return smoothstep(.55, .95, c);

}

float traceClouds(in vec3 ro, in vec3 rd, float depth)
{
    float T = iTime*.02;
    float t1=0.; float t2=0.;
    if(t1<0.0) return 0.0;
    vec3 hitp = traceSphere(ro, rd, OUTER_SHELL,t1,t2);
    vec3 hitp2 = ro+rd*t2;
    float c = getClouds(hitp, T);
    if (depth > length(hitp2-ro))
    {
        float c2=getClouds(ro+rd*t2,T);
        c=mix(c, c2, c2);
    }
    return c;
}

vec2 g_uv;
vec3 g_viewDir;
#define CLOUDS 0
#define GROUND 1
#define WATER 2

vec4 waterTexture(in vec3 rp)
{
    rp=planetRotatedVec(rp);
    float T = iTime*.01;
    float S=2.0;
    rp *= rotx(T);
    float c1=textureSpherical(iChannel1, rp, S).x;
    rp *= rotx(1.+T);
    float c2=textureSpherical(iChannel1, rp, S).x;
    rp *= rotx(2.+T);
    float c3=textureSpherical(iChannel1, rp, S).x;
    float B = iTime*2.;
    float col = mix(c3, mix(c1, c2, 0.5+0.5*(sin(PI*0.5+B)*0.5+0.5)), 0.5+0.5*(sin(B)*0.5+0.5));
    return vec4(col);

}

vec3 gradWater(in vec3 rp)
{
    vec3 w=normalize(rp);
    vec3 u=normalize(cross(vec3(0.0, 1.0, 0.0), w));
    vec3 v=(cross( u,w));

    float s=.0009;
    float h1 = waterTexture(rp).x;
    float h2 = waterTexture(rp+u*s).x;
    float h3 = waterTexture(rp+v*s).x;

    float bumpiness=.2;
    vec3 v1 = normalize(vec3(bumpiness, h1-h2, 0.0));
    vec3 v2 = normalize(vec3(0.0, bumpiness , h1-h3 ));
    vec3 g = normalize(cross(v1,v2));
    mat3 m; m[0]=v;m[1] = u;m[2]=w;
    return m*g;
}

// Background nebula
vec4 texSpace(in sampler2D tex, in vec3 dir)
{
    vec2 uv = vec2(atan(dir.x, dir.z), dir.y);
    uv.x=abs(uv.x);
	uv*=.4;
    vec4 col = texture(tex, uv);
    uv+=col.rg*.05;
    col = texture(tex, uv);
    uv+=col.rg*.1;
    col = texture(tex, uv);

    col*=smoothstep(0.1, 1.0, col.r);
    vec4 col1=vec4(0.2, 0.2, .7, 0.0);
    vec4 col2=vec4(0.2, 0.9, 0.5, 0.0);
    vec4 cl=col*vec4(0.0, 0.5, 1.0, 0.0);
    cl=mix(cl, col1, smoothstep(0.0, 0.4, col.r));
    cl=mix(cl, col2, smoothstep(0.1, 0.6, col.r));
    col=cl;

    col*=smoothstep(0.4, 0., abs(dir.y));
    col*=smoothstep(1., .5, abs(uv.x));
    col*= clamp((.6-abs(dir.y))-0.1*sin(2.0*atan(dir.x, dir.z)), 0.0, 1.0);
    return col;
}

vec4 bgCol(in vec3 rd)
{
    vec4 col=texSpace(iChannel0, rd);
    col+=smoothstep(0.93, 1.0, textureSpherical(iChannel2, rd, 1.0).g)*texture(iChannel1, vec2(.01*iTime+atan(rd.z, rd.x), rd.y)).r;
    return col*.2;
}

float doShadows(in vec3 rp)
{
    float s = 1.0;
    rp+=lightDir*.01;

    for (int i = 0; i < 4; ++i)
    {
    	float dist=map(rp);
        rp+=max(dist, 0.001)*lightDir;
        s=min(s,dist);
    }
    return mix(1.0, clamp(s/0.01, 0.0, 1.0), 0.8);
}

void trace(in vec3 rp, in vec3 rd, inout vec4 color)
{
    vec4 top =   vec4(.3, .1, .2, .0)*.1;
    vec4 bottom= vec4(.1, .0, .2, .0)*.1;

    color = mix(top, bottom, clamp(rd.y+0.5, 0.0, 1.0));

    color+=bgCol(rd);
    bool hit = false;
    vec3 ro = rp;
    int hitId = CLOUDS;
    float t1Clouds=0.0;float t2Clouds=0.0;
    vec3 hitp = traceSphere(ro, rd, OUTER_SHELL,t1Clouds,t2Clouds);

    float dist = 99.;

    for (int i = 0; i < 20; ++i)
    {
        dist = map(rp);
        rp += max(0.008, dist) * rd;

        if (dist < 0.0)
        {
            hitId = GROUND;
            break;
        }

        if (length(ro - rp) > 5.0) break;
    }


    if (hitId==GROUND)
    {
        for (int i = 0; i < 8; ++i)
        {
            rp += rd*dist*.4;
            dist = map(rp);
        }
    }

    float depth = length(ro-rp);
    float t1=0.0;float t2=0.0;
	vec3 waterLevelRp = traceSphere(ro, rd, WATERLEVEL+0.00*(sin(iTime*.25)*.5+.5),t1,t2);
    float wdepth = length(ro-waterLevelRp);

    if (wdepth < depth)
    {
        hitId=WATER;
    }

    float waterLevel = map(waterLevelRp);
    vec3 gGround = grad(rp);
    vec3 gWaterSurface = normalize(waterLevelRp);

    float dGround = clamp(dot(gGround, lightDir), 0.05, 1.0);
    float dWater = clamp(dot(gWaterSurface, lightDir), 0.05, 1.0);
    float dGlobal = clamp(dot(normalize(rp), lightDir), 0.05, 1.0);
    vec3 wg = gradWater(waterLevelRp);
    dGlobal=(0.9+dGlobal)/(1.9);

    vec3 H = normalize(-rd+lightDir);
    float dSpec = clamp(dot(H, wg), 0.0, 1.0);
    dSpec = pow(dSpec, 56.0)*.7;
    dSpec*=smoothstep(0.1, 0.2, dGlobal);
    dGround*=dGlobal;
    dWater*=dGlobal;

    if (hitId==GROUND)
    {
        vec4 cl = vec4(1.);
        color=mix(cl, vec4(0.3, 0.5, 0.0, 0.5)*textureSpherical(iChannel2, rp, 16.0), 1.-smoothstep(0., 0.02, length(rp-waterLevelRp)));
        color*=dGround;
    }    

    ////////
    // Foam
    ////////
    if (hitId==WATER)
    {

        float s = smoothstep(.007, .0, waterLevel);
        vec4 c1 = vec4(0., 0.13,  0.3, 0.0);
        vec4 c2 = vec4(0., 0.4, 0.6, 0.0);

        vec4 cl = mix(c1, c2, s);
		const int stps = 4;	
        const float STP=2.;
        const float phase=STP/float(stps);
        float foam = 0.0;
        const float b=STP/4.;
        for (int i = 0; i < stps; ++i)
        {
            float T=iTime*.2+float(i)*phase;
            vec3 _rp = waterLevelRp*3.;
			float p = mod(T, STP); 
            _rp-=p*gGround*.05;

            float f = textureSpherical(iChannel3, _rp, 3.0).r;
			f*=smoothstep(0.0, b, p);
            f*=smoothstep(STP, STP-b, p);
            foam = max(foam, f);
        }
       	foam = smoothstep(0.6, .9, foam);
        foam*=s;
        foam/=.4;

        float dFinal = mix(dWater, dGround, dGround);
        cl += foam;
        color = cl * dFinal;
	    color += vec4(dSpec);
    }


    if (hitId==0 )
    {
        // Sun
        float sund=clamp(dot(rd, lightDir), 0.0, 1.0);
        float sunocc=clamp(dot(g_viewDir, lightDir), 0.0, 1.0);
        sunocc=smoothstep(1.002, 0.99, sunocc);
        float sun=(pow(sund, 40.))*sunocc;
        sun=max(sun, smoothstep(0.99, 0.995, sund));
        vec4 suncol=vec4(sun);
        // cheap atmospeheric scattering effect. 
        //
        // red scattering, direct light transport from sun to eye
        vec4 mie=vec4(1.0, 0.2, 0., 1.0)*(t2Clouds-t1Clouds)*pow(sund, 15.)*3.;

        // blue, how much light gets reflected from ground to eye from sun
        float rlscatter=clamp(dot(lightDir, reflect(rd,normalize(hitp))),0.2, 1.);
        vec4 rayleigh=rlscatter*(t2Clouds-t1Clouds)*vec4(.4, .6, 1., 1.0);
        // 
        vec4 atmos=mix(mie,rayleigh,1.-mie.a);
        color+=atmos;                 
        color+=suncol;                
    }

    if(hitId!=0)
    {
	    color*=doShadows(hitId==GROUND?rp:waterLevelRp);
    }

    if(t1Clouds>=.0)
    {
#ifdef SHADOWS
        vec3 nrp = normalize(rp);
        float shad=traceClouds(nrp*10., -nrp, wdepth);

        if (hitId != CLOUDS)
        {
            color = mix(color, vec4(0.), shad*.7);
        }
#endif
#ifdef DO_CLOUDS
        float clouds = traceClouds(ro, rd, wdepth);

        // fading the clouds a little at the silhouettes
        clouds*= mix(1.0, smoothstep(0.1, 0.4, abs(dot(rd, normalize(hitp)))), 0.9);
        // dark side of the planet
        float darkness=smoothstep(-0.4, 0.5,dot(normalize(hitp), lightDir));
        color = mix(color, vec4(clouds*darkness), clouds);
#endif
    }


}


mat3 lookat(vec3 from, vec3 to)
{
    vec3 f = normalize(to - from);
    vec3 _tmpr = normalize(cross(f, vec3(0.0, 1.0, 0.0)));
    vec3 u = normalize(cross(_tmpr, f));
    vec3 r = normalize(cross(u, f));
    return mat3(r, u, f);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
	vec2 uv = ((fragCoord.xy / iResolution.xy)-vec2(.5))*vec2(1.0, iResolution.y/iResolution.x);
    g_uv = uv;
    g_planetRot=roty(iTime*.008)*rotz(iTime*.02);

    vec2 im = 16.0 * ((iMouse.xy / iResolution.xy) - vec2(0.5));
    vec3 rp = vec3(0.0, 0.0, -.8);
    if(iMouse.z<=0.0) 
    {	
        float T=PI*(4.0/3.0)-iTime*.1+6.;
        im.x=(0.5+0.5*cos(T))*3.;
    	im.y=sin(T)*8.;
    }
    rp = roty(-im.x) * rp;
    rp.y = -im.y*.25;

    vec3 rd = normalize(vec3(uv, 1.0));
    rd = lookat(rp, vec3(0.0)) * rd;
    g_viewDir=lookat(rp, vec3(.0))*vec3(0.0, 0.0, 1.0);
    trace(rp, rd, fragColor);

    fragColor.rgb = pow(fragColor.rgb, vec3(1.0 / 2.2));

}

#define EARSSIZE 0.25
#define NOSESIZE 0.05
#define MOUSTACHELENGTH 0.3
#define ANTIALIAS 0.01
#define PI 3.141592654

vec2 scale(vec2 v, vec2 s) {
	return mat2(s.x, 0, 0, s.y) * v;
}

vec2 rot(vec2 v, float angle) {
	return mat2(cos(angle), -sin(angle), sin(angle), cos(angle)) * v;
}

vec2 animate(vec2 uv) {
    float tt = mod(iTime, 1.5) / 1.5;  
    float ss = pow(tt, 0.2) * 0.5 + 0.5;  
    ss = 1.0 + ss*0.5*sin(tt * PI * 2.0 * 3.0 + uv.y*0.5)*exp(-tt*4.0); 
    uv *= vec2(0.5,1.2) + ss * vec2(0.5,-0.2); 
	return uv;
}

vec4 heart(vec2 uv, vec2 center) {
    vec2 p = uv - center;
    float a = atan(p.x, p.y) / PI;
    float r = length(p) / EARSSIZE;
    float h = abs(a);
    float d = (13.0 * h - 22.0 * h * h + 10.0 * h * h * h) / (6.0 - 5.0 * h);
	float t = smoothstep(-ANTIALIAS, ANTIALIAS, r - d);
    return vec4(0.8, 0.3, 0.4, 1.0 - t);
}

vec4 ear(vec2 uv, vec2 center) {
    float d1 = length(uv - center) - EARSSIZE;
    float t1 = smoothstep(0.0, ANTIALIAS, d1);
    float r = 4.5 * EARSSIZE;
    float d2 = length(uv - vec2(center.x, center.y - r)) - 4.0 * EARSSIZE;
    float t2 = smoothstep(0.0, ANTIALIAS, d2);
	return vec4(0.3, 0.3, 0.3, d2 > 0.0 ? (1.0 - t1) : t2);
}

vec4 nose(vec2 uv, vec2 center) {
    float d = length(uv - center);
    float t = smoothstep(NOSESIZE, NOSESIZE + ANTIALIAS, d);
	return vec4(0.0, .0, .0, 1.0 - t);
}

vec4 moustache(vec2 uv, vec2 p) {
    uv.x = abs(uv.x);
    float r = length(uv - p);
    vec2 m = vec2(uv.x, sin(uv.x / MOUSTACHELENGTH * 2.0 * PI - iTime * 5.) * r);
    m.y /= 5.0;
    float d = length(uv - m);
    float t = smoothstep(0.01, 0.01 + ANTIALIAS, d);
    t = uv.x < p.x || uv.x > p.x + MOUSTACHELENGTH ? 1.0 : t;
	return vec4(1.0, 1.0, 1.0, 1.0 - t);
}

vec4 mulNoise(vec4 col, float f) {
    vec3 c = col.rgb * f;
	return vec4(c, col.a);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (2.0 * fragCoord.xy - iResolution.xy) / min(iResolution.x, iResolution.y);
    uv.y += 0.25;

    vec4 bgCol = vec4(0.7, 0.6, 0.5, 1.0);

    vec4 nCol = nose(uv, vec2(0.0, 0.0));

    vec2 m1UV = rot(uv, 30.0 / 180.0);
    vec4 m1Col = moustache(m1UV, vec2(MOUSTACHELENGTH, 0.0));
    vec2 m2UV = rot(uv, -30.0 / 180.0);
    vec4 m2Col = moustache(m2UV, vec2(MOUSTACHELENGTH, 0.0));

    vec2 e1UV = rot(uv, -PI / 5.0);
    e1UV = scale(e1UV, vec2(1.0, 1.1));
    vec2 e2UV = rot(uv, PI / 5.0);
    e2UV = scale(e2UV, vec2(1.0, 1.1));

    e1UV = animate(e1UV);
    e2UV = animate(e2UV);

    vec4 e1Col = ear(e1UV, vec2(0.0, 0.8));
    vec4 e1hCol = heart(e1UV, vec2(0.0, 0.93));
    vec4 e2Col = ear(e2UV, vec2(0.0, 0.8));
    vec4 e2hCol = heart(e2UV, vec2(0.0, 0.93));

    vec4 col = bgCol;
    col = mix(col, nCol, nCol.a);
    col = mix(col, m1Col, m1Col.a);
	col = mix(col, m2Col, m2Col.a);
	col = mix(col, e1Col, e1Col.a);
    col = mix(col, e1hCol, e1hCol.a);
	col = mix(col, e2Col, e2Col.a);
    col = mix(col, e2hCol, e2hCol.a);

	fragColor = col;
}

const float n = 10.0;

const float rateOfTime = 1.0; // = speed of rotation in rad/sec
const float screenSize = 2.0;

const float circleRadius = 1.0; //radius of circes
const float d = 2.0; //distance from center of screen to center of circle
const float circleWidth = .01;
const float bulletWidth = .05;
const float lineWidth = .1; //width of line in units of time, not distance





//A list of colors. Have fun messing with these!
const vec4 bgColor = vec4(0.25,0.0,0.3,1.0);
const vec4 polyColor = vec4(5.0,0.0,5.0,1.0);
const vec4 lineColor = vec4(1.0,0.99,1.0,1.0);
const vec4 capColor = vec4(1.0);

const float tau = 6.2831853071795864769;
//START: HELPER FUNCTIONS------------------------------------------------------------------------

bool inFrame(vec2 xy, float radius)
{
   return( 
       abs(xy.y) < circleRadius + bulletWidth &&                  //y within bounds of picture and
      (
       abs(xy.x) < d                                              //x between centers of circles
       ||                                                      //or
       radius < circleRadius + bulletWidth  //inside the two semicircles that stick out
      )
   );
}


bool inCircle(vec2 xy)
{
    return(abs(length(xy) - circleRadius) < .1);
}

float time()
{
    return(mod(iTime,tau)*rateOfTime);
}


bool inBullet(vec2 xy, float radius, float time)
{
    return(
        abs(length(vec2(abs(abs(xy.x)-d),xy.y))-circleRadius) < bulletWidth //&& abs(atan(xy.y,xy.x)-time) < bulletWidth
        );
}


float timeInLine(vec2 xy, float d)
{
    float x = xy.x;
    float y = xy.y;
    return(2.0*atan((-sqrt(-4.0*d*d*y*y+2.0*d*d+2.0*x*x+2.0*y*y-1.0)+d-x+y)/(2.0*d*y+d+x+y+1.0)));
}

