//MAIN------------------------------------------------------------------------
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Make an aspect ratio out of it and align the origin to the center of the screen
    vec2 xy = screenSize*(-1.0 + 2.0*fragCoord/iResolution.xy);
    xy.x *= iResolution.x/iResolution.y;




    float radius = sqrt(pow((abs(xy.x)-d),2.0) + pow((xy.y),2.0));
    if(abs(radius-1.0)<.005)
    {
        fragColor = polyColor;
        return;
    }


    //The current time, in two second loops.
    //0.0 is 0 seconds, tau is two seconds.
    float time = time();


    if (inBullet(xy,radius,time))
        fragColor = capColor;





    //don't do anything else if the pixel obviously isn't in picture
    //this step should come before calculation of time
    //only where it is right now b/c I'm not worried about optimization yet
    if(abs(xy.y)>1.0 || (abs(xy.x) > 2.0 && radius > 1.0))
       {
       fragColor = bgColor;
       return;
       }



    //draw line
   	//if (!(r < 1.0 && xy.x < 0.0))
    if(abs(mod(timeInLine(xy,d)-time,tau/n))<0.1
       && (!(radius < 1.0 && xy.x < 0.0))||

       abs(mod(timeInLine(vec2(-xy.x,xy.y),d)+time-tau/4.0,tau/n))<0.1 
       && (!(radius < 1.0 && xy.x > 0.0))

       //(mod(timeInLine(vec2(-1.0*xy.x,xy.y),d)-time,tau/n)<0.1))

      )
    {
        fragColor = lineColor;
        return;
    }




    fragColor = bgColor;

}

// note that your triangle was drawn for 2*h
bool isInTriangle(vec2 U, float h, float a)
{
    float s = sin(a), c = cos(a);
  	U *= mat2(c,-s,s,c);

    return     U.y > - h/3.  // distance from center to each sides
         && .5*U.y - 1.73/2. * U.x < h/3.
         && .5*U.y + 1.73/2. * U.x < h/3.;
}
vec3 hsv2rgb(float h, float s, float v)
{
    h = fract(h);
    vec3 c = smoothstep(2./6., 1./6., abs(h - vec3(0.5, 2./6., 4./6.)));
    c.r = 1.-c.r;

    return mix(vec3(s), vec3(1.0), c) * v;
}

vec3 getRandomColor(float f, float t)
{
    return hsv2rgb(f+t, 0.2+cos(sin(f))*0.3, 0.9);
}

void mainImage( out vec4 O,  vec2 U )
{
    float step = 15.0;
	vec2  R = iResolution.xy;
    float t = iTime;
    O =  vec4(1.0, 1.0, 1.0, 1.0);
    for(int i = 0; i < int(step); i++) {
        float m = 1.0/(step+1.0)*float(i);
        vec3 color = getRandomColor(float(i) *0.05 + 0.01, t);

        bool test = isInTriangle((U-.5*R)/R.y+(float(i)*0.01), mod(1000.0 - t, 1000.0/R.y) * 3.0 - m*15.0, (3.1415 * t)+(float(i)*5.0));
        if( test )   
    		O =  vec4(color, 1);
    }
}

#define MAX_STEPS 512
#define EPSILON 0.00001
#define MAX_DISTANCE 40.0
#define MAX_REFLECTIONS 4
#define MAX_REFRACTIONS 1
#define NUM_LIGHTS 1
//#define ANTIALIASING

#define GLASS_ETA 1.5
#define METAL_ETA 50.0
#define MAX_ETA 20.0

#define SKY_ID 0
#define FLOOR_ID 4

