// Utility function to convert from integer RGB to float
vec3 RGB(int r, int g, int b)
{
    return vec3(float(r), float(g), float(b))/255.0;
}

// Struct to keep track of the distance and the object ID
struct Object
{
    float d; 			// distance
    int id; 			// used to identify the object
    float eta; 			// used for refraction
    bool reflection; 	// enable reflection
    bool refraction; 	// enable refraction
};

// Signed distance of a point p from the surface of a sphere of radius r
float sdSphere(vec3 p, float r)
{
    return length(p) - r;
}

// Signed distance of a point p from the surface of a plane
float sdPlane(vec3 p)
{ 
    return p.y;
}

// Signed distance of a point p from the surface of a torus
float sdTorus(vec3 p, vec2 t)
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

// Unsigned distance of a point p from the surface of a box
float udBox(vec3 p, vec3 b)
{
    return length(max(abs(p) - b, 0.0));
}

// Union of two objects using distance fields
Object opUnion(Object ob1, Object ob2)
{
    if(ob1.d < ob2.d)
        return ob1;
    else
        return ob2;
}

// Subtraction of two objects using distance fields
Object opSubtract(Object ob1, Object ob2)
{
    if(-ob1.d < ob2.d)
        return ob1;
    else if(-ob2.d < ob1.d)
        return ob2;
}

// Find the distance of the point p to the nearest object
// Scene for testing purpose
Object map(vec3 p)
{
    Object s1 = Object(
        sdSphere(p - vec3(0.0, 0.6, 0.0), 0.6),
        6,
        GLASS_ETA,
        true,
        true
    );

    Object b1 = Object(
        udBox(p - vec3(0.0, 0.5, 0.0), vec3(0.5)),
        6,
        METAL_ETA,
        false,
        true
    );

    Object b2 = Object(
        udBox(p - vec3(1.2, 0.5, 1.2), vec3(0.5)),
        1,
        METAL_ETA,
        true,
        false
    );
    Object b3 = Object(
        udBox(p - vec3(1.2, 0.5, -1.2), vec3(0.5)),
        2,
        METAL_ETA,
        true,
        false
    );
    Object b4 = Object(
        udBox(p - vec3(-1.2, 0.5, 1.2), vec3(0.5)),
        3,
        METAL_ETA,
        true,
        false
    );
    Object b5 = Object(
        udBox(p - vec3(-1.2, 0.5, -1.2), vec3(0.5)),
        5,
        METAL_ETA,
        true,
        false
    );

    Object p1 = Object(
        sdPlane(p),
        FLOOR_ID,
        METAL_ETA,
        true,
        false
    );

    Object res = opUnion(s1, p1);
    //Object res = opUnion(b1, p1);

    res = opUnion(res, b2);
    res = opUnion(res, b3);
    res = opUnion(res, b4);
    res = opUnion(res, b5);

    return res;
}

// Return the object color based on the id and the point
vec3 color(int id, vec3 p)
{
    if(id == 1)
    {
        return RGB(204, 70, 70);
    }
    else if(id == 2)
    {
        return RGB(94, 204, 30);
    }
    else if(id == 3)
    {
        return RGB(30, 105, 204);
    }
    else if(id == 4)
    {
        // Implement checkerboard pattern
        vec3 grey = RGB(255, 255, 255);
        float f = mod(floor(4.0*p.z) + floor(4.0*p.x), 2.0);
        return 0.3 + grey * f;
    }
    else if(id == 5)
    {
        return RGB(200, 160, 0);
    }
    else if(id == 6)
    {
        return RGB(255, 255, 255);
    }
    return RGB(255, 255, 100);
}

// Determine the normal of a surface (gradient trick)
vec3 normal(vec3 p) {
	vec3 eps = vec3(0.001, 0.0, 0.0);
	vec3 nor = vec3(
		map(p + eps.xyz).d - map(p - eps.xyz).d,
		map(p + eps.zxy).d - map(p - eps.zxy).d,
		map(p + eps.yzx).d - map(p - eps.yzx).d
	);
	return normalize(nor);
}

// Determine light on the surface of an object: Lambertian model
// https://en.wikipedia.org/wiki/Lambertian_reflectance
vec3 LambertianLight(vec3 p, int id)
{
    // Check if we are dealing with the background
    if(id == 0)
        return color(id, p);

    // Add light to the object (light intensity and source)
    vec3 li = RGB(255, 255, 255);
    vec3 ls = vec3(2.0*cos(iTime), 2.5, 2.0*sin(iTime));
    //vec3 ls = vec3(0.0, 2.5, -1.0);

    // Determine the normal vector and the Lambertian coefficient
    vec3 sn = normal(p);
    vec3 ls_p = normalize(ls - p); // light source to surface point vector
    float NdotL = clamp(dot(ls_p, sn), 0.0, 1.0);

    // Determine light intensity based on the distance between the light source and the object
    float k = 0.2;
    NdotL *= (1.0 / (1.0 + dot(ls_p, ls_p) * k));

    // Add ambience light (light color and source)
    vec3 alc = RGB(255, 255, 255);
    //alc = vec3(0.0); // Disabling ambient light!
    vec3 als = vec3(0.0, 1.0, 0.0);

    // Determine the Lambertian coefficient and the ambience light intensity
    float aNdotL = 0.5 * dot(sn, normalize(als));
    float ak = 1.0;
    aNdotL *= (1.0 / (1.0 + dot(als, als) * ak));

    // Add object occlusion
    float occ = 0.5 + 0.5 * sn.y;

    // Apply the Lambertian reflectance
    vec3 c = color(id, p);
    c *= NdotL;
    c += aNdotL * alc;
    c += 0.05 * occ; 
    return c;
}

// Determine the object shadow using the light origin and direction
// http://www.iquilezles.org/www/articles/rmshadows/rmshadows.htm
float shadow(vec3 origin, vec3 direction) {
    float hit = 1000.0;
    float t = EPSILON;
    for (int i = 0; i < MAX_STEPS; i++) {
        float d = map(origin + direction * t).d;
        if (d < EPSILON)
            return 0.05;
        t += d;
        hit = min(hit, 20.0 * d / t);
        if (t > MAX_DISTANCE)
            break;
    }

    return clamp(hit, 0.0, 1.0);
}

// Determine reflection using Reflected Ray Origin and Direction
vec3 reflection(vec3 rro, vec3 rrd, vec3 ls)
{
    vec3 p = vec3(0.0);
    vec3 c = vec3(1.0);
    // Save ray origin
    vec3 origin = rro;
    float t = 0.01;
    Object hit = Object(0.0, 0, 0.0, false, false);
    // Reflect more times
    for(int j = 0; j < MAX_REFLECTIONS; j++)
    {
        // Raytrace the reflection vector
        for(int i = 0; i < MAX_STEPS; i++)
        {
            // Check if we hit an object in the scene
            hit = map(rro + rrd * t);
            if(hit.d < EPSILON)
                break;
            // Reset hit object to default
            hit = Object(hit.d, 0, 0.0, false, false);
            // Increment the steps
            t += hit.d;
            // Check the maximum distance
            if(t > MAX_DISTANCE)
                break;
        }
        // Check if we hit the sky
        if(hit.id == SKY_ID)
        	break;
        // We hit an object, determine if it reflects
        if(hit.reflection == false)
            break;
        // We hit an object, determine the base color
        p = rro + rrd * t;
        // We hit an object, determine if it refracts
        if(hit.refraction == false)
        	c *= color(hit.id, p) * shadow(p, ls);
        else
            c *= color(hit.id, p);
        // Determine new object normal and riflected ray direction
        vec3 N = normal(p);
        vec3 L = rrd;
        vec3 R = normalize(reflect(L, N));
        rro = p;
        rrd = R;
        t = 0.01;
    }
    return c;
}

// Determine refraction using Refraction Ray Origin and Direction
vec3 refraction(vec3 rro, vec3 rrd, vec3 ls)
{
    Object hit = Object(0.0, 0, 0.0, false, false);
    float t = 0.0;
    // Raytrace the refraction vector
    for(int i = 0; i < MAX_STEPS; i++)
    {
        // Check if we hit an object in the scene
        hit = map(rro + rrd * t);
        // Check if we exited the object
		if(hit.d > EPSILON)
            break;
        // Reset hit object to default
        hit = Object(hit.d, 0, 0.0, false, false);
        // Increment the steps
        t += 0.01;
        // Check the maximum distance
        if(t > MAX_DISTANCE)
            break;
    }
    // Check if we hit the sky or a non refractive material
    if(hit.id != 0)
    {
        // We hit the other face of the object
        vec3 p = rro + rrd * t;
        vec3 N = normal(p);
        vec3 L = normalize(p - rro);
        vec3 R = refract(L, -N, hit.eta);
        // Trace to see which object the ray will hit
        rro = p;
        rrd = R;
        t = 0.0;
        for(int i = 0; i < MAX_STEPS; i++)
        {
            // Check if we hit an object in the scene
            hit = map(rro + rrd * t);
            if(hit.d < EPSILON)
                break;
            // Reset hit object to default
            hit = Object(hit.d, 0, 0.0, false, false);
            // Increment the steps
            t += hit.d;
            // Check the maximum distance
            if(t > MAX_DISTANCE)
                break;
        }
    }
    vec3 c = color(hit.id, rro + rrd * t);

    if(hit.reflection == true)
        c *= reflection(rro, rrd, ls);

    return c;
}

// Determine light on the surface of an object: Blinn-Phong model
// https://en.wikipedia.org/wiki/Phong_reflection_model
// https://en.wikipedia.org/wiki/Blinn%E2%80%93Phong_shading_model
// p = point on the surface
// e = direction
// eye = viewer eye location
// ls = light source coordinates
// lc = light color
// obj = object hit by the ray
vec3 light(vec3 p, vec3 e, vec3 eye, vec3 ls[4], vec3 lc[4], Object obj)
{
    // Check if we hit the background
    if(obj.id == 0)
        return color(obj.id, p);

    // Setup intensities: ambient, diffuse, specular, reflection
    vec3 ia = RGB(255, 255, 255);
    vec3 id = RGB(255, 255, 255);
    vec3 is = RGB(255, 255, 255);
    vec3 ir = RGB(255, 255, 255);

    // Setup reflection constants: ambient, diffuse, specular
    vec3 ka = vec3(0.9);
    vec3 kd = vec3(0.6);
    vec3 ks = vec3(0.9);

    // Setup shininess of the material: specular
    float a = 64.0;

    // Define base color
    vec3 c = vec3(0.0);

    for(int i = 0; i < NUM_LIGHTS; i++)
    {
        // Determine useful vectors
        vec3 N = normal(p);				// vector normal to the surface
        vec3 L = normalize(ls[i] - p); 	// light-point direction
        vec3 H = normalize(-e + ls[i]); 	// light-eye direction

        // Determine the diffuse and specular coefficients
        float diff = clamp(dot(L, N), 0.0, 1.0);
        float spec = clamp(pow(dot(H, N), a), 0.0, 1.0);

        // Add ambient and object light
        float occ = 0.5 + 0.5 * N.y;
        float amb = clamp(0.5 + 0.5 * N.y, 0.0, 1.0);

        // Add shadows to the image
        float sha = shadow(p + N * EPSILON, L);

        // Add surface reflection
        vec3 esd = normalize(p - eye); // eye-surface direction
        vec3 R1 = normalize(reflect(esd, N));
        vec3 refl = reflection(p + N * EPSILON, R1, ls[i]);

        // Determine Blinn-Phong value (with reflection too)
        vec3 phong =
            amb * ia * occ * ka											// ambient
            + diff * id * occ * kd										// diffuse
            + diff * spec * is * occ * ks								// specular
            ;

        // Check if we need to add reflection to the object
        if(obj.reflection)
            phong += diff * refl * ir * occ * exp(-1.4*p.y);				// reflection

        // Check if we need to add refraction to the object
        if(obj.refraction)
        {
            // Add surface refraction
        	vec3 esd2 = normalize(eye - p);
        	vec3 R2 = normalize(refract(esd2, N, 1.0/obj.eta));
        	vec3 refr = refraction(p - N * EPSILON, R2, ls[i]);
            phong += occ * refr;										// refraction
        }

        // The light intensity depends on the distance
        float li = 3.0; // light intensity tweaker
        phong *= 1.0/(1.0 + dot(L, L) * li);

        // Mix lights
        if(i == 0)
        {
            c = color(obj.id, p) * lc[i] * phong;
            if(!obj.refraction)
                c *= sha;
        }
        else
        {
            if(!obj.refraction)
            	c += color(obj.id, p) * lc[i] * phong * sha;
            else
                c += color(obj.id, p) * lc[i] * phong;
        }

    }

    // Global intensity, let's tweak the shit out of this shader
    c *= 1.8;

    return c;
}

// Raymarching algorithm with distance fields
vec3 raymarch(vec3 ro, vec3 rd)
{
    // Save eye position
    vec3 eye = ro;
    // Define standard empty object
    Object obj = Object(0.0, 0, 0.0, false, false);
    // Maximum distance
    float t = 0.0;
    // Raymarch MAX_STEPS
    for(int i = 0; i < MAX_STEPS; i++)
    {
        // Passing the point origin to the scene
        obj = map(ro);
        // Checking if we hit something
        if(obj.d < EPSILON)
            break;
        // Incrementing the step
        ro += rd * obj.d;
        // Reset object to default
        obj = Object(obj.d, 0, 0.0, false, false);
        // Keep track of the maximum distance
        t += obj.d;
        if(t > MAX_DISTANCE)
            break;
    }
    // Determine pixel color
    vec3 lc[4], ls[4];
    lc[0] = vec3(1.0, 1.0, 1.0);
    lc[1] = vec3(0.0, 0.0, 0.0);
    lc[2] = vec3(0.0, 0.0, 0.0);
    lc[3] = vec3(1.0, 1.0, 1.0);
    ls[0] = vec3(4.0, 5.0, 4.0);
    ls[1] = vec3(6.0, 5.0, -6.0);
    ls[2] = vec3(-6.0, 5.0, 6.0);
    ls[3] = vec3(-2.0, 5.0, -2.0);
    vec3 color = light(ro, rd, eye, ls, lc, obj);
    // Add fog to the image (distance)
    float fogAmount = 1.0 - exp(-t*0.04);
    return mix(color, RGB(255, 255, 255), fogAmount);
}

// Total control over the camera
mat3 setCamera(vec3 origin, vec3 target, float rotation)
{
	vec3 forward = normalize(target - origin);
	vec3 orientation = vec3(sin(rotation), cos(rotation), 0.0);
    vec3 left = normalize(cross(forward, orientation));
    vec3 up = normalize(cross(left, forward));
    return mat3(left, up, forward);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Normalized coordinates [-1, 1]
    vec2 uv = 2.0*(fragCoord.xy/iResolution.xy) - 1.0;

    // Aspect ratio
    uv.x *= iResolution.x/iResolution.y;

    // Camera position and "look at"
    vec3 origin = vec3(0.0, 1.6, 0.0);
    vec3 target = vec3(0.0, 0.0, 0.0);

    float speed = 0.5;
    origin.x += 3.5 * cos(speed*iTime + 0.0*iMouse.x);
    origin.z += 3.5 * sin(speed*iTime + 0.0*iMouse.y);

    mat3 toWorld = setCamera(origin, target, 0.0);
    vec3 direction = toWorld * normalize(vec3(uv.xy, 2.0));

    // Raymarch scene  
#ifdef ANTIALIASING
    vec3 color = vec3(0.0);
    vec3 point = origin;
    float AA = 0.00055;
    //float AA = 0.002;
    for(int i = 0; i < 10; i++)
    {
        if(i == 1)
        {
            point.x = origin.x - AA;
            point.y = origin.y - AA;
            point.z = origin.z - AA;
        } else if(i == 2)
        {
            point.x = origin.x + AA;
            point.y = origin.y - AA;
            point.z = origin.z - AA;
        } else if(i == 3)
        {
            point.x = origin.x - AA;
            point.y = origin.y + AA;
            point.z = origin.z - AA;
        } else if(i == 4)
        {
            point.x = origin.x + AA;
            point.y = origin.y + AA;
            point.z = origin.z - AA;
        } else if(i == 5)
        {
            point.x = origin.x - AA;
            point.y = origin.y - AA;
            point.z = origin.z + AA;
        } else if(i == 6)
        {
            point.x = origin.x + AA;
            point.y = origin.y - AA;
            point.z = origin.z + AA;
        } else if(i == 7)
        {
            point.x = origin.x - AA;
            point.y = origin.y + AA;
            point.z = origin.z + AA;
        } else if(i == 8)
        {
            point.x = origin.x + AA;
            point.y = origin.y + AA;
            point.z = origin.z + AA;
        }
    	color += raymarch(point, direction);
    }
    color /= 10.0;
#else
    vec3 color = raymarch(origin, direction);
#endif
    // Adjust the gamma
    vec3 gamma = vec3(1.0/0.9);
    color = pow(color, gamma);

    // Return pixel color
	fragColor = vec4(color ,1.0);
}

#define MARCH_DIST      2.5
#define MARCH_COUNT     20
#define SCATTER_DEPTH   10
#define SCATTER_DIST    2.5
#define SPHERE_RADIUS   2.4
#define SPHERE_DENSITY  3.0
//#define LIGHT_POS vec3(1.0 * cos(iTime), 10.0, 15.0)
#define LIGHT_COLOR 1.2 * vec3(0.5, 0.5, 1.0)
#define ABSORPTION_COLOR 0.0028 * vec3(1.0, 0.85, 0.7)
#define VOLUME_COLOR 0.045 * vec3(1.0, 0.75, 0.3)
#define NOISE_SCALE 1.2
#define BG_COLOR 1.3 * vec3(0.15, 0.25, 0.25)

float hash(float n)
{
    return fract(sin(n)*43758.5453);
}

float noise(in vec3 x)
{
    vec3 p = floor(x);
    vec3 f = fract(x);

    f = f * f * (3.0 - 2.0 * f);

    float n = p.x + p.y * 57.0 + 113.0 * p.z;

    float res = mix(mix(mix( hash(n+  0.0), hash(n+  1.0),f.x),
                        mix( hash(n+ 57.0), hash(n+ 58.0),f.x),f.y),
                    mix(mix( hash(n+113.0), hash(n+114.0),f.x),
                        mix( hash(n+170.0), hash(n+171.0),f.x),f.y),f.z);
    return res;
}

float fbm( vec3 p )
{
    float f;
    f  = 0.5000*noise( p ); p = p*2.02;
    f += 0.2500*noise( p ); p = p*2.03;
    f += 0.1250*noise( p );
    return f;
}

float get_density_at_pos(vec3 p)
{
    float d = (SPHERE_RADIUS - length(p)) / SPHERE_RADIUS;
    float noise_mult = fbm(NOISE_SCALE * (p + vec3(0.0, 0.0, iTime)));
    return clamp(noise_mult * SPHERE_DENSITY * d, 0.0, 1.0);
}

//TODO: actual Rayleigh scattering
vec3 get_scatter_color(vec3 p)
{
    float absorption = 0.0;
    vec2 m = 2.0 * iMouse.xy / iResolution.xy - 1.0;
    vec3 light_pos = vec3(20.0 * m, 10.0);
    vec3 light_dir = normalize(light_pos - p);

    float t = 0.0;
    float rd = SCATTER_DIST / float(SCATTER_DEPTH);

    for(int i = 0; i < SCATTER_DEPTH; i++)
    {
    	vec3 sp = p + t * light_dir;
        float d = get_density_at_pos(sp);
        absorption += d;
    	t+= rd;
    }


    return clamp(LIGHT_COLOR * VOLUME_COLOR - absorption * ABSORPTION_COLOR, 0.0, 1.0);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	//1 : retrieve the fragment's coordinates
	vec2 uv = ( fragCoord.xy / iResolution.xy ) * 2.0 - 1.0;
	//preserve aspect ratio
	uv.x *= iResolution.x / iResolution.y;


	//2 : camera position and ray direction
	vec3 pos = vec3( 0.,0.,-3.);
	vec3 dir = normalize( vec3( uv, 1. ) );

	vec3 ip;
    float t = 0.0;
    float density = 0.0;
    vec3 march_color = BG_COLOR;
    float rd = MARCH_DIST / float(MARCH_COUNT);
    for(int i = 0; i < MARCH_COUNT; i++)
    {
        ip = pos + dir * t;
        float d = get_density_at_pos(ip);

        density += d;
        vec3 c = get_scatter_color(ip);
        march_color += density * c;
        t += rd;
    }


	//4 : apply color to this fragment
	fragColor = vec4(march_color, 1.0);

}
