void mainImage( out vec4 fragColor, in vec2 fragCoord ) {


	const float yscl = 800.0;
	const float f = 450.0;

    vec2 uvn = (fragCoord - 0.5*iResolution.xy)/iResolution.y;
	vec2 uv = uvn*yscl;


    vec3 rd = normalize(vec3(uv, f));
    rd = normalize(vec3(rd.xy, rd.z - dot(rd.xy, rd.xy)*.15)); // Subtle lens distortion.

	vec3 ro = vec3(0, 0, iTime*5.);
	vec3 tgt = ro + vec3(0, 0, .25);
	vec3 up = vec3(0, 0, 1);



    lp = ro + vec3(FAR*.24, FAR*.52, FAR*.78);



    vec2 sw = path(tgt.z);

    ro.xy += path(ro.z);
	tgt.xy += sw;

    // The original uses a different coordinate system, so this is a quick hack
    // to enable me to drop in a "Z into the page" coordinate system.
    ro = ro.zxy;
    tgt = tgt.zxy;
    lp = lp.zxy;    

	vec3 rz = normalize(tgt - ro);
	vec3 rx = normalize(cross(rz,up));
	vec3 ry = cross(rx,rz);

    mat3 R = mat3(rx,ry,rz);



	float thetax = sw.y/32.;
	float thetay = 0.; // sw.x/64.

/*  
    // Mouse camera direction overide.
	if (iMouse.y > 10.0 || iMouse.x > 10.0) { 
		thetax = (iMouse.y - 0.5*iResolution.y) * -1.25/iResolution.y;
		thetay = (iMouse.x - 0.5*iResolution.x) * 6.28/iResolution.x; 
	}
*/ 

	float cx = cos(thetax);
	float sx = sin(thetax);
	float cy = cos(thetay);
	float sy = sin(thetay);

	mat3 Rx = mat3(cx, 0, -sx, 0, 1, 0, sx, 0, cx);
    mat3 Ry = mat3(cy, -sy, 0, sy, cy, 0, 0, 0, 1);

    ro = Ry*Rx*(ro-tgt) + tgt;
    rd = Ry*Rx*R*rd;

    rd.yz *= r2(-sw.x/64.);


    vec3 color = ray_shade(ro, rd);

    // Some post processing. Wasn't used.
    //color = mix(color, color.xxx, .8);
    //color = mix(color.xxx, pow(color.xxx, vec3(1, 2, 3)), .35);



    // Subtle vignette.
    uv = fragCoord/iResolution.xy;
    color *= pow(16.*uv.x*uv.y*(1. - uv.x)*(1. - uv.y) , .125)*.5 + .5;

	color = sqrt(color);

    fragColor = vec4(color, 1.0);

}

// Simplex 2D noise
// https://gist.github.com/patriciogonzalezvivo/670c22f3966e662d2f83
vec3 permute(vec3 x) { return mod(((x*34.0)+1.0)*x, 289.0); }

float snoise(vec2 v){
  const vec4 C = vec4(0.211324865405187, 0.366025403784439,
           -0.577350269189626, 0.024390243902439);
  vec2 i  = floor(v + dot(v, C.yy) );
  vec2 x0 = v -   i + dot(i, C.xx);
  vec2 i1;
  i1 = (x0.x > x0.y) ? vec2(1.0, 0.0) : vec2(0.0, 1.0);
  vec4 x12 = x0.xyxy + C.xxzz;
  x12.xy -= i1;
  i = mod(i, 289.0);
  vec3 p = permute( permute( i.y + vec3(0.0, i1.y, 1.0 ))
  + i.x + vec3(0.0, i1.x, 1.0 ));
  vec3 m = max(0.5 - vec3(dot(x0,x0), dot(x12.xy,x12.xy),
    dot(x12.zw,x12.zw)), 0.0);
  m = m*m ;
  m = m*m ;
  vec3 x = 2.0 * fract(p * C.www) - 1.0;
  vec3 h = abs(x) - 0.5;
  vec3 ox = floor(x + 0.5);
  vec3 a0 = x - ox;
  m *= 1.79284291400159 - 0.85373472095314 * ( a0*a0 + h*h );
  vec3 g;
  g.x  = a0.x  * x0.x  + h.x  * x0.y;
  g.yz = a0.yz * x12.xz + h.yz * x12.yw;
  return 130.0 * dot(m, g);
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float x = cos(iTime);
    float y = sin(iTime);
    vec2 p = -1.0 + 2.0 * fragCoord.xy / iResolution.xy;
    p.x += x;
    p.y += y;
    float n = snoise(p + iTime);
	float radius = sqrt(p.x * p.x + p.y * p.y) + n; 
	fragColor = vec4(radius*0.5, 0.3*radius, 1.0-radius, 1.0);
}

// OutLine
#define OL(v,p,f,w) f;{v+=(1.-(f));p*=(1.+w);v+=(f);}

#define sin1(a) (sin(a)*.5+.5)

float circle(vec2 p, float r)
{
    return smoothstep(1.,.975,length(p/r));
}

float rect(vec2 p, float w, float h)
{
 	p=abs(p/vec2(w,h));
    return smoothstep(1.,.975,max(p.x,p.y));
}

float rect(vec2 p,float a)
{
    return rect(p,a,a);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 p = -1.+2.*uv;
    p.x*=iResolution.x/iResolution.y;
    vec3 col = vec3(.0);

    float v = 0.;
    float w = OL(v,p,max(circle(p,1.),rect(p,.9)),sin1(iTime)*.15);

    col += mix(
        w*mix(vec3(1.,.0,.0),vec3(.0,1.,.0),v),
        vec3(w),step(uv.x,.5));

	fragColor = vec4(col,1.0);
}

const int MAX_MARCHING_STEPS = 255;
const float MIN_DIST = 0.0;
const float MAX_DIST = 100.0;
const float EPSILON = 0.0001;

float boxSDF( vec3 p, vec3 b )
{
  return length(max(abs(p)-b,0.0));
}

float shortestDistanceToSurface(vec3 eye, vec3 marchingDirection, float start, float end)
{
    // Start depth
    float depth = start;
    // Keep looking for where the marching ray hits a surface
    for (int i = 0; i < MAX_MARCHING_STEPS; ++i)
    {
        // Get the distance from marching ray point to surface of box
        float dist = boxSDF(eye + marchingDirection * depth, vec3(0.5));
        // If we've hit near the surface, return this distance
        if (dist < EPSILON)
        {
            return depth;
        }
        // Didn't find anything, let's go to where we found something
        depth += dist;
        // We're at the end, stop
        if (depth > end)
        {
            return end;
        }
    }
    // Ran out of steps before we hit the end, just return end
    return end;
}

/**
    fov:         Field of View of camera
    screen_size: Screen size
    fragCoord:   Screen coord of pixel
    return:      Direction of rendering ray of the projection camera
 */
vec3 rayDirection(float fov, vec2 screen_size, vec2 fragCoord)
{
	vec2 xy = fragCoord - screen_size / 2.0;
    float z = (screen_size.y / 2.) / tan(radians(fov) / 2.);
    return normalize(vec3(xy,-z));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 march_dir = rayDirection(60., iResolution.xy, gl_FragCoord.xy);
    float dist = shortestDistanceToSurface(
        vec3(
                cos(iTime) * 1.,
                0,
                (sin(iTime)+2.) * 2.
            ), 
        march_dir, MIN_DIST, MAX_DIST);
    if (dist > MAX_DIST - EPSILON)
    {
        fragColor = vec4(0.);
        return;
    }
    fragColor = vec4(2./dist);
}

