/*
 From left to right:
       Gradient     |    round    | texture noise | pseudo blue noise | cheap gaussian noise
*/


float mask(vec2 p) // see https://www.shadertoy.com/view/ldyXDd
{
#define T(l) textureLod(iChannel1,U/256.,l)
#define DMUL  8.12235325   
#define SIZE  5.5
    vec2 U = floor(p/SIZE)*SIZE;
    p += ( T(0.).xy - .5 ) *DMUL;
    return fract( p.x*1.705 + p.y*.5375 ); 
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float p = 0.5 + dot( uv - vec2( ceil( uv.x * 3.0 ) / 3.0 - 0.16666, 0.5), vec2(sin(iTime), 1.0));
    fragColor = vec4( mix( COLOR_TOP, COLOR_BOTTOM, p ), 1.0 );

    if ( uv.x > 0.333333 )
    {
        float m =
           uv.x < 0.5 ? 0.5 :
         ( uv.x < 0.666 ? texture(iChannel0, fragCoord.xy/iChannelResolution[0].xy).r :
         ( uv.x < 0.8333 ? mask( fragCoord ) :
         ( texture(iChannel1, fragCoord.xy/iChannelResolution[0].xy).r +
           texture(iChannel1, fragCoord.xy/iChannelResolution[0].xy).g +
           texture(iChannel1, fragCoord.xy/iChannelResolution[0].xy).b ) / 3.0
            ) );

		fragColor.rgb = m > p ? COLOR_TOP : COLOR_BOTTOM;
    }
}

float rand(vec2 uv)
{
    return fract(sin(dot(uv, vec2(12.9898, 78.233))) * 43758.5453);
}

vec3 hue2rgb(float h)
{
    h = fract(h) * 6.0 - 2.0;
    return clamp(vec3(abs(h - 1.0) - 1.0, 2.0 - abs(h), 2.0 - abs(h - 2.0)), 0.0, 1.0);
}

vec2 uv2tri(vec2 uv)
{
    float sx = uv.x - uv.y / 2.0; // skewed x
    float sxf = fract(sx);
    float offs = step(fract(1.0 - uv.y), sxf);
    return vec2(floor(sx) * 2.0 + sxf + offs, uv.y);
}

vec3 tri(vec2 uv)
{
    vec2 p = floor(uv2tri(uv));
    float h = rand(p + 0.1) * 0.2 + iTime * 0.2;
    float s = sin((rand(p + 0.2) * 3.3 + 1.2) * iTime) * 0.5 + 0.5;
    return hue2rgb(h) * s + 0.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    vec2 uv = (fragCoord.xy - iResolution.xy / 2.0) / iResolution.y;

    float t1 = iTime / 2.0;
    float t2 = t1 + 0.5;

    vec3 c1 = tri(uv * (8.0 - 4.0 * fract(t1)) + floor(t1) * 4.0);
    vec3 c2 = tri(uv * (8.0 - 4.0 * fract(t2)) + floor(t2) * 4.0 + 2.0);

    fragColor = vec4(mix(c1, c2, abs(1.0 - 2.0 * fract(t1))), 1.0);
}

