    return normalize(vec3(xy, -z));
}


mat4 viewMatrix(vec3 eye, vec3 center, vec3 up) {
	vec3 f = normalize(center - eye);
	vec3 s = normalize(cross(f, up));
	vec3 u = cross(s, f);
	return mat4(
		vec4(s, 0.0),
		vec4(u, 0.0),
		vec4(-f, 0.0),
		vec4(0.0, 0.0, 0.0, 1)
	);
}

float checker(float x, float y) {
    return mod(mod(x, 1.) + mod(y, 1.), 2.);
}

float regularPoints(float x)
{
    return mod(x, 1.);
}

float sphereSDF(vec3 p, vec3 center, float radius) {
    return length(p - center) - radius;
}

float squareSDF(vec3 p, vec3 center, vec3 dimensions) {

    vec3 d = abs(p - center) - dimensions / 2.;

  	return min(max(d.x, max(d.y, d.z)), 0.) + length(max(d, 0.));
}

object cylinderX(vec3 samplePoint, float radius) {

    vec3 p = samplePoint;
    p.x = 0.;

	return object(length(p) - radius, vec3(1., 1., 1.) * regularPoints(samplePoint.x), vec3(1.));
}

object cylinderY(vec3 samplePoint, float radius) {

    vec3 p = samplePoint;
    p.y = 0.;

	return object(length(p) - radius, vec3(1., 1., 1.) * regularPoints(samplePoint.y), vec3(1.));
}

object cylinderZ(vec3 samplePoint, float radius) {

    vec3 p = samplePoint;
    p.z = 0.;

	return object(length(p) - radius, vec3(1., 1., 1.) * regularPoints(samplePoint.z), vec3(1.));
}

object behindPlane(vec3 p) {

    vec3 color = vec3(0., 1.1, .99) * (0.3 + 0.7 * mod((p.x - 2.) + (p.y - 2.) * (p.x - 2.) + iTime / 9., 1.));

	return object(p.z + 3., color, vec3(1.));
}

float polarCheckers(vec2 xy) {
    float r = length(xy) - mod(iTime, 2.);
    float a = atan(xy.y / xy.x) + iTime;

    return mod(mod(r, 1.) + mod(a, 2. * 3.1415 / 32.), 2.);
}

object leftPlane(vec3 p) {

    vec3 diffColor = vec3(0., 1., 0.) * polarCheckers(p.yz * 2.);

	return object(p.x + 3., diffColor, vec3(1.));
}

object rightPlane(vec3 p) {
    vec3 diffColor = vec3(0., 1., 0.) * polarCheckers(p.yz * 2.);

	return object(3. - p.x, diffColor, vec3(1.));
}

object belowPlane(vec3 p) {

    vec3 diffColor = vec3(.9, .5, .9) * checker(-p.x, p.z);

	return object(2. + p.y, diffColor, vec3(1.));
}

object abovePlane(vec3 p) {
    float sdf = 2. + .3 * sin(iTime) - p.y;

    vec3 diffColor = vec3(.9, .5, .9) * checker(p.x, p.z);

	return object(sdf, diffColor, vec3(1.));
}

object oUnion(object o1, object o2) {

    float sdf1 = o1.sdf;
    float sdf2 = o2.sdf;

    vec3 diffColor = mix(o1.diffColor, o2.diffColor, step(EPSILON, sdf1));

	return object(min(sdf1, sdf2), diffColor, vec3(1.));
}

float triangleWave(float x) {
	return abs(mod(x, 2.) - 1.);
}

object axes(vec3 p) {

    float radius = 0.03;

    object xAxis = cylinderX(p, radius);
    object yAxis = cylinderY(p, radius);
    object zAxis = cylinderZ(p, radius);

	return oUnion(oUnion(xAxis, yAxis), zAxis);
}

float sphericChecker(vec3 p) {
	float a = atan(p.z / p.x) + iTime;
    float b = atan(p.z / p.y) + iTime;

    return mod(mod(a, 2. * PI / 32.) + mod(b, 2. * PI / 32.), 2.);
}

object sphere2(vec3 p) {

    float cx = -2.5 + 5. * triangleWave(iTime * 15. / 32. + 2.4387);
    float cz = -0.5 + 13. * triangleWave(iTime * 13. / 32.);

    vec3 center = vec3(cx, -1.5, cz);

    float sdf = sphereSDF(p, center, .5);

    vec3 diffColor = vec3(.9, .8, .3) * sphericChecker(p - center);

    return object(sdf, diffColor, vec3(1.));
}


vec3 rotateX(vec3 p, float angle) {
	vec3 result = p;

    result.y = p.y * cos(angle) - p.z * sin(angle);
    result.z = p.y * sin(angle) + p.z * cos(angle);

    return result;
}

object scene(vec3 p) {

    object light = object(length(lightPos - p) - .03, vec3(1.), vec3(1.));

    object rightAndLeftWalls = oUnion(leftPlane(p), rightPlane(p));
    object floorAndCeiling = oUnion(belowPlane(p), abovePlane(p));

    object walls = oUnion(behindPlane(p), oUnion(rightAndLeftWalls, floorAndCeiling));

    float sphere1SDF = sphereSDF(p, vec3(0., 0., 0.), 1.);
    object sphere1 = object(sphere1SDF, vec3(1., 1., 0.), vec3(1.));

    object sphere2Obj = sphere2(p);

    object axesObj = axes(p);

    vec3 square1Coord = rotateX(p - vec3(2., 0., 0.), iTime * 5.);

    object square1 = object(squareSDF(square1Coord, vec3(0.), vec3(.5)), vec3(.8), vec3(1.));

    object spheres = oUnion(sphere1, sphere2Obj);

    object scene = oUnion(oUnion(spheres, walls), oUnion(square1, axesObj));

    return object(scene.sdf, scene.diffColor, vec3(1.));
}

float sceneSDF(vec3 p) {
	object sceneObj = scene(p);

    return sceneObj.sdf;
}

vec3 estimateNormal(vec3 p) {
	return normalize(vec3(
    	sceneSDF(vec3(p.x + EPSILON, p.y, p.z)) - sceneSDF(vec3(p.x - EPSILON, p.y, p.z)),
        sceneSDF(vec3(p.x, p.y + EPSILON, p.z)) - sceneSDF(vec3(p.x, p.y - EPSILON, p.z)),
        sceneSDF(vec3(p.x, p.y, p.z + EPSILON)) - sceneSDF(vec3(p.x, p.y, p.z - EPSILON))
    ));
}

float shortestDistanceToSurface(vec3 eye, vec3 marchingDirection, float start, float end) {
    float depth = start;
    for (int i = 0; i < MAX_MARCHING_STEPS; i++) {
        float dist = sceneSDF(eye + depth * marchingDirection);
        if (dist < EPSILON) {
			return depth;
        }
        depth += dist;
        if (depth >= end) {
            return end;
        }
    }
    return end;
}

vec3 phongShading(vec3 diffColor, vec3 specColor, vec3 surfPoint, vec3 surfNorm, vec3 lightPos, vec3 eyePos)
{
    vec3 visionDir = normalize(eyePos - surfPoint);
    vec3 lightVec = lightPos - surfPoint;
    vec3 lightDir = normalize(lightVec);
    float lightDist = length(lightVec);

    // =================== Figure out if light is occluded by obstacles ====================//

    float distToObstacle = shortestDistanceToSurface(
        surfPoint,
        normalize(lightVec),
        EPSILON * 20.,
        lightDist
    );

    if (distToObstacle < lightDist) {
    	return vec3(0.); // No dice. We're in the shadow.
    }

    // ================= Good, we're not in the shadow! compute light intensity =============//

    float diffDot = dot(lightDir, surfNorm);
    float specDot = dot(normalize(visionDir), normalize(reflect(-lightDir, surfNorm)));

    if (diffDot < 0.) {
    	return vec3(0.);
    }

    vec3 diffIntensity = diffColor * diffDot;

    if (specDot < 0.) {
    	return diffIntensity;
    }

    return diffIntensity + 0.5 * specColor * pow(specDot, 10.);
}

vec3 handleLights(vec3 diffColor, vec3 specColor, vec3 surfPoint, vec3 surfaceNormal, vec3 eyePos) {
    float light1PosRadius = 2.8;
    float light1PosAngle = iTime * 2. / 10.;

    vec3 light1Pos = vec3(
        light1PosRadius * cos(light1PosAngle),
        1.5 * sin(iTime / 1.74327623 / 10.),
        light1PosRadius * sin(light1PosAngle)
    );

    vec3 color = phongShading(
        diffColor,
        specColor,
        surfPoint,
        surfaceNormal,
        light1Pos,
        eyePos
    );

    vec3 light2Pos = vec3(-1., 1., 1.);

    color += phongShading(
        diffColor,
        specColor,
        surfPoint,
        surfaceNormal,
        light2Pos,
        eyePos
    );

    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 viewDir = rayDirection(45.0, iResolution.xy, fragCoord);
    vec3 eye = vec3(0.0, 0.2, 12.0);

    float eyeTargetX = sin(1. * iMouse.x / iResolution.x - .5);
    float eyeTargetY = sin(1. * iMouse.y / iResolution.y - .5);

    vec3 eyeTargetPos = eye + vec3(eyeTargetX, eyeTargetY, -1.);

    mat4 viewToWorld = viewMatrix(eye, eyeTargetPos, vec3(0., 1., 0.));
    vec3 worldDir = (viewToWorld * vec4(viewDir, 0.0)).xyz;

    float lightPosRadius = 2.8;
    float lightPosAngle = iTime * 2. / 2.;

    lightPos = vec3(
        lightPosRadius * cos(lightPosAngle),
        1.5 * sin(iTime / 1.74327623 / 2.),
        lightPosRadius * sin(lightPosAngle)
    );

    float dist = shortestDistanceToSurface(eye, worldDir, MIN_DIST, MAX_DIST);

    if (dist > MAX_DIST - EPSILON) {
        // Didn't hit anything
        fragColor = vec4(0.0, 0.0, 0.0, 0.0);
		return;
    }

    vec3 surfPoint = eye + dist * worldDir;

    object sceneObj = scene(surfPoint);

    vec3 surfaceNormal = estimateNormal(surfPoint);

    vec3 color = handleLights(
        sceneObj.diffColor,
        sceneObj.specColor,
        surfPoint,
        surfaceNormal,
        eye
    );

    fragColor = vec4(.5 * color + 0.1, 1.);
}

