//constants
//raymarching
const float EPSILON = 0.001;
const int MAX_STEPS = 150;
//camera
const float NEAR = 0.0;
const float FAR = 300.0;
//fog
const float FOG_START = 1.0;
const float FOG_END = 10.0;

struct light
{
    vec3 position;
    vec3 color;
    float ambientCoef;
    float attenuation;
};

struct material
{
    vec3 color;
    vec3 specularColor;
    float shininess;
};

light light0 = light(vec3(0.8,0.5,1.8), vec3(0.5,0.5,0.5), 0.02, 0.02);

material material0 = material(vec3(0.0,1.0,0.0), vec3(1.0,1.0,1.0), 10.0);

//environment
vec4 backgroundColor = vec4(0.0, 0.0, 0.0, 1.0);
float fov = 45.0;

//distance functions for the primitives
float sphereDist (vec3 point, float radius)
{
    return length(point)-radius;
}

float boxDist( vec3 point, vec3 dimensions )
{
    return length(max(abs(point)-dimensions,0.0));
}

//gets the minimal distance of the whole scene
//the scene is specified here
//transformations are done by transforming the point
float sceneMinDist (vec3 point)
{
    return min(sphereDist(point,1.0),
               boxDist(point+vec3(0.0,-1.0,0.0), vec3(60.0,0.1,60.0)));
}

//view matrix
mat4 lookAt(vec3 cam, vec3 focusPoint, vec3 up) {
    //we need to create the new coord system
    vec3 z = normalize(focusPoint-cam);
    vec3 x = cross(z, up);
    vec3 y = cross(z,x);
    //simply make a matrix of this system
    return mat4(vec4(x, 0.0),vec4(y, 0.0),vec4(-z, 0.0),vec4(0.0, 0.0, 0.0, 1));
}


//aproximates the normal based on the gradient change, gradient tells us which direction is the fastest growth of function, direction to the object is negative so gradient = normal
//gradient is calculated as partial derivation by x,y,z for each element of f(x,y,z), approximation by nearby points gives us this function:
vec3 getNormal(vec3 p) {
    return normalize(vec3(
                         sceneMinDist(vec3(p.x + EPSILON, p.y, p.z)) - sceneMinDist(vec3(p.x - EPSILON, p.y, p.z)),
                         sceneMinDist(vec3(p.x, p.y + EPSILON, p.z)) - sceneMinDist(vec3(p.x, p.y - EPSILON, p.z)),
                         sceneMinDist(vec3(p.x, p.y, p.z  + EPSILON)) - sceneMinDist(vec3(p.x, p.y, p.z - EPSILON))
                     ));
}

//raymarching function, returns the distance to the closest surface
//gets the cam position, direction of the marching ray and near and far clipping params
float closestDistance(vec3 cam, vec3 direction, float near, float far)
{
    float t=near;

    //TODO: recognize object and return the correct material
    for (int i=0; i<MAX_STEPS; i++)
    {
        vec3 ray=cam+direction*t;
        float minDistance = sceneMinDist(ray);
        if (minDistance<EPSILON)
            return t;

        t +=minDistance;

        if (t >= far)
            return far;
    }
    return far;
}

vec3 blinnPhong(vec3 cameraPos,light light0, vec3 point)
{
    //blinn-phong lighting model
    vec3 normal = getNormal(point);
    vec3 surfaceToLight = normalize(light0.position - point);

    //ambient aka when there is no light at all
    vec3 ambient = light0.ambientCoef*material0.color;
    ambient *= light0.color;

    //diffuse - that normal shading
    //cosine of the angle of incidence = dot(x,y)/(|x|*|y|) where lengths are 1 - normalized
    float diffuseCoef = max(0.0,dot(normal, surfaceToLight));
    vec3 diffuse = diffuseCoef*light0.color*material0.color;

    //specular - shiny stuff
    //if it was a mirror, how much would the reflection from the light go to the camera?
    //incidence vector = -surfaceToLight
    vec3 specular = vec3(0.0,0.0,0.0);
    if (diffuseCoef > 0.0)
    {
        vec3 surfaceToCamera = normalize(cameraPos - point);
        //normal phong calculates the cos of angle between vector to camera and reflection
        //vec3 reflection = reflect(-surfaceToLight, normal);
        //blinn adds the halfway vector
        vec3 halfway = normalize(surfaceToLight+surfaceToCamera);
        float cosAngle = max(0.0, dot(normal,halfway));
        float specularCoef = pow(cosAngle,material0.shininess);
        specular = specularCoef*material0.specularColor*light0.color;
    }


    //attenuation - loss of light over distance from the light source
    float distanceToLight = length(light0.position - point);
    float attenuation = 1.0/(1.0 + light0.attenuation * pow(distanceToLight,2.0));
    vec3 linearColor = ambient + attenuation*(diffuse+specular);

    return linearColor;
}


void mainImage(out vec4 fragColor, in vec2 fragCoord )
{

    vec3 cameraPos=vec3(0.0,0.0,5.0);

    //let's normalize the point on the view plane to -1-1 coordinates, center is 0,0
    vec2 planeCoord = (fragCoord.xy/iResolution.xy)*2.0-1.0;
    //we need to fix one dimension
    planeCoord.x *= iResolution.x/iResolution.y;

    //x,y of the current plane point, the z is based on the right triangle where tan = A/B (right angle at center of screen, A-half of screen, B is Z), - sign defines the direction of Z
    //since we have -1 to 1 plane, half is 1.0
    vec3 rayDirection = vec3(planeCoord.xy, -(1.0 / tan(radians(fov))));
    rayDirection = normalize(rayDirection);

    //animation of the camera
    //sin and cos are related to the unit circle, polar coordinates r*cos/sin+center
    cameraPos.x = 2.0*cos(iTime);
    cameraPos.z = 2.0*sin(iTime);
    //calculate the rotation for the camera direction
    //creates the view->world matrix
    mat4 rrr = lookAt(cameraPos,vec3(0.0,0.0,0.0), vec3(0.0,1.0,0.0));
    //apply it to the rays that are coming out of it, result in world space
    rayDirection = (rrr*vec4(rayDirection,0.0)).xyz;

    //raymarching
    float dist = closestDistance(cameraPos, rayDirection, NEAR, FAR);
    //the ray went too far, nothing
    vec3 linearColor = backgroundColor.xyz;
    //the ray hit something, get the color then
    if (dist-EPSILON < FAR)
    {
        //get the point in world space
        vec3 point = cameraPos+rayDirection*dist;
        linearColor = blinnPhong(cameraPos, light0, point);
    }

    //now let's get the fog factor using simple linear fog
    float fogFactor = (FOG_END-dist)/(FOG_END - FOG_START); 
    fogFactor = clamp(fogFactor, 0.0, 1.0);
    linearColor = mix(vec3(0.7,0.7,0.7), linearColor, fogFactor);

    //gamma correction - transform the linear rgb to more natural one for human eye (CRT style)
    vec3 gamma = vec3(1.0/2.2);
    fragColor = vec4(pow(linearColor,gamma), 1.0);
}

