  float dMin, dMinO, dMinI, d, h, ws, wf;
  dMinO = dstFar;
  dMinI = dstFar;
  wf = 0.98;
  h = 0.5;
  r2 = vec2 (0.2, 0.3 + 0.05 * clamp (p.z, -2., 2.));
  d = PrEllips2Df (p.xz, r2);
  d = min (max (d, abs (p.y) - h), length (vec2 (d, p.y)) - h);
  dMinO = min (d, dMinO);
  d = PrEllips2Df (p.xz, wf * r2);
  d = min (max (d, abs (p.y) - wf * h), length (vec2 (d, p.y)) - wf * h);
  dMinI = min (d, dMinI);
  q = p;
  q.x = abs (q.x);
  q -= vec3 (0.5, 0.3, 0.6);
  q.yz = Rot2Cs (q.yz, cs);
  q.xy = Rot2Cs (q.xy, vec2 (cs.x, - cs.y));
  q.xz = Rot2Cs (q.xz, vec2 (cs.x, - cs.y));
  h = 0.07;
  r2 = vec2 (0.3, 0.5 + 0.08 * clamp (q.z, -2., 2.));
  d = PrEllips2Df (q.yz, r2);
  d = min (max (d, abs (q.x) - h), length (vec2 (d, q.x)) - h);
  dMinO = SmoothMin (d, dMinO, 0.05);
  d = PrEllips2Df (q.yz, wf * r2);
  d = min (max (d, abs (q.x) - wf * h), length (vec2 (d, q.x)) - wf * h);
  dMinI = SmoothMin (d, dMinI, 0.05);
  r3 = vec3 (0.4, 0.35, 0.5);
  q = p - vec3 (0., 0.75, -0.4);
  dMinO = SmoothMin (PrEllipsDf (q, r3), dMinO, 0.1);
  dMinI = SmoothMin (PrEllipsDf (q, wf * r3), dMinI, 0.1);
  q = p - vec3 (0., 0.65, -0.9);
  q.zy = Rot2Cs (q.zy, vec2 (cs.x, - cs.y));
  h = 0.15;
  r2 = vec2 (0.15, 0.04) * (1. - 0.1 * min (2., max (0., 1. - q.z / h)));
  dMinO = SmoothMin (max (PrEllCylDf (q, r2, h),
     - PrEllCylDf (q - vec3 (0., 0., 0.9 * h), r2 - 0.02, 2. * h)), dMinO, 0.01);
  ws = 0.02;
  q = abs (mod (p + ws, 2. * ws) - ws) - 0.15 * ws;
  dMin = 0.9 * max (max (dMinO, - dMinI), min (min (q.x, q.z), q.y));
  idObj = 1;
  if (! doSh) {
    d = PrSphDf (p - lbPos, 0.04);
    if (d < dMin) { dMin = d;  idObj = 2; }
  }
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 100; j ++) {
    d = ObjDf (ro + dHit * rd);
    dHit += d;
    if (d < 0.0005 || dHit > dstFar) break;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  const vec3 e = vec3 (0.0001, -0.0001, 0.);
  vec4 v = vec4 (ObjDf (p + e.xxx), ObjDf (p + e.xyy),
     ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (vec3 (v.x - v.y - v.z - v.w) + 2. * v.yzw);
}

float ObjSShadow (vec3 ro, vec3 rd, float lbDist)
{
  float sh, d, h;
  sh = 1.;
  d = 0.002;
  for (int j = 0; j < 100; j ++) {
    h = ObjDf (ro + rd * d);
    sh = min (sh, smoothstep (0., 0.1 * d, h));
    d += 0.01 * (1. + d);
    if (sh < 0.02 || d > lbDist) break;
  }
  return 0.3 + 0.7 * sh;
}

float GlowCol (vec3 ro, vec3 rd, float dstHit)
{
  vec3 ld;
  float d, wGlow;
  wGlow = 0.;
  ld = lbPos - ro;
  d = length (ld);
  ld /= d;
  if (d < dstHit) wGlow += pow (max (dot (rd, ld), 0.), 1024.);
  return clamp (0.7 * wGlow, 0., 1.);
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec3 roo, vn, col, lbDir;
  float dstObj, sh, att, lbDist;
  dstFar = 20.;
  doSh = false;
  roo = ro;
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    ro += rd * dstObj;
    lbDir = normalize (lbPos - ro);
    vn = ObjNf (ro);
    if (idObj == 1) {
      doSh = true;
      sh = ObjSShadow (ro, ltDir, dstFar);
      col = vec3 (0.2, 0.5, 0.8);
      col = 0.1 * col + sh * (0.6 * col * max (0., dot (vn, ltDir)) +
         0.5  * vec3 (1.) * pow (max (dot (normalize (ltDir - rd), vn), 0.), 256.));
    } else {
      col = max (0.6 + 0.4 * dot (rd, - vn), 0.) * vec3 (1., 1., 0.5);
    }
  } else {
    col = vec3 (0.1, 0.15, 0.1) * (0.5 + 1.5 * rd.y);
    if (rd.y < 0.) {
      ro -= ((ro.y + 0.51) / rd.y) * rd;
      lbDir = lbPos - ro;
      lbDist = length (lbDir);
      att = 1. / (1. + dot (lbDir, lbDir));
      lbDir /= lbDist;
      doSh = true;
      col = mix (col, att * vec3 (1., 1., 0.5) * ObjSShadow (ro, lbDir, lbDist),
         smoothstep (0.1, 0.2, att));
    }
  }
  col = mix (col, vec3 (0.7, 0.7, 0.4), GlowCol (roo, rd, dstObj));
  col = pow (clamp (col, 0., 1.), vec3 (0.9));
  return col;
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd;
  vec2 canvas, uv, ori, ca, sa;
  float az, el;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  az = 0.;
  el = -0.15 * pi;
  if (mPtr.z > 0.) {
    az += 2.5 * pi * mPtr.x;
    el += 0.5 * pi * mPtr.y;
    el = clamp (el, -0.27 * pi, -0.01 * pi);
  } else {
    az -= 3.5 * pi * sin (0.014 * pi * tCur);
    el -= 0.12 * pi * sin (0.2 * pi * tCur);
  }
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  vuMat = mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
          mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
  rd = vuMat * normalize (vec3 (uv, 4.5));
  ro = vuMat * vec3 (0., 0., -5.);
  ro.y += 0.25;
  ltDir = vuMat * normalize (vec3 (1., 1., -1.));
  lbPos = vec3 (0., 0.1, 0.);
  fragColor = vec4 (ShowScene (ro, rd), 1);
}

float PrSphDf (vec3 p, float s)
{
  return length (p) - s;
}

float PrEllipsDf (vec3 p, vec3 r)
{
  return (length (p / r) - 1.) * min (r.x, min (r.y, r.z));
}

float PrEllips2Df (vec2 p, vec2 r)
{
  return (length (p / r) - 1.) * min (r.x, r.y);
}

float PrEllCylDf (vec3 p, vec2 r, float h)
{
  return max ((length (p.xy / r) - 1.) * min (r.x, r.y), abs (p.z) - h);
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b, a, h) - r * h * (1. - h);
}

vec2 Rot2Cs (vec2 q, vec2 cs)
{
  return vec2 (dot (q, vec2 (cs.x, - cs.y)), dot (q.yx, cs));
}

