float fogAngleFixer(vec3 angle){
	return clamp(-angle.y*2.0+0.1,0.0,1.0);   
}
vec3 getBackground(vec3 angle){
    float heigh = clamp(angle.y*2.0+0.2,0.0,1.0);
    return vec3(0.3*(1.0-heigh),0.5*heigh*0.5+0.5,1.0*heigh/2.0+0.5);
}
vec3 getColor(vec3 pos, vec3 norm){
    float col1=0.0,col2 = 0.0, col3 = 0.0;
    col1 = clamp((pos.y-3.6) * 10.0, 0.0, 1.0);
    col2 = (clamp(abs((pos.y-3.6) * 10.0), 0.0, 1.0) * -1.0) + 1.0;
    col3 = 1.0 - col1 - col2;
    return
        col1 * vec3(1.0,1.0,1.0) +
        col2 * vec3(0.2,0.9,0.3) +
        col3 * vec3(0.7,0.5,0.1);
}
