vec3 vpHit, cId, qHit, ltDir;
const vec3 jRep = vec3 (6., 30., 6.);
float dstFar, tCur, qRnd;
int idObj;
const float pi = 3.14159;

float JFishDf (vec3 p)
{
  vec3 q;
  float dMin, d, dc, hr, a, r;
  p -= (Hashv3f (qRnd) - 0.5) * jRep * vec3 (0.5, 0.7, 0.5);
  hr = -1.2 + 3. * SmoothBump (0.2, 0.7, 0.2, mod (0.2 * tCur + qRnd, 1.));
  p.y -= 2. + 0.5 * hr;
  p.xz *= 1. + 0.2 * hr;
  dc = SmoothMax (PrSphAnDf (p, 1., 0.03), -0.2 - p.y, 0.1);
  dMin = dc;
  idObj = 1;
  p.xz += Rot2D (vec2 (0.3 * sin (p.y + 2. * tCur + 2. * pi * qRnd) *
     (1. - smoothstep (-2., 0.5, p.y)), 0.), 2. * pi * qRnd);
  qHit = p;
  r = length (p.xz);
  a = (r > 0.) ? atan (p.z, - p.x) / (2. * pi) : 0.;
  q = p;
  q.xz = Rot2D (q.xz, 2. * pi * floor (7. * a + 0.5) / 7.);
  q.xy += vec2 (0.3, 3.2);
  d = PrCylDf (q.xzy, (0.045 + 0.01 * q.y) * (1. +
     0.3 * (sin (24. * q.y) + sin (40. * q.y))), 4.);
  if (d < dMin) idObj = 2;
  dMin = min (dMin, SmoothMin (dc, d, 0.1));
  q = p;
  q.xz = Rot2D (q.xz, 2. * pi * floor (25. * a + 0.5) / 25.);
  q.xy += vec2 (0.84, 2.);
  d = PrCylDf (q.xzy, (0.035 + 0.02 * q.y) * (1. + 0.1 * sin (100. * q.y) *
     (1. - smoothstep (-1., 0., p.y))), 2.);
  if (d < dMin) idObj = 3;
  dMin = min (dMin, SmoothMin (dc, d, 0.07));
  return 0.7 * dMin;
}

float JFishRay (vec3 ro, vec3 rd)
{
  vec3 p, h, rdi;
  float dHit, d;
  ro.y -= tCur;
  dHit = 0.;
  rdi = 1. / (rd + 0.0001);
  for (int j = 0; j < 250; j ++) {
    p = ro + rd * dHit;
    cId = floor (p / jRep);
    h = (jRep * (cId + step (0., rd)) - p) * rdi;
    p -= jRep * (cId + 0.5);
    qRnd = Hashfv3 (cId);
    d = (qRnd < 0.7 && length (cId.xz) > 2.) ? JFishDf (p) : dstFar;
    d = min (d, abs (min (min (h.x, h.y), h.z)) + 0.1);
    dHit += d;
    if (d < 0.001 || dHit > dstFar) break;
  }
  vpHit = p;
  return dHit;
}

vec3 JFishNf (vec3 p)
{
  vec4 v;
  const vec3 e = vec3 (0.0001, -0.0001, 0.);
  v = vec4 (JFishDf (p + e.xxx), JFishDf (p + e.xyy),
     JFishDf (p + e.yxy), JFishDf (p + e.yyx));
  return normalize (vec3 (v.x - v.y - v.z - v.w) + 2. * v.yzw);
}

float ObjDf (vec3 p)
{
  p.xz = Rot2D (p.xz, 2. * pi * (floor (16. *
     ((length (p.xz) > 0.) ? atan (p.z, - p.x) / (2. * pi) : 0.)) + 0.5) / 16.);
  p.z = abs (p.z);
  return max (PrCapsAnDf (p.xzy, 1., 0.005, 0.3),
     SmoothMin (abs (mod (p.y, 0.2) - 0.1) - 0.003,
     dot (p.xz, vec2 (sin (0.02 * 2. * pi / 32.), cos (0.02 * 2. * pi / 32.))), 0.003));
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 100; j ++) {
    d = ObjDf (ro + dHit * rd);
    dHit += d;
    if (d < 0.0002 || dHit > dstFar) break;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  const vec3 e = vec3 (0.00005, -0.00005, 0.);
  v = vec4 (ObjDf (p + e.xxx), ObjDf (p + e.xyy),
     ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (vec3 (v.x - v.y - v.z - v.w) + 2. * v.yzw);
}

vec3 BgCol (vec3 rd)
{
  float t, gd, b;
  t = 4. * tCur;
  b = dot (vec2 (atan (rd.x, rd.z), 0.5 * pi - acos (rd.y)), vec2 (2., sin (rd.x)));
  gd = clamp (sin (5. * b + t), 0., 1.) * clamp (sin (3.5 * b - t), 0., 1.) +
     clamp (sin (21. * b - t), 0., 1.) * clamp (sin (17. * b + t), 0., 1.);
  return vec3 (0.25, 0.4, 1.) * (0.24 + 0.44 * (rd.y + 1.) * (rd.y + 1.)) *
     (1. + gd * 0.05);
}

float TurbLt (vec3 p, vec3 n, float t)
{
  vec2 q, qq, a1, a2;
  float c, tt;
  q = vec2 (dot (p.yzx, n), dot (p.zxy, n));
  q = 2. * pi * mod (q, 1.) - 256.;
  t += 11.;
  c = 0.;
  qq = q;
  for (float k = 1.; k <= 7.; k ++) {
    tt = t * (1. + 1. / k);
    a1 = tt - qq;
    a2 = tt + qq;
    qq = q + tt + vec2 (cos (a1.x) + sin (a2.y), sin (a1.y) + cos (a2.x));
    c += 1. / length (q / vec2 (sin (qq.x), cos (qq.y)));
  }
  return clamp (pow (abs (1.25 - abs (0.167 + 40. * c)), 8.), 0., 1.);
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec3 col, vn, bg;
  float dstObj, dstCage, a;
  dstObj = JFishRay (ro, rd);
  dstCage = ObjRay (ro, rd);
  if (dstCage < min (dstObj, dstFar)) {
    dstObj = dstCage;
    idObj = 4;
  }
  bg = BgCol (rd);
  if (dstObj < dstFar) {
    if (idObj != 4) {
      vn = JFishNf (vpHit);
      a = atan (qHit.x, qHit.z);
      col = HsvToRgb (vec3 (mod (qRnd + sin (0.2 * tCur), 1.), 0.6, 0.8));
      if (idObj == 1) col = mix ((bg + 0.1) * (2. + vn.y), 1.2 * col,
         SmoothBump (0.4, 0.6, 0.05, mod (4. * qHit.y +
         sign (qRnd - 0.5) * a / pi + 0.1 * sin (8. * a) +
         2. * tCur + qRnd, 1.)) * SmoothBump (-0.1, 0.9, 0.1, qHit.y));
      else if (idObj == 2) col = mix (0.6 * col + 0.1, vec3 (1.),
         SmoothBump (0.2, 0.7, 0.1, 0.5 + 0.5 * sin (qRnd +
         3. * (1. + qRnd) * qHit.y + 4. * (1. + 0.5 * qRnd) * tCur)) *
         SmoothBump (0.07, 0.93, 0.03, mod (0.3 * tCur + 3. * qRnd, 1.))) *
         (1. - 0.3 * smoothstep (-2., 0., qHit.y));
      else if (idObj == 3) col = mix (col, (bg + 0.1) * (2. + vn.y),
         smoothstep (-0.5, 0., qHit.y));
      col = mix (mix (bg, col, max (0., - dot (vn, rd))), bg,
         smoothstep (0.6, 1., min (dstObj / dstFar, 1.)));
      col += 0.3 * TurbLt (0.05 * vpHit, abs (vn), 0.3 * tCur) *
         smoothstep (-0.3, -0.1, vn.y);
    } else {
      ro += dstObj * rd;
      vn = VaryNf (500. * ro, ObjNf (ro), 0.5);
      col = vec3 (0.6, 0.7, 0.6) * (0.2 + 0.6 * max (dot (vn, ltDir), 0.)) +
         0.2 * pow (max (dot (normalize (ltDir - rd), vn), 0.), 64.);
    }
  } else col = bg;
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd;
  vec2 canvas, uv, ori, ca, sa;
  float az, el, zmFac;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  tCur += 20.;
  if (mPtr.z > 0.) {
    az = 2. * pi * mPtr.x;
    el = clamp (1. * pi * mPtr.y, -0.3 * pi, 0.3 * pi);
  } else {
    az = pi * (-1. + 2. * SmoothBump (0.25, 0.75, 0.25, mod (0.003 * tCur, 1.)));
    el = 0.2 * pi * (-1. + 2. * SmoothBump (0.25, 0.75, 0.25, mod (0.019 * tCur, 1.)));
  }
  ro = vec3 (0., 0., -0.7);
  zmFac = 4.2;
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  vuMat = mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
          mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
  rd = vuMat * normalize (vec3 (uv, zmFac));
  ro = vuMat * ro;
  ltDir = vuMat * normalize (vec3 (0., 2., -1.));
  dstFar = 120.;
  fragColor = vec4 (ShowScene (ro, rd), 1.);
}

const vec4 cHashA4 = vec4 (0., 1., 57., 58.);
const vec3 cHashA3 = vec3 (1., 57., 113.);
const float cHashM = 43758.54;

vec3 Hashv3f (float p)
{
  return fract (sin (vec3 (p, p + 1., p + 2.)) *
     vec3 (cHashM, cHashM * 0.43, cHashM * 0.37));
}

float Hashfv3 (vec3 p)
{
  return fract (sin (dot (p, cHashA3)) * cHashM);
}

vec4 Hashv4f (float p)
{
  return fract (sin (p + cHashA4) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec4 t;
  vec2 ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = Hashv4f (dot (ip, cHashA3.xy));
  return mix (mix (t.x, t.y, fp.x), mix (t.z, t.w, fp.x), fp.y);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  const vec3 e = vec3 (0.1, 0., 0.);
  g = vec3 (Fbmn (p + e.xyy, n), Fbmn (p + e.yxy, n), Fbmn (p + e.yyx, n)) -
     Fbmn (p, n);
  return normalize (n + f * (g - n * dot (n, g)));
}

float SmoothMin (float a, float b, float r)
{
  float h;
  h = clamp (0.5 + 0.5 * (b - a) / r, 0., 1.);
  return mix (b, a, h) - r * h * (1. - h);
}

float SmoothMax (float a, float b, float r)
{
  return - SmoothMin (- a, - b, r);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

vec2 Rot2D (vec2 q, float a)
{
  return q * cos (a) + q.yx * sin (a) * vec2 (-1., 1.);
}

float PrSphAnDf (vec3 p, float r, float w)
{
  return abs (length (p) - r) - w;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrCapsAnDf (vec3 p, float r, float w, float h)
{
  vec3 b;
  float d;
  p.z = abs (p.z);
  b = vec3 (0.);
  b.z = min (p.z, h + w);
  d = length (p - b);
  b.z = min (p.z, h - w);
  return max (d - r, - length (p - b) + r) - w;
}

vec3 HsvToRgb (vec3 c)
{
  vec3 p;
  p = abs (fract (c.xxx + vec3 (1., 2./3., 1./3.)) * 6. - 3.);
  return c.z * mix (vec3 (1.), clamp (p - 1., 0., 1.), c.y);
}

