float intersect( in vec3 ro, in vec3 rd, in float tmin, in float tmax )
{
    vec3 pos;
    float t = tmin;
		for( int i=0; i<256; i++ )
	{
         pos = ro + t*rd;
		float h = pos.y - scene( pos.xz );
		if( h<(0.002*t) || t>tmax ) break;
		t += 0.5*h;
	}

	return t;
}


vec3 calcNormal( in vec3 pos, float t )
{
    vec2  eps = vec2( 0.002*t, 0.0 );
    return normalize( vec3( scene(pos.xz-eps.xy) - scene(pos.xz+eps.xy),
                            2.0*eps.x,
                            scene(pos.xz-eps.yx) - scene(pos.xz+eps.yx) ) );
}

vec3 calcNormal( in vec3 pos)
{
    vec2  eps = vec2( 0.0001 );
    return normalize( vec3( scene(pos.xz-eps.xy) - scene(pos.xz+eps.xy),
                            2.0*eps.x,
                            scene(pos.xz-eps.yx) - scene(pos.xz+eps.yx) ) );
}

vec3 phongContribForLight(vec3 k_d, vec3 k_s, float alpha, vec3 p, vec3 eye,
                          vec3 lightPos, vec3 lightIntensity) {
    vec3 N = calcNormal(p);
    vec3 L = normalize(lightPos - p);
    vec3 V = normalize(eye - p);
    vec3 R = normalize(reflect(-L, N));

    float dotLN = dot(L, N);
    float dotRV = dot(R, V);

    if (dotLN < 0.0) {
        // Light not visible from this point on the surface
        return vec3(0.0, 0.0, 0.0);
    } 

    if (dotRV < 0.0) {
        // Light reflection in opposite direction as viewer, apply only diffuse
        // component
        return lightIntensity * (k_d * dotLN);
    }
    return lightIntensity * (k_d * dotLN + k_s * pow(dotRV, alpha));
}

/**
 * Lighting via Phong illumination.
 * 
 * The vec3 returned is the RGB color of that point after lighting is applied.
 * k_a: Ambient color
 * k_d: Diffuse color
 * k_s: Specular color
 * alpha: Shininess coefficient
 * p: position of point being lit
 * eye: the position of the camera
 *
 * See https://en.wikipedia.org/wiki/Phong_reflection_model#Description
 */
vec3 phongIllumination(vec3 k_a, vec3 k_d, vec3 k_s, float alpha, vec3 p, vec3 eye, vec3 nor, vec3 ro) {

    vec3 ambientLight = 0.5 * k_d;
    vec3 color = ambientLight * k_a;
   // vec3 light1Pos = vec3(10.0, 1.0, 10.0);
    // hacemos que se mueva para que quede mas interesante
    vec3 light1Pos = vec3(ro.x+10.0 * sin(iTime),
                          ro.y+2.0,
                          ro.z+10.0 * cos(iTime));
    vec3 light1Intensity = vec3(0.4, 0.4, 0.4);


    vec3 pointToLight = light1Pos - p; // Vector foco-vÃ©rtice
	float distanceToLight = length(pointToLight);
	vec3 L = normalize(pointToLight);
	vec3 power = k_d;// * atenuate(distanceToLight,omniLights[i].fade);// Fade de distancia
	vec3 diff = power * k_d * dot(nor, L);
	color+=clamp(diff,0.0,1.0);

    // Especular
    vec3 R = reflect(-L,nor);
    vec3 V=pointToLight;
    float Sfactor = max(dot(R,V),0.0001);

    Sfactor=pow(Sfactor,length(nor));
    vec3 spec = power * k_s * Sfactor;
    color+=clamp(spec,0.0,1.0);
    return color;

}


