float RADIUS = 0.4; // normalized window
float AA     = 2.0;  // pixels

vec4 SURFACE    = vec4(0.0, 0.75, 0.0, 1.0);
vec4 SPACE      = vec4(0.0, 0.0 , 0.0, 0.0);
vec4 ATMOSPHERE = vec4(0.0, 0.75, 1.0, 0.5);

float DARK  = 0.5;  // Minnaert parameter

float TERM  = 0.05; // width of terminator (as percentage of planet radius)

float HAZE = 0.25; // radius of atmospheric haze (as percentage of planet radius)


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float tscale = 5.0;
    float ct     = cos(iTime / tscale);
    float st     = sin(iTime / tscale);
	vec3  LIGHT = normalize(vec3(ct, st/5.0, st));

    float rez    = min(iResolution.x, iResolution.y);
    vec2  center = iResolution.xy / 2.0;
    vec2  v      = (fragCoord.xy - center) / rez;
    float l      = length(v);
    float diff   = l - RADIUS;

    float pixels = AA / rez;
    float aa     = 1.0 - smoothstep(-pixels, 0.0, diff);

    vec3 n = vec3(v / RADIUS, 0.0);
    vec4 s;
    if (length(n) < 1.0) {
    	n = vec3(n.xy, sqrt(1.0 - n.x*n.x - n.y*n.y));

		float lambert  = dot(n, LIGHT);
		float minnaert = pow(lambert, DARK);
		float toon     = smoothstep(-TERM, TERM, minnaert - 0.5);

	    s = aa*mix(0.10*SURFACE, SURFACE, toon);
    }
    else {
        s = SPACE;
    }

    vec3 p = vec3(v / RADIUS, 0.0);
    vec4 a;
    if (length(p) < 1.0)
    	p = vec3(p.xy, sqrt(1.0 - p.x*p.x - p.y*p.y));
    else
    	p = normalize(p);

    p = p - vec3(0.0, 0.0, RADIUS + HAZE*RADIUS);

    float haze      = smoothstep(0.0, HAZE*RADIUS, diff);
    float backlight = dot(p, LIGHT);

	a = clamp(backlight, 0.0, 1.0)*mix(ATMOSPHERE.w*ATMOSPHERE, SPACE, haze);

    fragColor =  s+a;
}

