//else we have a box

vec2 point = vec2(0.5f, 0.7f);

/*

Interpolating outside of a distance field, basic 2d setup.

*/

float sdFunc(vec2 fragPos)
{

    #ifdef SD_POINT

    return length(point * iResolution.xy - fragPos);

    #endif

    float sdf_angle = 1. + iTime / 10.;

    fragPos -= point * iResolution.xy;
    fragPos *= mat2(cos(sdf_angle),-sin(sdf_angle),sin(sdf_angle),cos(sdf_angle)); 
    vec2 d = abs(fragPos) - vec2(110.,20.);
 	return min(max(d.x,d.y),0.0) + length(max(d,0.0));

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float sd = sdFunc(fragCoord);

    float thrshld = 0.4f * iResolution.y;

    float slider = (iMouse.z > 0. ? iMouse.x : 0.5 * iResolution.x) ;

    if((fragCoord.y < thrshld && fragCoord.x > slider && fragCoord.x < slider+1.) || (fragCoord.y > thrshld && fragCoord.y < thrshld + 1.))
    {
         fragColor = vec4(1.,0.,0.,1.);
           return;
    }

    if(fragCoord.x < slider)
    {
    	if(fragCoord.y < thrshld) 
    	{
        	sd = abs(fragCoord.y - thrshld) + sdFunc(vec2(fragCoord.x, thrshld));
    	}
    }
    else
    {
        if(fragCoord.y < thrshld) 
    	{

            //let's do with circles
            vec2 vec_to_limit = vec2(0, thrshld - fragCoord.y);

            float d = 1.;
            vec2 p0 = vec2(fragCoord.x, thrshld);
            vec2 p1 = vec2(fragCoord.x, thrshld + d);
            float r0 = sdFunc(p0);
            float r1 = sdFunc(p1);

            float h = 0.5 + (r0*r0 - r1*r1) / (2. * d*d);

            float r_i = sqrt(abs(r0*r0 - h*h*d*d));

            vec2 p2 = p0 + (p1-p0)*h;

            vec2 p3 = p2 + vec2(p1.y-p0.y, p1.x-p0.x) * r_i;

            sd = length(fragCoord - p3);
    	}
    }

    fragColor = vec4( fract(sd / max(iResolution.x, iResolution.y) * 20.) );

    return;
}

