struct Info { float dist; vec4 color; };
Info info;
mat2 rz2 (float a) { float c=cos(a),s=sin(a); return mat2(c,s,-s,c); }
float lfo (float o, float s) { return .5+.5*sin(t*s+o); }
float sphere (vec3 p, float r) { return length(p)-r; }
float cyl (vec2 p, float r) { return length(p)-r; }
float cyli (vec3 p, float r, float h) { return max(length(p.xz)-r, abs(p.y)-h); }
float iso (vec3 p, float r) { return dot(p,normalize(sign(p)))-r; }
float cube (vec3 p, vec3 r) { return length(max(abs(p)-r,0.)); }
float scol (float a, float b, float r) { return clamp(.5+.5*(b-a)/r,0.,1.); }
float smin (float a, float b, float r) { float h = scol(a,b,r); return mix(b,a,h)-r*h*(1.-h); }
vec3 hsv2rgb(vec3 c)
{
	vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
	vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
	return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}
vec3 moda (vec2 p, float c) {
    float ca = TAU/c;
    float a = atan(p.y,p.x)+ca*.5;
    float ci = floor(a/ca);
    a = mod(a,ca)-ca*.5;
    return vec3(vec2(cos(a),sin(a))*length(p), ci);
}

float leaf (vec3 p, float radius, float cycle, float index, vec2 scale) {
    p.xz *= rz2(index*.5);
    p.xz *= scale+lfo(index*5.,1.);
    p.y -= sin(p.z*1.5)*.3;
    p.z -= radius;
    p.y -= sin(abs(p.x)*3.)*.1;
    p.y = mod(p.y,cycle)-cycle*.5;
    return cyli(p, radius, 0.01);
}

vec3 torsade (vec3 p, float offset, float scale) {
    p.xz *= rz2(t+offset);
    float a = p.y * scale;
    p.xz -= vec2(cos(a),sin(a));
    return p;
}

float map (vec3 p) {

    float wave1 = lfo(p.y*.2,1.);
    vec3 pball = p;
    vec3 pk = p;

    p.yz *= rz2(sin(t*.5)*.5);
    p.xz *= rz2(t*.3);

    vec3 mosaic = moda(p.xz, 7.);
    mosaic.z *= 5.;
    float torsadeScale = (0.1 + lfo(mosaic.z,0.)) * wave1;
    p.xz = mosaic.xy;
    p.x -= 8.- 4.*wave1;
    //float cyclem = 5.;
    //p.x = mod(p.x-t, cyclem)-cyclem*.5;

    // leaves
    float radius = 1.*wave1;
    float cycle = 1.;
    float index = 0.;
    vec2 scale = vec2(1.2,.75);
    float offset = mosaic.z;
    index = floor(p.y/cycle) + mosaic.z;
   	vec3 pleaves = torsade(p,offset, torsadeScale)+vec3(0,.5,0);
    float leaf1 = leaf(pleaves, radius, cycle, index, scale);
    float leaf2 = leaf(pleaves, radius, cycle, index+4., scale);
    float leaf3 = leaf(pleaves, radius, cycle, index+8., scale);
    float leaves = min(leaf1, min(leaf2, leaf3));

    // rod
    vec3 p2 = torsade(p,offset, torsadeScale);
    float rod = cyl(p2.xz, .05+.05*lfo(p.y*4.,0.));

    // ball
    float cycleb = 1.9;
    float cycled = 1.55;
    float speedb = 1.;
    float indexb = floor((pball.y+t*speedb)/cycleb);
    pball.y = mod(pball.y+t*speedb, cycleb)-cycleb*.5;
    pball.xz *= rz2(pball.y*.6);
    vec3 pdots = mod(pball,cycled)-cycled*.5;
    float ball = cyl(pball.xz, .5+5.*(1.-wave1));
    ball = mix(max(ball, -sphere(pdots, 1.)), ball, smoothstep(.0,0.5,wave1));
    ball = max(ball, -cyl(pball.xz, 5.*(1.-wave1)));

    // red ball
    float redb = cyl(pball.xz, 4.*(1.-wave1));

    // iso
    vec3 mosab = moda(pball.xz, 15.);
    pball.xz = mosab.xy;
    pball.x -= 4.*(1.-wave1);
    pball.xy *= rz2(t*.3+indexb);
    pball.yz *= rz2(t*.6+indexb*5.);
    pball.xz *= rz2(t*.9+indexb*10.);
    pball.y = mod(pball.y+t*5., cycleb)-cycleb*.5;
    float iso1 = iso(pball, 0.75*(1.-wave1));
    iso1 = max(-iso1, redb);

    // colors
    vec4 red = vec4(hsv2rgb(vec3(.0,.9,1.)),1);
    vec4 green = vec4(hsv2rgb(vec3(.25,.7,.8)),1);
    vec4 green2 = vec4(hsv2rgb(vec3(.25,.5,.3)),1);
    vec4 blue = vec4(hsv2rgb(vec3(.5,.3,.9)),1);
    vec4 orange = vec4(hsv2rgb(vec3(.12,.5,.8)),1);
    info.color = mix(green2, green, scol(leaves, rod, .1));

    float scene = smin(leaves, rod, .1);
    info.color = mix(info.color, orange, scol(ball, scene, .1));

    scene = min(scene, ball);
    info.color = mix(info.color, red, scol(iso1, scene, .1));
    scene = min(scene, iso1);

    return scene;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = (fragCoord.xy-.5*iResolution.xy)/iResolution.y;
    vec3 ro = vec3(uv,-15.-5.*lfo(0.,0.3))+vec3(0,.5,0), rd = vec3(uv,1), mp = ro;
    int ri = 0;
    for (int i=0;i<50;++i) {
        ri = i;
        float md = map(mp);
        if (md < 0.01) {
            break;
        }
        mp += rd*md*.5;
    }
    float l = length(mp);

    float r = float(ri)/50.;
	fragColor = info.color;
    fragColor *= 1.-r;
}

const int NUM_STEPS = 8;
const float PI	 	= 3.1415;
const float EPSILON	= 1e-3;
#define EPSILON_NRM (0.1 / iResolution.x)

// sea
const int ITER_GEOMETRY = 3;
const int ITER_FRAGMENT = 5;
const float SEA_HEIGHT = 1.0;
const float SEA_CHOPPY = 4.0;
const float SEA_SPEED = 0.8;
const float SEA_FREQ = 0.16;
const vec3 SEA_BASE = vec3(0.1,0.19,0.22);
const vec3 SEA_WATER_COLOR = vec3(0.8,0.9,0.6);
#define SEA_TIME (1.0 + iTime * SEA_SPEED)
const mat2 octave_m = mat2(1.6,1.2,-1.2,1.6);

mat3 fromEuler(vec3 ang) {
	vec2 a1 = vec2(sin(ang.x),cos(ang.x));
    vec2 a2 = vec2(sin(ang.y),cos(ang.y));
    vec2 a3 = vec2(sin(ang.z),cos(ang.z));
    mat3 m;
    m[0] = vec3(a1.y*a3.y+a1.x*a2.x*a3.x,a1.y*a2.x*a3.x+a3.y*a1.x,-a2.y*a3.x);
	m[1] = vec3(-a2.y*a1.x,a1.y*a2.y,a2.x);
	m[2] = vec3(a3.y*a1.x*a2.x+a1.y*a3.x,a1.x*a3.x-a1.y*a3.y*a2.x,a2.y*a3.y);
	return m;
}

float hash( vec2 p ) {
	float h = dot(p,vec2(127.1,311.7));	
    return fract(sin(h)*43758.5453123);
}

float noise( in vec2 p ) {
    vec2 i = floor( p );
    vec2 f = fract( p );	
	vec2 u = f*f*(3.0-2.0*f);
    return -1.0+2.0*mix( mix( hash( i + vec2(0.0,0.0) ), 
                     hash( i + vec2(1.0,0.0) ), u.x),
                mix( hash( i + vec2(0.0,1.0) ), 
                     hash( i + vec2(1.0,1.0) ), u.x), u.y);
}

// lighting
float diffuse(vec3 n,vec3 l,float p) {
    return pow(dot(n,l) * 0.4 + 0.6,p);
}
float specular(vec3 n,vec3 l,vec3 e,float s) {    
    float nrm = (s + 8.0) / (3.1415 * 8.0);
    return pow(max(dot(reflect(e,n),l),0.0),s) * nrm;
}

// sky
vec3 getSkyColor(vec3 e) {
    e.y = max(e.y,0.0);
    return vec3(pow(1.0-e.y,2.0), 1.0-e.y, 0.6+(1.0-e.y)*0.4);
}

// sea
float sea_octave(vec2 uv, float choppy) {
    uv += noise(uv);        
    vec2 wv = 1.0-abs(sin(uv));
    vec2 swv = abs(cos(uv));    
    wv = mix(wv,swv,wv);
    return pow(1.0-pow(wv.x * wv.y,0.65),choppy);
}

float map(vec3 p) {
    float freq = SEA_FREQ;
    float amp = SEA_HEIGHT;
    float choppy = SEA_CHOPPY;
    vec2 uv = p.xz; uv.x *= 0.75;

    float d, h = 0.0;    
    for(int i = 0; i < ITER_GEOMETRY; i++) {        
    	d = sea_octave((uv+SEA_TIME)*freq,choppy);
    	d += sea_octave((uv-SEA_TIME)*freq,choppy);
        h += d * amp;        
    	uv *= octave_m; freq *= 1.9; amp *= 0.22;
        choppy = mix(choppy,1.0,0.2);
    }
    return p.y - h;
}

float map_detailed(vec3 p) {
    float freq = SEA_FREQ;
    float amp = SEA_HEIGHT;
    float choppy = SEA_CHOPPY;
    vec2 uv = p.xz; uv.x *= 0.75;

    float d, h = 0.0;    
    for(int i = 0; i < ITER_FRAGMENT; i++) {        
    	d = sea_octave((uv+SEA_TIME)*freq,choppy);
    	d += sea_octave((uv-SEA_TIME)*freq,choppy);
        h += d * amp;        
    	uv *= octave_m; freq *= 1.9; amp *= 0.22;
        choppy = mix(choppy,1.0,0.2);
    }
    return p.y - h;
}

vec3 getSeaColor(vec3 p, vec3 n, vec3 l, vec3 eye, vec3 dist) {  
    float fresnel = clamp(1.0 - dot(n,-eye), 0.0, 1.0);
    fresnel = pow(fresnel,3.0) * 0.65;

    vec3 reflected = getSkyColor(reflect(eye,n));    
    vec3 refracted = SEA_BASE + diffuse(n,l,80.0) * SEA_WATER_COLOR * 0.12; 

    vec3 color = mix(refracted,reflected,fresnel);

    float atten = max(1.0 - dot(dist,dist) * 0.001, 0.0);
    color += SEA_WATER_COLOR * (p.y - SEA_HEIGHT) * 0.18 * atten;

    color += vec3(specular(n,l,eye,60.0));

    return color;
}

// tracing
vec3 getNormal(vec3 p, float eps) {
    vec3 n;
    n.y = map_detailed(p);    
    n.x = map_detailed(vec3(p.x+eps,p.y,p.z)) - n.y;
    n.z = map_detailed(vec3(p.x,p.y,p.z+eps)) - n.y;
    n.y = eps;
    return normalize(n);
}

float heightMapTracing(vec3 ori, vec3 dir, out vec3 p) {  
    float tm = 0.0;
    float tx = 1000.0;    
    float hx = map(ori + dir * tx);
    if(hx > 0.0) return tx;   
    float hm = map(ori + dir * tm);    
    float tmid = 0.0;
    for(int i = 0; i < NUM_STEPS; i++) {
        tmid = mix(tm,tx, hm/(hm-hx));                   
        p = ori + dir * tmid;                   
    	float hmid = map(p);
		if(hmid < 0.0) {
        	tx = tmid;
            hx = hmid;
        } else {
            tm = tmid;
            hm = hmid;
        }
    }
    return tmid;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    //uv += noise(uv); 
    float time = 0.0;
    vec3 ang = vec3(sin(time*3.0)*0.1,sin(time)*0.2+0.3,time);    
    vec3 ori = vec3(0.0,3.5,time*5.0);
    vec3 dir = normalize(vec3(uv.xy,-2.0)); dir.z += length(uv) * 0.15;
    dir = normalize(dir) * fromEuler(ang);

    // tracing
    vec3 p;
    heightMapTracing(ori,dir,p);
    //p = vec3(0.0, 0.0, 1.0);
    vec3 dist = p - ori;
    vec3 n = getNormal(p, dot(dist,dist) * EPSILON_NRM);
    vec3 light = normalize(vec3(0.0,1.0,0.8)); 

    vec3 color = mix(
        getSkyColor(dir),
        getSeaColor(p,n,light,dir,dist),
    	pow(smoothstep(0.0,-0.05,dir.y),0.3));

	fragColor = vec4(uv,0.5+0.5*sin(iTime),1.0);
    fragColor = vec4(uv, 1.0, 1.0);
    fragColor = vec4(pow(color,vec3(0.75)), 1.0);
}

mat2 rz2(float a){float c=cos(a),s=sin(a);return mat2(c,s,-s,c);}

struct M{float d;vec3 c;};
M m;

void map(vec3 p){
    p.xy*=rz2(iTime*.03);
    p.yz*=rz2(iTime*.05);
    p.xy*=rz2(iTime*.07);
    vec3 q=mod(p+2.5,5.)-2.5;
    vec3 f=floor((p-2.5)/5.);
    p=q;
    p.xy*=rz2(f.x+iTime);
    p.yz*=rz2(f.y+iTime);
    p.zx*=rz2(f.z+iTime);
    float d1=length(p)-1.;
    float a=atan(p.y,p.x);
    float d2=min(1.,abs(a)-(0.5+sin(iTime*10.)*.2))*length(p.xy);
    m.d=max(d1,d2);
    m.c=mix(vec3(1.,0.3,0.3),vec3(0.6,1.,0.6),smoothstep(.75,.85,length(p)));
    m.c=mix(m.c,vec3(0.,0.3,0.1),smoothstep(0.95,1.,length(p)));
    float mb=6.2831853/16.;
    float b=mod(atan(p.z,p.y),mb)-.5*mb;
    m.c=mix(vec3(0.),m.c,smoothstep(.18,.22,length(vec2(b*3.,length(p.xz)-0.2))));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv-=.5;
    uv.x*=iResolution.x/iResolution.y;
    vec3 ro=vec3(uv,-2.),rd=vec3(uv,1.),mp=ro;
    int i;
    for (i=0;i<50;++i){
        map(mp);
        m.d*=.5;
        if(abs(m.d)<.001)break;
        mp+=rd*m.d;
    }
    float ma=1.-float(i)/50.;
    vec3 c=m.c*ma;
    fragColor = vec4(c,1.0);
}

#define PSD pow(textureLod(iChannel0, vec2(.5), 0.).r, 1.)
void mainImage( out vec4 f, in vec2 g )
{
    f-=f;
    float x = 1., i=x, T=iTime, l;
	vec2 R = iResolution.xy;
    vec4 p = vec4((g+g-R)/R.y, .5, 0), d=p;
    p.y -= 5.; p.z -= T+T;
    p *= .5;
    for (; i > 0. && x > 1e-3; i-= .02)
        x = length(cos(p.wxw)-cos(p.yzw))-PSD*.5,
        f = i*i*vec4(l=distance(p, p-p), 1./l*l, abs(p.y), 1.),
        p -= d*x*.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord / iResolution.y, s = vec2(.0),c = vec2(.0);
    float sp = (sin(iTime*0.01)+1.0)*0.5;
    sp *= 0.05;
    sp += 0.0001;
    float tt = iTime * sp;

    tt += fract(sin(uv.x+1.0))*2.0;
    float e = 1.0+sin(uv.x*3.0)*2.6;
    float k =  cos(tt-e) + 7.0;
    vec4 ok = vec4( abs( cos(tt + sin(0.5 * tt + (uv.x+tt*0.001) * k) ) ));

    vec3 pixel = vec3(1.3);
    float t = iTime * 0.037,d = 0.0,r = 0.0;

    for(float i=0.0; i < 50.0; i+=1.0) {
       s = vec2(t + fract(sin(i*0.9)*37.0),t + fract(cos(i*2.5)*37.0));
       c.x = fract(cos(i*42.91 + s.x)*2.751) * 5.0 - 2.0;
       c.y = fract(sin(i*13.44 + s.y)*1.387) * 4.0 - 2.0;
        r = fract(sin((i*0.6))*5.0) * 0.67;
       c += ok.xy*vec2(0.3,-0.1);
       d = length(uv - c);
       pixel[int( mod(i,3.0))] -= smoothstep(d*0.05,d,r*r)*0.87;


       c.x = fract(cos(i*122.91 + s.x)*0.1751) * 5.0 - 2.0;
       c.y = fract(sin(i*73.44 + s.y)*0.387) * 4.0 - 2.0;
       r = fract(sin((i*0.4)+1.0)*0.2) * 0.00167;
       c += ok.xy*vec2(0.1,0.1);
       d = length(uv - c);
       pixel[int( mod((i+1.0),3.0))]-= smoothstep(d*0.000000005,d,r)*0.537;


    }

    fragColor = vec4(pixel.rgb, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.x;
    uv.x -= 0.5;
    uv.y -= iResolution.y/iResolution.x/2.0;
    uv *= 4.0;
    vec2 mv = iMouse.xy / iResolution.x;
    mv.x -= 0.5;
    mv.y -= iResolution.y/iResolution.x/2.0;
    mv *= 4.0;
  	float c = 1.0/distance(uv,mv);
    c += 1.0/length(uv);
    vec2 coord = vec2(sin(iTime),cos(iTime));
    c += 1.0/distance(uv,coord);
    coord = vec2(sin(-iTime),cos(-iTime));
    c += 1.0/distance(uv,coord);
    float n;
    if(mod(iTime/4.0,2.0) < 1.0) {
    	n = mod(iTime/2.0,2.0);
    } else {
    	n = (mod(2.0-iTime/2.0,2.0));
    }
    n -= 1.0;
    n *= 2.0;
    coord = vec2(n,0.0);
    c += 1.0/distance(uv,coord);
    c /= 20.0;
	fragColor = vec4(vec3(c),1.0);
}

#define TAU 6.28318531
#define rand(x) fract(sin(x)*1e4)
mat2 rz2(float a){float c=cos(a),s=sin(a);return mat2(c,s,-s,c);}

vec3 cam(vec3 p){
    p.yz*=rz2(sin(iTime*.5)*.2);
    p.xz*=rz2(iTime*.3);
    return p;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 v=uv*(1.-uv);
    uv-=.5;
    uv.x*=iResolution.x/iResolution.y;
    vec3 ro=cam(vec3(uv,-3.)),rd=cam(normalize(vec3(uv,1.)));
    float c=0.;
    for(int i=0;i<64;++i){
        float f=float(i);
        float a=rand(f)*TAU;
        float r=rand(f)+rand(f+2.);
        r=min(r,2.-r);
        float y=mod(rand(f+3.)*3.-iTime*.1, 3.)-1.5;
        vec3 p=vec3(r*cos(a),y,r*sin(a));
        vec3 rop=ro-p;
        float d=length(rop-dot(rop,rd)*rd);
        c+=.02*rand(f+5.)/d*min(1.,(1.5-abs(y))*10.);
    }
    vec3 clr=vec3(.2,.4,1.)*c;
    clr=pow(clr,vec3(1./2.2))*pow(v.x*v.y * 25.0, 0.25);
	fragColor = vec4(clr,0.);
}

//
// recreation of the "filip" fragment shader, as a raytracer
// written from scratch
//
// original shader idea and wonderful music by movAX13h:
// https://www.shadertoy.com/view/4ssfzn
//
//

vec2 rotate(vec2 p, float a)
{
    float co = cos(a), si = sin(a);
    return p * mat2(co, si, -si, co);
}

float hash(vec2 p)
{
    return fract(989253. * sin(205.124 * dot(p, vec2(.1, 33.))));
}

// the floor texture
float map0(vec2 uv)
{
    return hash(floor(uv));
}

float particles(out vec3 col, vec3 ro, vec3 rd, float time)
{
    float tm = 1e38;
    vec3 add = vec3(0.);

    for(int i = 0; i < 100; ++i)
    {
        mat3 mp = mat3(1., 0., 0., 0., 1., 0., 0., 0., 1.);

        float pt = -time * 2. + float(i) * 0.06 + sin(float(i)) * .3;

        vec3 pos = vec3(
            sin(pt * .2) * sin(pt * .4) * 4.,
            abs(sin(pt * 1.5) * sin(pt * .5)) + .01,
            cos(pt * .3) * 2.);

        float rt = pt * 2. + sin(float(i));

        float c0 = cos(rt), s0 = sin(rt);
        mp *= mat3(1., 0., 0., 0., c0, -s0, 0., s0, c0);
        float c1 = cos(rt), s1 = sin(rt);
        mp *= mat3(c1, 0., s1, 0., 1., 0., -s1, 0., c1);

        float t = -dot(ro - pos, mp[2]) / dot(rd, mp[2]);

        if(t > 0.)
        {
            vec3 vsun = normalize(vec3(.2, 1., .4));

            vec3 hit = (ro - pos) + rd * t;
            vec2 quv = vec2(dot(hit, mp[0]), dot(hit, mp[1]));

            vec3 ref = reflect(rd, mp[2]);

            float l = max(dot(mp[2], vsun), 0.) + pow(max(dot(ref, vsun), 0.), 64.);

            float size = fract(sin(float(i) * 124.123) * 12415.25323) * .1 + .001;

            float df = smoothstep(0., 0.01, max(abs(quv.x), abs(quv.y)) - size);

            col = mix(vec3(1.) * l * .5, col, df);

            //col += exp(length(quv) * -10.) * vec3(0.5, 0., 0.);
        }

        if(i == 0)
        {
            float to = 1. - dot(rd, normalize(pos - ro));
            add += vec3(1., 0.4, 0.3) * exp(to * -100.);
        }

        tm = min(tm, t);
    }
    col += add;
    return tm;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2. - 1.;
    uv.x *= iResolution.x / iResolution.y;


    float icst = floor(iTime / 10.);
    float cst = fract(iTime / 10.);

    float shut = smoothstep(1., .9, cst) * smoothstep(0., .1, cst);

    vec3 ro = vec3(0., .5 + sin(iTime * .7) * .3, -2.);
    vec3 rd = normalize(vec3(uv, 1.66));

    float pn = sin(icst) * iTime * 0.3;
    float tt = 0.;//step(.8, sin(icst));

    ro.xz = rotate(ro.xz, pn);
    rd.xz = rotate(rd.xz, pn);

    ro.yx = rotate(ro.yx, tt);
    rd.yx = rotate(rd.yx, tt);


    ro.x += cst * 2.;

    vec3 sky = vec3(abs(1. - rd.y));

    vec3 col = sky;

    float t = -ro.y / rd.y;
    vec3 hit = ro + rd * t;

    if(t > 0.)
    {
        vec2 tuv = hit.xz * 3.;
        vec2 dx = dFdx(tuv), dy = dFdy(tuv);
    	col = vec3(.25) * (
            map0(tuv) +
            map0(tuv + dx * .5) +
            map0(tuv + dy * .5) +
            map0(tuv + dy * .5 + dx * .5));

        col = col * .5;

        col = mix(sky, col, exp(t * -.04));

        vec3 ref = reflect(rd, vec3(0., 1., 0.));

        ref += (sin(hit.x * 30.) + sin(hit.z * 10.)) * .002;

        vec3 colr;
        particles(colr, hit, ref, iTime * .5);

        col += colr * .5;
    }

    particles(col, ro, rd, iTime * .5);


    col *= texture(iChannel1, uv * .1 + vec2(pn * 0.2, iTime * -.01)).x * .2 + .8;

    col *= shut;

    col *= step(abs(uv.y) - .7, 0.);

    col *= (1. - pow(abs(uv.x * .2), 2.)) * (1. - pow(abs(uv.y * .6), 2.));

    //col = pow(col, vec3(3.)) * 3.;
    //col = pow(col, vec3(1. / 2.2));

	fragColor = vec4(col, 1.0);
}

