const int NUM_STEPS = 8;
const float PI	 	= 3.1416;
const float EPSILON	= 1e-3;
#define EPSILON_NRM (0.1 / iResolution.x)

// sea
const int ITER_GEOMETRY = 30;
const int ITER_FRAGMENT = 5;
const float SEA_HEIGHT = 0.6;
const float SEA_CHOPPY = 4.0;
const float SEA_SPEED = 0.8;
const float SEA_FREQ = 0.16;

//const vec3 SEA_BASE = vec3(0.9,0.1,0.1);
//const vec3 SEA_WATER_COLOR = vec3(0.6,0.1,0.8);

const vec3 SEA_BASE = vec3(0.1,0.19,0.22);
const vec3 SEA_WATER_COLOR = vec3(0.8,0.9,0.6);
#define SEA_TIME (1.0 + iTime * SEA_SPEED)
const mat2 octave_m = mat2(1.6,1.2,-1.2,1.6);

// math
mat3 fromEuler(vec3 ang) {
	vec2 a1 = vec2(sin(ang.x),cos(ang.x));
    vec2 a2 = vec2(sin(ang.y),cos(ang.y));
    vec2 a3 = vec2(sin(ang.z),cos(ang.z));
    mat3 m;
    m[0] = vec3(a1.y*a3.y+a1.x*a2.x*a3.x,a1.y*a2.x*a3.x+a3.y*a1.x,-a2.y*a3.x);
	m[1] = vec3(-a2.y*a1.x,a1.y*a2.y,a2.x);
	m[2] = vec3(a3.y*a1.x*a2.x+a1.y*a3.x,a1.x*a3.x-a1.y*a3.y*a2.x,a2.y*a3.y);
	return m;
}
float hash( vec2 p ) {
	float h = dot(p,vec2(127.1,311.7));	
    return fract(sin(h)*43758.5453123);
}
float noise( in vec2 p ) {
    vec2 i = floor( p );
    vec2 f = fract( p );	
	vec2 u = f*f*(3.0-2.0*f);
    return -1.0+2.0*mix( mix( hash( i + vec2(0.0,0.0) ), 
                     hash( i + vec2(1.0,0.0) ), u.x),
                mix( hash( i + vec2(0.0,1.0) ), 
                     hash( i + vec2(1.0,1.0) ), u.x), u.y);
}

// lighting
float diffuse(vec3 n,vec3 l,float p) {
    return pow(dot(n,l) * 0.4 + 0.6,p);
}
float specular(vec3 n,vec3 l,vec3 e,float s) {    
    float nrm = (s + 8.0) / (3.1415 * 8.0);
    return pow(max(dot(reflect(e,n),l),0.0),s) * nrm;
}

// sky
vec3 getSkyColor(vec3 e) {
    e.y = max(e.y,0.0);
    return vec3(pow(1.0-e.y,2.0), 1.0-e.y, 0.6+(1.0-e.y)*0.4);
}

// sea
float sea_octave(vec2 uv, float choppy) {
    uv += noise(uv);        
    vec2 wv = 1.0-abs(sin(uv));
    vec2 swv = abs(cos(uv));    
    wv = mix(wv,swv,wv);
    return pow(1.0-pow(wv.x * wv.y,0.65),choppy);
}

float map(vec3 p) {
    float freq = SEA_FREQ;
    //float amp = SEA_HEIGHT;
    float amp = texture(iChannel0, vec2(.02, .25)).r*1.5;
    float choppy = SEA_CHOPPY;
    vec2 uv = p.xz; uv.x *= 0.75;

    float d, h = 0.0;    
    for(int i = 0; i < ITER_GEOMETRY; i++) {        
    	d = sea_octave((uv+SEA_TIME)*freq,choppy);
    	d += sea_octave((uv-SEA_TIME)*freq,choppy);
        h += d * amp;        
    	uv *= octave_m; freq *= 1.9; amp *= 0.22;
        choppy = mix(choppy,1.0,0.2);
    }
    return p.y - h;
}

float map_detailed(vec3 p) {
    float freq = SEA_FREQ;
    //float amp = SEA_HEIGHT;
    float amp = texture(iChannel0, vec2(.02, .25)).r*1.5;
    float choppy = SEA_CHOPPY;
    vec2 uv = p.xz; uv.x *= 0.75;

    float d, h = 0.0;    
    for(int i = 0; i < ITER_FRAGMENT; i++) {        
    	d = sea_octave((uv+SEA_TIME)*freq,choppy);
    	d += sea_octave((uv-SEA_TIME)*freq,choppy);
        h += d * amp;        
    	uv *= octave_m; freq *= 1.9; amp *= 0.22;
        choppy = mix(choppy,1.0,0.2);
    }
    return p.y - h;
}

vec3 getSeaColor(vec3 p, vec3 n, vec3 l, vec3 eye, vec3 dist) {  
    float fresnel = clamp(1.0 - dot(n,-eye), 0.0, 1.0);
    fresnel = pow(fresnel,3.0) * 0.65;

    vec3 reflected = getSkyColor(reflect(eye,n));    
    vec3 refracted = SEA_BASE + diffuse(n,l,80.0) * SEA_WATER_COLOR * 0.12; 

    vec3 color = mix(refracted,reflected,fresnel);

    float atten = max(1.0 - dot(dist,dist) * 0.001, 0.0);
    color += SEA_WATER_COLOR * (p.y - SEA_HEIGHT) * 0.18 * atten;

    color += vec3(specular(n,l,eye,60.0));

    return color;
}

// tracing
vec3 getNormal(vec3 p, float eps) {
    vec3 n;
    n.y = map_detailed(p);    
    n.x = map_detailed(vec3(p.x+eps,p.y,p.z)) - n.y;
    n.z = map_detailed(vec3(p.x,p.y,p.z+eps)) - n.y;
    n.y = eps;
    return normalize(n);
}

float heightMapTracing(vec3 ori, vec3 dir, out vec3 p) {  
    float tm = 0.0;
    float tx = 1000.0;    
    float hx = map(ori + dir * tx);
    if(hx > 0.0) return tx;   
    float hm = map(ori + dir * tm);    
    float tmid = 0.0;
    for(int i = 0; i < NUM_STEPS; i++) {
        tmid = mix(tm,tx, hm/(hm-hx));                   
        p = ori + dir * tmid;                   
    	float hmid = map(p);
		if(hmid < 0.0) {
        	tx = tmid;
            hx = hmid;
        } else {
            tm = tmid;
            hm = hmid;
        }
    }
    return tmid;
}

// main
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;    
    float time = iTime * 0.3 + iMouse.x*0.01;

    // ray
    vec3 ang = vec3(sin(time*3.0)*0.1,sin(time)*0.2+0.3,time);    
    vec3 ori = vec3(0.0,3.5,time*5.0);
    vec3 dir = normalize(vec3(uv.xy,-2.0)); dir.z += length(uv) * 0.15;
    dir = normalize(dir) * fromEuler(ang);

    // tracing
    vec3 p;
    heightMapTracing(ori,dir,p);
    vec3 dist = p - ori;
    vec3 n = getNormal(p, dot(dist,dist) * EPSILON_NRM);
    vec3 light = normalize(vec3(0.0,1.0,0.8)); 

    // color
    vec3 color = mix(
        getSkyColor(dir),
        getSeaColor(p,n,light,dir,dist),
    	pow(smoothstep(0.0,-0.05,dir.y),0.3));

    // post
	fragColor = vec4(pow(color,vec3(0.75)), 1.0);
}

#define rand(x) fract(sin(x)*1e4)

float rd(vec2 p, float x) {
    p.x=mod(p.x,x);
    return fract(sin(dot(p,vec2(12.9898,78.233)))*43758.5453123);
}

float noise(vec2 uv, float x) {
    vec2 iuv=floor(uv),
        fuv=smoothstep(0.,1.,fract(uv)),
        oi=vec2(0.,1.);
    float n00=rd(iuv,x),
        n01=rd(iuv+oi.xy,x),
        n10=rd(iuv+oi.yx,x),
        n11=rd(iuv+oi.yy,x);
    return mix(
        mix(n00,n10,fuv.x),
        mix(n01,n11,fuv.x),
        fuv.y);
}

vec3 hsv(vec3 c) {
    vec4 k=vec4(1.,2./3.,1./3.,3.);
    vec3 p=abs(fract(c.xxx+k.xyz)*6.-k.www);
    return c.z*mix(k.xxx,clamp(p-k.xxx,0.,1.),c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv-=.5;
    uv.x*=iResolution.x/iResolution.y;
    float d=length(uv);
    vec3 cex=vec3(0.);
    for(float f=0.;f<6.;++f){
		vec3 r=rand(f+vec3(0.,3.,5.));
        float x=10.+10.*f;
        float a=atan(uv.y,uv.x)/6.28318531*x+(r.z-.5)*iTime;
        float n=noise(vec2(a,iTime*r.x),x);
        cex+=.1*n/d*hsv(vec3(r.y,.5,1.));
    }
    vec3 c=mix(vec3(.1-d*.2),cex,smoothstep(.2,.21,d));
	fragColor = vec4(c,1.);
}

// filip fragment shader and music by movAX13h, May 2017
// supersampling added by srtuss

float rand( float n )
{
  	return fract(cos(n)*4145.92653);
}

float noise(vec2 p)
{
  	vec2 f  = smoothstep(0.0, 1.0, fract(p));
  	p  = floor(p);
  	float n = p.x + p.y*57.0;
  	return mix(mix(rand(n+0.0), rand(n+1.0),f.x), mix( rand(n+57.0), rand(n+58.0),f.x),f.y);
}

float fbm( vec2 p )
{
	mat2 m2 = mat2(1.6,-1.2,1.2,1.6);	
  	float f = 0.5000*noise( p ); p = m2*p;
  	f += 0.2500*noise( p ); p = m2*p;
  	f += 0.1666*noise( p ); p = m2*p;
  	f += 0.0834*noise( p );
  	return f;
}

vec2 rotate(vec2 p, float a)
{
	vec2 r;
	r.x = p.x*cos(a) - p.y*sin(a);
	r.y = p.x*sin(a) + p.y*cos(a);
	return r;
}

float dir(vec2 a, vec2 b, vec2 c)
{
	return (a.x - c.x) * (b.y - c.y) - (b.x - c.x) * (a.y - c.y);
}

bool insideQuad(vec2 p, vec2 a, vec2 b, vec2 c, vec2 d)
{
	bool b1 = dir(p, a, b) < 0.0;
	bool b2 = dir(p, b, c) < 0.0;
	bool b3 = dir(p, c, d) < 0.0;
	bool b4 = dir(p, d, a) < 0.0;
  	return ((b1 == b2) && (b2 == b3) && (b3 == b4));
}

float background(vec2 uv)
{
    float i = smoothstep(0.0, 0.5, 0.1-uv.y);
    float z = 0.3 - uv.y;

    uv.x = (0.2*uv.x - 0.1) / z;
    uv.y = (1.9*uv.y - 0.3) / z;

    i *= smoothstep(-0.8, 0.2, uv.x);

    float n = iResolution.x / 24.0;    
    vec2 g = floor(uv * n) / n;

    return 1.0 - 0.8 * i * fbm(g * 80.0);    
}

float seg(float id, vec2 p, float time)
{
    float t = 0.8*time - 0.18 * id;
    float s = 0.022;
    float r = t + 6.283 * rand(id);
    float z = 1.0+0.3*sin(t*0.6) + 0.3*sin(t*1.9);

    p.x /= z;
    p.y /= z;

    vec2 q = p;
    q.y = abs(q.y + 0.2) - 0.33;

    vec2 pos = vec2(0.5 * sin(t) + 0.4*sin(t*1.6), 
                    0.2 * abs(sin(2.2*t)) - 0.3);

    vec2 scale = vec2(sin(t*3.0), cos(t*3.0));

    vec2 a = rotate(scale*vec2(-s, -s), r) + pos;
    vec2 b = rotate(scale*vec2( s, -s), r) + pos;
    vec2 c = rotate(scale*vec2( s,  s), r) + pos;
    vec2 d = rotate(scale*vec2(-s,  s), r) + pos;

    return insideQuad(q, a, b, c, d) ? 0.1 + 0.6 *step(0.0, p.y+0.2) : 0.0;
}

vec2 worm(vec2 uv, float time)
{
    for(int i=0; i < 15; i++)
    {
        float s = seg(float(i), uv, time);
        if (s > 0.0) return vec2(s, step(float(i), s-0.5));        
    }

    return vec2(0.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);

    for(int i = 0; i < 2; ++i)
    {
        for(int j = 0; j < 2; ++j)
        {
            vec2 uv = 2.0 * ((fragCoord.xy + vec2(j, i) * 0.5) / iResolution.xy) - 1.0;
    		uv.x *= iResolution.x / iResolution.y;

            vec3 col = vec3(1.0);

            float time = iTime + mod(iTime, 0.01);

            col *= background(uv);
            vec2 w = worm(uv, time);
            col -= w.x;
            col = mix(col, vec3(1.0, 0.0, 0.0), w.y);

            col *= smoothstep(0.48, 0.481, 1.0-abs(uv.y));
            col -= 0.6*smoothstep(0.3,2.0,length(uv));
            col -= vec3(0.01, 0.01, 0.0);
            col = clamp(col, 0.0, 1.0);
            fragColor.xyz += col;
        }
    }
    fragColor /= 4.0;
}

