// f(x) = x^8+15x^4-16
vec2 f(vec2 z) {
    vec2 c1 = cx_pow(z, 8.0);
    vec2 c2 = cx_pow(z, 4.0) * 15.0;

    return c1 + c2 - 16.0;
} 

// f(x) derivated
// f'(x) = 8x^7+60x^3  
vec2 fPrim(vec2 z) {
    return cx_pow(z, 7.0) * 8.0 + cx_pow(z, 3.0) * 60.0;
} 

int maxIterations = 50;
vec2 one = vec2(1, 0);
float newtonRapson(vec2 z) {
  vec2 oldZ = z;
  float s = 0.0;
  for(int i = 0; i < maxIterations; i++){
    z = z - cx_div(f(z), fPrim(z)); 
    if(abs(oldZ.x - z.x) < 0.001 && abs(oldZ.y - z.y) < 0.001) {
      return s;
    }

    vec2 w = cx_div(one, (oldZ - z));
    float wAbs = cx_abs(w);

    s += exp(-wAbs);
    oldZ = z;
  }
  return float(maxIterations);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 mouse = iMouse.xy;
    if(mouse.x < 0.01 && mouse.y < 0.01) {
     	mouse = iResolution.xy / 3.0;
    }

    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 zoomed = uv / mouse.x * iResolution.x / 2.0;
    vec2 rotated = zoomed * rotate(iTime / 3.0);


    float hue = newtonRapson(rotated) / float(maxIterations) * mouse.y / 20.0;
    vec3 c = hsv2rgb(vec3(hue, 1.0, 1.0));
    fragColor = vec4(c, 1.0);
}

