bool isLightVisible( Ray shadowRay ) {
    float distToHit;
    SurfaceHitInfo tmpHit;

    raySceneIntersection( shadowRay, EPSILON, true, tmpHit, distToHit );

    return ( tmpHit.mtl_id_ >= LIGHT_ID_BASE );
}

Light pickOneLight(out float lightPickingPdf) {
    lightPickingPdf = 1.0;
    return lights[0];
