// Normalised random number, borrowed from Hornet's noise distributions: https://www.shadertoy.com/view/4ssXRX
float nrand(in vec2 n) {
	return fract(sin(dot(n.xy, vec2(12.9898, 78.233)))* 43758.5453);
}

/*
---- INTERSECTION OPS ----
*/

// Union of two signed distances
float unionOp(float d0, float d1) {
    return min(d0, d1);
}

// Intersection of two signed distances
float intersectOp(float d0, float d1) {
    return max(d0, d1);
}

// Difference of two signed distances
float differenceOp(float d0, float d1) {
    return max(d0, -d1);
}

/*
---- Scene rendering ----
*/

float seaHeight(in Ray ray, in float t) { 
    float result = 0.0;
    vec2 s = vec2(0.10, 0.11);
    float h = 2.0;
    for (int i=0; i<2; i++) {
        result += sin(ray.origin.x * s.x + ray.origin.z * s.y + t * 2.0) * h;
        s *= vec2(0.75, 1.25);
        result += sin(ray.origin.x * s.x + ray.origin.z * s.y + t * 2.0) * h;
        s *= vec2(2.6, 1.4);
        h *= 0.5;
    }
    return result;
}
float seaDist(in Ray ray, in float t) {
    // the sea
    float seaDist;
    if (ray.dir.y < 0.0 || ray.origin.y < 3.0) {
        seaDist = ray.origin.y + seaHeight(ray, t);
    } else {
        seaDist = (ray.origin.y - 3.0) / -ray.dir.y;
        float seaSkipper = step(0.01, seaDist);
        seaDist = seaDist * seaSkipper + 10000.0 * (1. - seaSkipper);
    }
    return seaDist;
}

float scrollDist(in Ray ray, inout vec2 uv, inout int idx, in float offset) {
    // The scroller
    Ray tr = ray;
    R(tr.origin.xz, -0.5);
    tr.origin.x = -tr.origin.x;
    tr.origin.y += offset;

    /*
    float r = (sin(ray.origin.x + iTime)) * 0.2;
    tr.origin += vec3(
        sin(tr.origin.y*.156145 + iTime*1.561)+sin(tr.origin.y*.11568 + iTime*1.754), 
        sin(tr.origin.x*.18491 + iTime*1.875)+sin(tr.origin.z*.17854 + iTime*1.7432), 
        sin(tr.origin.x*.1475 + iTime*1.1561)+sin(tr.origin.x*.1156 + iTime*1.456)
    )*4.;
*/
    tr.origin.x += iTime * SCROLL_SPEED;
    // The scroll box, assume character size of 10x10
    Box scrollBox = Box(
        vec3(
            (MESSAGE_LENGTH * 10) / 2 + int(SCROLL_START_DELAY),
            5.,
            40),
        vec3(MESSAGE_LENGTH * 10, 9.75, 0.1), 
        0.0
        );
    float scrollD = boxDist(tr, scrollBox);
    // tr.origin.x *= 0.2;
    uv = vec2(mod(tr.origin.x, 10.), (tr.origin.y - 0.0)) / 10.;
    idx = int(mod((tr.origin.x - SCROLL_START_DELAY) / 10., float(MESSAGE_LENGTH)));
    return scrollD;
}

// Get the distance to the scene (returns a struct containing distance and nearest material)
SDResult sceneDist(in Ray ray) {
    SDResult result;

    float t = mod(iTime, kPI);

    float seaDist = seaDist(ray, t);

    vec2 uv; int idx;
    float scrollDist = scrollDist(ray, uv, idx, seaHeight(ray, t));
    result.uv = uv; result.idx = idx;

    // the thing
    float thingHeight = clamp(iTime - 16.0, 0.0, 5.0) * 100.0;
    vec3 thingPos = vec3(0, thingHeight - 200.0, 1000);    

    ray.origin -= thingPos;
    ray.origin = abs(ray.origin);

    R(ray.origin.xy, t);
    R(ray.origin.xz, t);

    float distToBall = differenceOp(
        sphereDist(ray, Sphere(vec3(0), 160.0)),
        sphereDist(ray, Sphere(vec3(0), 150.0))
        );
    float distToBox1 = boxDist(ray, Box(vec3(0), vec3(400,400,20), 10.0));
    float distToBox2 = boxDist(ray, Box(vec3(0), vec3(400,20,400), 10.0));

    float thingDist = unionOp(distToBox1, distToBox2);
    thingDist = intersectOp(thingDist, distToBall);

    result.d = unionOp(
        seaDist,
        thingDist
        );
    result.d = unionOp(result.d, scrollDist);
    //result.d = unionOp(result.d, bDist);

    if (result.d == seaDist) {
        result.material = kSEAMATERIAL;
    } else if (result.d == scrollDist) {
        result.material = kSCROLLMATERIAL;
    } else {
    	result.material = kGLOWMATERIAL;
    }
    return result;
}

// Gets the normal
vec3 normal(in Ray ray) {
    vec2 eps = vec2(0.0001, 0);
    float baseDist = sceneDist(ray).d;
 	return normalize(vec3(
        sceneDist(Ray(ray.origin + eps.xyy, ray.dir)).d - 
        sceneDist(Ray(ray.origin - eps.xyy, ray.dir)).d,
        sceneDist(Ray(ray.origin + eps.yxy, ray.dir)).d -
        sceneDist(Ray(ray.origin - eps.yxy, ray.dir)).d,
        sceneDist(Ray(ray.origin + eps.yyx, ray.dir)).d -
        sceneDist(Ray(ray.origin - eps.yyx, ray.dir)).d
        ));
}

// Moves the ray to the surface. Helps avoid artefacts due to ray intersection imprecision.
void clampToSurface(in Ray ray, in float d, inout vec3 n) {
 	ray.origin += n * d;
 	d = sceneDist(ray).d;
 	n = normal(ray);
}

// Calulcate a fresnel term for reflections
float fresnelTerm(in Ray ray, in vec3 n, in float power) {
	float fresnel = min(1., dot(ray.dir, n) + 1.0);
	fresnel = pow(fresnel, power);
    return fresnel;
}

