#define CS(a) vec2(cos(a), sin(a))

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{ 
    float phaseDiff = 2.22;

	vec2 uv = (fragCoord.xy -0.5 * iResolution.xy) / iResolution.x * 2.0;
    vec2 uv2 = vec2(uv);

    float t = iTime;
    vec2 xy = CS(t/2.2)/3.0 + CS(t*3.0)/5.0;
    uv.xy += xy;
	float radius = length(uv);
	bool toggle = mod(radius, 0.1) > 0.05;

    float t2 = iTime + phaseDiff;
    vec2 xy2 = CS(t2/1.7)/1.533 + CS(t2*2.34)/4.0;
    uv2.xy += xy2;
    float radius2 = length(uv2);
    bool toggle2 = mod(radius2, 0.1) > 0.05;

    bool xor = toggle != toggle2;

	fragColor = vec4(xor, 0.0, xor, 1.0);
}

