{
    vec3 p3 = vec3(p.x, p.y, 0.0) + vec3(0.0, 0.0, time*0.025);
    float noise = simplex3d(p3*32.0);// simplex3d_fractal(p3*8.0+8.0);
	return 0.5 + 0.5*noise;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = 28.22+NIGHTSPEEDBONUS*iTime;
    float bignessScale = 1.0/NOISEBIGNESS;
	vec2 p = fragCoord.xy / iResolution.y;
    float aspect = iResolution.x/iResolution.y;
    vec2 positionFromCenter = p-vec2(0.5*aspect, 0.5);

    p = vec2(0.5*aspect, 0.5)+normalize(positionFromCenter)*min(length(positionFromCenter)+CENTERCONNECTEDNESS*CENTERSCALE*RADIUS, CENTERSCALE*RADIUS);    

    float noise = getNoiseValue(bignessScale*0.25*p, time);

    float distanceFromCenter = clamp(1.0-length(positionFromCenter)/RADIUS, 0.0, 1.0);

    float scaledDistance = distanceFromCenter * noise;


    float falloffMask = 1.0; 

    #ifndef UNLIMITED
        falloffMask =  2.0*scaledDistance-1.0;
        falloffMask = clamp(1.0-pow(abs(falloffMask), FALLOFFPOW), 0.0, 1.0);
    #endif

    float thinnerMask;

    thinnerMask = 1.0-clamp(abs(distanceFromCenter-(1.0-CENTERSCALE))/CENTERSCALE, 0.0, 1.0);
    thinnerMask = pow(thinnerMask, 16.0);    
    thinnerMask = clamp(0.9*thinnerMask, 0.0, 1.0);

    float finalValue;
    finalValue = falloffMask;

    float innerBall = clamp(abs(distanceFromCenter-(1.0-CENTERSCALE))/CENTERSCALE, 0.0, 1.0);
    innerBall = smoothstep(0.5, 0.85, innerBall);
    innerBall += noise;

    finalValue = mix( (noise*falloffMask+thinnerMask)*thinnerMask + innerBall, noise*falloffMask+thinnerMask, step(distanceFromCenter, 1.0-CENTERSCALE));

    finalValue = smoothstep(EDGE,EDGE+0.1, finalValue);


    vec3 colorNoise;
    colorNoise.x	= getNoiseValue(bignessScale*0.25*p, 10.0+time);
    colorNoise.y 	= getNoiseValue(bignessScale*0.25*p, 00.0+time);
    colorNoise.z	= getNoiseValue(bignessScale*0.25*p, 30.0+time);

    colorNoise.x = smoothstep(EDGE,EDGE+0.1, colorNoise.x);
    colorNoise.y = smoothstep(EDGE,EDGE+0.1, colorNoise.y);
    colorNoise.z = smoothstep(EDGE,EDGE+0.1, colorNoise.z);


    vec3 finalColor;
    //finalColor = vec3(colorNoise.x, colorNoise.y, colorNoise.x+colorNoise.y); 
    finalColor = mix(vec3(1.0, colorNoise.x, 0.05), vec3(colorNoise.x, 1.0, 1.0), colorNoise.y);
    finalColor += vec3(1.0) * (pow(clamp(distanceFromCenter+CENTERSCALE, 0.0, 1.0), 8.0));

    //finalColor = clamp(finalColor, vec3(0.0), vec3(1.0));

    finalColor *= finalValue;

    vec3 bgColor = mix(vec3(0.07,0.0,0.15), vec3(0.35,0.15,0.5), distanceFromCenter*0.5);
    bgColor += vec3(0.2,.8,0.4)* pow(distanceFromCenter, 4.0);
    finalColor += bgColor;

	fragColor = vec4(finalColor,1.0);
}

/*

	Mountain Path
	-------------

    A path running through some dry rocky terrain. Rendered in a pseudo low-poly style.

	This was orginally supposed to be a simple curvy path through a field, but then I wondered what
	stepping the path would look like, then I wanted to see what it'd look like going through
	something... so now, I'm not really sure what it is, or why anyone in their right mind would 
	build a ridiculously long path in the middle of a mountainous pass that leads to nowhere. :)

	I'm fond of the old demo scenes and those pictures on 80s sci-fi novels, so I went for a psuedo 
	low-poly look with a little realism thrown into the mix. The geometry was surprisingly simple to 
    produce - I thought the stairs might present some problems, but it just involved stepping the 
    path heights and dealing with the overlap.

	The low poly terrain is just a sinusoidal-based layer with some relatively cheap cellular noise 
	added to it. Anyway, this is just a practice run for a more interesting variation I have in mind.



*/

#define FAR 80. // Maximum ray distance. Analogous to the far plane.


// Scene object ID. Either the path (0) or the surroundings (1).
float objID;
float svObjID; // Global ID to keep a copy of the above from pass to pass.

// Fabrice's concise, 2D rotation formula.
mat2 r2(float th){ vec2 a = sin(vec2(1.5707963, 0) + th); return mat2(a, -a.y, a.x); }

// vec3 to float hash.
float hash31( vec3 p ){ return fract(cos(dot(p, vec3(157, 113, 7)))*45758.5453); }

// Tri-Planar blending function. Based on an old Nvidia tutorial.
vec3 tex3D( sampler2D t, in vec3 p, in vec3 n ){

    n = max(abs(n), 0.001);
    n /= dot(n, vec3(1));
	vec3 tx = texture(t, p.yz).xyz;
    vec3 ty = texture(t, p.zx).xyz;
    vec3 tz = texture(t, p.xy).xyz;

    // Textures are stored in sRGB (I think), so you have to convert them to linear space 
    // (squaring is a rough approximation) prior to working with them... or something like that. :)
    // Once the final color value is gamma corrected, you should see correct looking colors.
    return (tx*tx*n.x + ty*ty*n.y + tz*tz*n.z);
}


