void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 r = 2.0 * vec2(uv.x - 0.5, uv.y - 0.5);
    r.x *= iResolution.x / iResolution.y;
    r.y *= sin(r.x * 6.0 + 1.0) / 4.0 + sin(r.x * 11.0 + 2.0) / 4.0 + 2.0;
    //r.x *= abs(sin(r.y * 3.0 + 1.0) / 4.0 + sin(r.y * 5.0 - 2.0) / 4.0) + 1.0;
    //r.y *= sin(r.x * 4.0 - 1.0) / 4.0 + sin(r.x * 9.0 + 2.0) / 4.0 + 1.0;

    //rotate
    const float RotateTime = 21.0;
    if(iTime >= RotateTime){
        float the = (3.0 - length(r)) * (iTime - RotateTime) / 10.0;
    	r.xy *= mat2(cos(the), -sin(the), sin(the), cos(the));
    }

    vec3 ret = vec3(0.0, 0.0, 0.0);

    float x[3] = float[](-1.2, 0.9, 0.0);
    float y[3] = float[](-0.9, 0.0, 0.8);
    vec3 cols[3] = vec3[](vec3(1.0, 0.0, 0.0), vec3(0.0, 1.0, 0.0), vec3(0.0, 0.0, 1.0));
    float dis = 10.0;

    for(int i = 0; i < 3; i++){
        float disi = length(r - vec2(x[i], y[i])) ;
        if(disi <= iTime * 0.1 && disi < dis){
        	ret = cols[i];
            dis = disi;
        }
    }

	fragColor = vec4(ret,1.0);
}

