// Spherical to cartesian coordinates
vec3 sphe2cart(vec2 p)
{
    vec2 s = sin(p);
    vec2 c = cos(p);
    return vec3(c.x*s.y, c.y, s.x*s.y);
}

vec2 cart2sphe(vec3 p)
{
    p *= inversesqrt(dot(p, p));
    return vec2(atan(p.z, p.x), acos(p.y));
}

// Ray-sphere intersection
float sphere(vec3 o, vec3 l, vec3 c, float r2)
{
    const float eps = 0.0001;

    float a_ = dot(l,l);
	vec3 p = o - c;
    float b_ = 2.0 * dot(l,p);
    float c_ = dot(p,p) - r2;

    float a = b_*b_-4.0*a_*c_;
    if (a < 0.0) return -1.0;
    a = sqrt(a);
    float t2 = -b_ + a;
    if (t2 < eps) return -1.0;
    float t1 = -b_ - a;
    float result = (t1 > eps && t1 < t2) ? t1 : t2;
    return result * 0.5 / a_;
}

int rotl17(int v) { return (v << 17) | (v >> 15); }

int hash(int x, int y, int z, int seed)
{
    int h = seed * 374761393;
    h = (x ^ h) * 374761393;
    h = (y ^ h) * 374761393;
    h = (z ^ h) * 374761393;
    h = (h ^ rotl17(h)) * 2246822519;
    return h;
}

float noise(vec3 p, int seed)
{
    return float(hash(int(p.x), int(p.y), int(p.z), seed)) / 4294967296.0;
}

float vnoise(vec3 p, int seed)
{
    vec3 p0 = floor(p);
    vec3 s = p - p0;
    //s = s * s * (3.0 - 2.0 * s);
    s = s * s * s * (s * (s * 6.0 - 15.0) + 10.0);

    return mix(
        mix(
        	mix(noise(p0, seed), noise(p0 + vec3(1.0, 0.0, 0.0), seed), s.x),
        	mix(noise(p0 + vec3(0.0, 1.0, 0.0), seed), noise(p0 + vec3(1.0, 1.0, 0.0), seed), s.x),
        	s.y),
        mix(
        	mix(noise(p0 + vec3(0.0, 0.0, 1.0), seed), noise(p0 + vec3(1.0, 0.0, 1.0), seed), s.x),
        	mix(noise(p0 + vec3(0.0, 1.0, 1.0), seed), noise(p0 + vec3(1.0), seed), s.x),
        	s.y),
        s.z);
}

vec3 grad(vec3 p, int seed)
{
    return vec3(noise(p, seed), noise(p, seed + 1), noise(p, seed + 2));
}

// by: IÃ±igo Quilez (with small modifications for custom hash)
// https://www.shadertoy.com/view/4dffRH
float gnoise(in vec3 x, int seed)
{
    // grid
    vec3 p = floor(x);
    vec3 w = fract(x);

    #if 1
    // quintic interpolant
    vec3 u = w*w*w*(w*(w*6.0-15.0)+10.0);
    vec3 du = 30.0*w*w*(w*(w-2.0)+1.0);
    vec3 iu = 1.0 - u;
    #else
    // cubic interpolant
    vec3 u = w*w*(3.0-2.0*w);
    vec3 du = 6.0*w*(1.0-w);
    #endif    

    // gradients
    vec3 ga = grad( p+vec3(0.0,0.0,0.0), seed );
    vec3 gb = grad( p+vec3(1.0,0.0,0.0), seed );
    vec3 gc = grad( p+vec3(0.0,1.0,0.0), seed );
    vec3 gd = grad( p+vec3(1.0,1.0,0.0), seed );
    vec3 ge = grad( p+vec3(0.0,0.0,1.0), seed );
	vec3 gf = grad( p+vec3(1.0,0.0,1.0), seed );
    vec3 gg = grad( p+vec3(0.0,1.0,1.0), seed );
    vec3 gh = grad( p+vec3(1.0,1.0,1.0), seed );

    // projections
    float va = dot( ga, w-vec3(0.0,0.0,0.0) );
    float vb = dot( gb, w-vec3(1.0,0.0,0.0) );
    float vc = dot( gc, w-vec3(0.0,1.0,0.0) );
    float vd = dot( gd, w-vec3(1.0,1.0,0.0) );
    float ve = dot( ge, w-vec3(0.0,0.0,1.0) );
    float vf = dot( gf, w-vec3(1.0,0.0,1.0) );
    float vg = dot( gg, w-vec3(0.0,1.0,1.0) );
    float vh = dot( gh, w-vec3(1.0,1.0,1.0) );

    // interpolations
    return 
        va + u.x*(vb-va) + u.y*(vc-va) + u.z*(ve-va) + 
        u.x*u.y*(va-vb-vc+vd) + 
        u.y*u.z*(va-vc-ve+vg) + 
        u.z*u.x*(va-vb-ve+vf) + 
        (-va+vb+vc-vd+ve-vf-vg+vh)*u.x*u.y*u.z;
}

// by: IÃ±igo Quilez (with small modifications for custom hash)
// https://www.shadertoy.com/view/4dffRH
vec4 gnoised(in vec3 x, int seed)
{
    // grid
    vec3 p = floor(x);
    vec3 w = fract(x);

    #if 1
    // quintic interpolant
    vec3 u = w*w*w*(w*(w*6.0-15.0)+10.0);
    vec3 du = 30.0*w*w*(w*(w-2.0)+1.0);
    vec3 iu = 1.0 - u;
    #else
    // cubic interpolant
    vec3 u = w*w*(3.0-2.0*w);
    vec3 du = 6.0*w*(1.0-w);
    #endif    

    // gradients
    vec3 ga = grad( p+vec3(0.0,0.0,0.0), seed );
    vec3 gb = grad( p+vec3(1.0,0.0,0.0), seed );
    vec3 gc = grad( p+vec3(0.0,1.0,0.0), seed );
    vec3 gd = grad( p+vec3(1.0,1.0,0.0), seed );
    vec3 ge = grad( p+vec3(0.0,0.0,1.0), seed );
	vec3 gf = grad( p+vec3(1.0,0.0,1.0), seed );
    vec3 gg = grad( p+vec3(0.0,1.0,1.0), seed );
    vec3 gh = grad( p+vec3(1.0,1.0,1.0), seed );

    // projections
    float va = dot( ga, w-vec3(0.0,0.0,0.0) );
    float vb = dot( gb, w-vec3(1.0,0.0,0.0) );
    float vc = dot( gc, w-vec3(0.0,1.0,0.0) );
    float vd = dot( gd, w-vec3(1.0,1.0,0.0) );
    float ve = dot( ge, w-vec3(0.0,0.0,1.0) );
    float vf = dot( gf, w-vec3(1.0,0.0,1.0) );
    float vg = dot( gg, w-vec3(0.0,1.0,1.0) );
    float vh = dot( gh, w-vec3(1.0,1.0,1.0) );

    // interpolations
    return vec4( va + u.x*(vb-va) + u.y*(vc-va) + u.z*(ve-va) + u.x*u.y*(va-vb-vc+vd) + u.y*u.z*(va-vc-ve+vg) + u.z*u.x*(va-vb-ve+vf) + (-va+vb+vc-vd+ve-vf-vg+vh)*u.x*u.y*u.z,    // value
                 ga + u.x*(gb-ga) + u.y*(gc-ga) + u.z*(ge-ga) + u.x*u.y*(ga-gb-gc+gd) + u.y*u.z*(ga-gc-ge+gg) + u.z*u.x*(ga-gb-ge+gf) + (-ga+gb+gc-gd+ge-gf-gg+gh)*u.x*u.y*u.z +   // derivatives
                 du * (vec3(vb,vc,ve) - va + u.yzx*vec3(va-vb-vc+vd,va-vc-ve+vg,va-vb-ve+vf) + u.zxy*vec3(va-vb-ve+vf,va-vb-vc+vd,va-vc-ve+vg) + u.yzx*u.zxy*(-va+vb+vc-vd+ve-vf-vg+vh) ));
}

// Curl-noise 
// Reference
// https://www.cct.lsu.edu/~fharhad/ganbatte/siggraph2007/CD2/content/papers/046-bridson.pdf
vec3 curlnoise(in vec3 p, int seed)
{
    vec4 x = gnoised(p, seed);
    vec4 y = gnoised(p, seed+3);
    vec4 z = gnoised(p, seed+6);
    return vec3(z.z - x.w, x.w - z.y, y.y - x.z);
}

vec3 noise_offset(vec3 p, float f, float s)
{
    p += curlnoise(p * f, 1) * s;
    p += curlnoise(p * f, 1) * s;
    p += curlnoise(p * f, 1) * s;
    return p;
}


float y_equator(float y)
{
    return pow(abs(y), 1.0) * sign(y);
}

float y_noise(float y)
{
    y += texture(iChannel0, vec2(y * 0.05, 0.0)).x * 0.08;
    y += texture(iChannel0, vec2(y * 0.1, 0.0)).x * 0.04;
    y += texture(iChannel0, vec2(y * 0.2, 0.0)).x * 0.02;
    return y;
}

vec4 swirl(vec3 p, vec3 sc, float sr)
{
    float t = max(0.0, 1.0 - distance(p, sc) / sr);
    float t2 = smoothstep(0.0, 1.0, t);
    float theta = t2 * 10.0;
    vec4 q1 = vec4(0.0, p.x, p.y, p.z);
    float theta_half = theta * 0.5;
    float s = sin(theta_half);
    float c = cos(theta_half);
    vec4 q2 = vec4(c, s * sc.x, s * sc.y, s * sc.z);
    vec4 q2i = vec4(q2.x, -q2.y, -q2.z, -q2.w);
    vec4 qa = vec4(
        q1.x*q2.x-q1.y*q2.y-q1.z*q2.z-q1.w*q2.w,
        q1.x*q2.y+q1.y*q2.x-q1.z*q2.w+q1.w*q2.z,
        q1.x*q2.z+q1.y*q2.w+q1.z*q2.x-q1.w*q2.y,
        q1.x*q2.w-q1.y*q2.z+q1.z*q2.y+q1.w*q2.x);
    vec4 q3 = vec4(
        q2i.x*qa.x-q2i.y*qa.y-q2i.z*qa.z-q2i.w*qa.w,
        q2i.x*qa.y+q2i.y*qa.x-q2i.z*qa.w+q2i.w*qa.z,
        q2i.x*qa.z+q2i.y*qa.w+q2i.z*qa.x-q2i.w*qa.y,
        q2i.x*qa.w-q2i.y*qa.z+q2i.z*qa.y+q2i.w*qa.x);
    return vec4(q3.yzw, t2);
    //return vec3(t);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= vec2(0.5);
    uv.x *= iResolution.x / iResolution.y;

#if 1
    vec3 cc = vec3(0.0,0.0,3.6);
    float cam_speed = 0.1;
    vec3 co = 3.6 * sphe2cart(vec2(cam_speed*iTime, 3.14159*0.5+0.5*sin(cam_speed*iTime))) + cc;
    vec3 cf = normalize(cc-co);
    vec3 cr = normalize(cross(cf, vec3(0.0,1.0,0.0)));
    vec3 cu = normalize(cross(cr, cf));
#else
    vec3 cc = vec3(0.0,0.0,3.6);
    vec3 co = vec3(0.0,2.6,3.6);
    vec3 cf = normalize(cc-co);
    vec3 cr = normalize(cross(cf, vec3(0.0,0.0,1.0)));
    vec3 cu = normalize(cross(cr, cf));
#endif
    uv *= 3.14159 * 0.25;

    vec3 rd = normalize(cr*uv.x + cu*uv.y + cf);

    vec3 so = vec3(0.0,0.0,3.6);
    float r = 1.0;
    float r2 = r*r;
    float t = sphere(co, rd, so, r2);
    if (t > 0.0)
    {
        vec3 p = co + t * rd;
        vec3 pc = p - so;

        // Bands
        vec2 scoord_clean = cart2sphe(pc);
        float y_clean = scoord_clean.y / PI * 2.0 - 1.0;
        y_clean = y_equator(y_clean);
        y_clean = y_noise(y_clean);
        float w0_clean = sin((y_clean * HALF_PI + HALF_PI / 4.0) * 12.0f);

        // Swirls
        float row_count = 2.0;
        float row = round(scoord_clean.y * row_count);
        vec3 row_rand = texture(iChannel0, vec2(row / row_count, 0.35)).rgb;
        float swirl_size = 0.3 + 0.7 * row_rand.y;
        float swirl_start = fract(row_rand.x * 10.0f);
        float swirl_offset = fract(iTime * (row_rand.z * 0.9 + 0.1) * 0.03);
        vec2 swirlc_sph = vec2((swirl_start + swirl_offset) * TWO_PI, (row + 0.5 * (1.0 - swirl_size) * (row_rand.z * 2.0 - 1.0)) / row_count);
        vec3 swirlc = sphe2cart(swirlc_sph);
        vec4 swirl_data = swirl(pc, swirlc, 0.9 * swirl_size * 0.5 / row_count);
        //float h = texture(iChannel0, cart2sphe(pc + swirl_data.xyz) / PI).r;
        float swirl_out = 1.0 - swirl_data.w;
        pc = swirl_data.xyz;

        vec3 pc1 = noise_offset(pc, 50.0, 0.002);
        vec3 pc2 = noise_offset(pc, 5.0, 0.02);
        pc1 += (pc2 - pc) * 0.2;
        pc2 += (pc1 - pc) * 0.5;

        pc2 = mix(pc2, swirl_data.xyz, swirl_data.w);

        vec2 scoord = cart2sphe(pc1);
        float y = scoord.y / PI * 2.0 - 1.0;
        y = y_equator(y);
        y = y_noise(y);

        float band = (y * HALF_PI + HALF_PI / 4.0) * 12.0f;
        float w0 = sin(band);
        float w1 = smoothstep(-1.0, 1.0, w0) * 2.0 - 1.0;
        float w2 = smoothstep(-1.0, 1.0, w1) * 2.0 - 1.0;

        // band force
        float a = sin((scoord.x + w0 * 0.25) * 10.0);
        vec3 pn = pc2 * 7.0;
        pn += swirl_out * gnoise(pc * 20.0, 1) * 0.2;
        vec3 ptan = cross(vec3(0.0,1.0,0.0), normalize(pc));
        pn += swirl_out * ptan * w0_clean * 2.0;

        //pn *= 4.0;
        float b = gnoise(pn, 1) + 0.6 * gnoise(pn * 2.0, 2) + 0.5 * gnoise(pn * 4.0, 3) + 0.4 * gnoise(pn * 8.0, 4);
        //float c = mix(w2, 0.75 * w0_clean + 0.4 * b, abs(w0_clean));
        float c = w2 - w0_clean * (b * 0.5 + 0.5);

        float swirl_height_dir = sign(row_rand.z - 0.5);
        c += 0.5 * swirl_data.w * swirl_height_dir;
        //c = clamp(c, -1.0, 1.0) - 0.2 * swirl_data.w * swirl_height_dir * b;
        float swirl_inner = max(0.0, swirl_data.w);
        //swirl_inner = pow(swirl_inner, 0.5);
        c = mix(c, swirl_height_dir * b, swirl_inner);
        //float a = gnoise(pc * 10.0, 1);



        fragColor = vec4(vec3(c * 0.5 + 0.5), 1.0);
        //fragColor = vec4(vec3(swirl_data.w), 1.0);
    }
    else
    {
        fragColor = vec4(vec3(0.1), 1.0);
    }
}

const float EPSILON = 0.0001;
const float MAX_DIST = 100.0;

float remap(float low1, float high1, float low2, float high2, float value){
    return low2 + (value - low1) * (high2 - low2) / (high1 - low1);
}

/**
 * Rotation matrix around the Y axis.
 */
mat3 rotateY(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(c, 0, s),
        vec3(0, 1, 0),
        vec3(-s, 0, c)
    );
}

/**
 * Constructive solid geometry intersection operation on SDF-calculated distances.
 */
float intersectSDF(float distA, float distB) {
    return max(distA, distB);
}

/**
 * Constructive solid geometry union operation on SDF-calculated distances.
 */
float unionSDF(float distA, float distB) {
    return min(distA, distB);
}

/**
 * Constructive solid geometry difference operation on SDF-calculated distances.
 */
float differenceSDF(float distA, float distB) {
    return max(distA, -distB);
}

float SphereSDF(vec3 p, float size){
	return length(p) - size;
}

float CubeSDF(vec3 p, vec3 size){
    vec3 d = abs(p) - size;

    float insideDistance = min( max(max(d.x, d.y),d.z), 0.0);

    float outsideDistance = length( max(d, 0.0) );

    return insideDistance + outsideDistance;
}

float TorusSDF( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}




float SceneSDF(vec3 p){

    p = rotateY(iTime * 0.2) * p;

    float gap = 1.75;

    p.x = mod(p.x + gap, 2.0 * gap) - gap;
    p.z = mod(p.z + gap, 2.0 * gap) - gap;

	float torus = TorusSDF(p, vec2(1,0.5));
    return torus;
}




vec3 EstimateNormal(vec3 p){
	return normalize(vec3(
        SceneSDF(vec3(p.x+EPSILON, p.y, p.z)) - SceneSDF(vec3(p.x-EPSILON, p.y, p.z)),
        SceneSDF(vec3(p.x, p.y+EPSILON, p.z)) - SceneSDF(vec3(p.x, p.y-EPSILON, p.z)),
        SceneSDF(vec3(p.x, p.y, p.z+EPSILON)) - SceneSDF(vec3(p.x, p.y, p.z-EPSILON))
        ));
}

/**
 * Using the gradient of the SDF, estimate the normal on the surface at point p.
 */
vec3 estimateNormal(vec3 p) {
    return normalize(vec3(
        SceneSDF(vec3(p.x + EPSILON, p.y, p.z)) - SceneSDF(vec3(p.x - EPSILON, p.y, p.z)),
        SceneSDF(vec3(p.x, p.y + EPSILON, p.z)) - SceneSDF(vec3(p.x, p.y - EPSILON, p.z)),
        SceneSDF(vec3(p.x, p.y, p.z  + EPSILON)) - SceneSDF(vec3(p.x, p.y, p.z - EPSILON))
    ));
}

vec3 RayDirection(float fieldOfView, vec2 fragCoord, vec2 size){
	vec2 xy = fragCoord - size / 2.0;
    float z = size.y / tan(radians(fieldOfView) / 2.0);
    return normalize(vec3(xy, -z));
}

float ShortestDistanceToSurface(vec3 eye, vec3 marchingDirection, float start, float end){
	float depth = start;
    for(int i = 0; i < 255; i++){
    	float dist = SceneSDF(eye + depth * marchingDirection);
        if( dist <= EPSILON){
            return depth;
        }
        depth += dist;
        if(depth >= end){
        	return end;
        }
    }
    return end;
}

/**
 * Return a transform matrix that will transform a ray from view space
 * to world coordinates, given the eye point, the camera target, and an up vector.
 *
 * This assumes that the center of the camera is aligned with the negative z axis in
 * view space when calculating the ray marching direction. See rayDirection.
 */
mat4 viewMatrix(vec3 eye, vec3 center, vec3 up) {
    // Based on gluLookAt man page
    vec3 f = normalize(center - eye);
    vec3 s = normalize(cross(f, up));
    vec3 u = cross(s, f);
    return mat4(
        vec4(s, 0.0),
        vec4(u, 0.0),
        vec4(-f, 0.0),
        vec4(0.0, 0.0, 0.0, 1)
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 rayDir = RayDirection(45.0, fragCoord.xy, iResolution.xy);

    vec3 eye = vec3(0.0 + 0.0 * cos(iTime),
                    12.0 + 0.0 * sin(iTime),
                    40.0 + 0.0 * sin(iTime));

    vec3 lookAtPos = vec3(3.0 + 0.0 * cos(iTime),
                          0.0 + 0.0 * sin(iTime/2.0),
                          -3.0 + 0.0 * sin(iTime));

    mat4 viewToWorld = viewMatrix(eye, lookAtPos, vec3(0,1,0) );
    vec3 worldDir = ( viewToWorld * vec4(rayDir,0) ).xyz;

    float dist = ShortestDistanceToSurface(eye, worldDir, 0.0, 100.0);

    vec3 ambientColor = vec3(0.5, 0.3, 0.3);
    vec3 color = vec3(1,0,0);

    //vec3 lightPos = vec3(0.5, 0.5, 0.5);
    vec3 lightPos = vec3(4.0 * sin(iTime),
                         6.0,
                         4.0 * cos(iTime));


    vec3 lightDirection = normalize(-lightPos);

    vec3 p = eye + dist * worldDir;
    vec3 normal = estimateNormal(p);
    float diffuse = dot(normal, -lightDirection);
    diffuse = max(diffuse, 0.0);

    vec3 finalColor = vec3(0);

    if( dist < MAX_DIST){
    	finalColor = (normal + vec3(1.0)) / 2.0;
        //finalColor = finalColor * (diffuse + ambientColor);
        finalColor = finalColor * remap(60.0, 20.0, 0.0, 1.0, dist);
    }

	fragColor = vec4(finalColor,1);
}

/// rand
float rand(vec2 n) { 
	return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	/// UV
	vec2 uv= 2.0 * fragCoord / iResolution.xy - vec2(1.0,1.0);
	uv.x *= iResolution.x / iResolution.y;

	/// zoom
	uv *= 1.2;

	/// result
	float r= 0.0;

	/// repat whole process few times
	const float iterationsNum= 16.0;
	for(float b=0.0;b<iterationsNum;b++){
		const float w=8.0;
		for( float a=-w;a<=w;a++){
			const float div= 1.0 / 64.0;
			float x= floor( uv.x/div )*div + div/2.0 + a*div;

			float y= mod( iTime+2.0*rand(vec2(x,0.1+b/iterationsNum)), 2.0 );

			/// radius of ball
			float rad= div*(w+0.5) * clamp( 0.5*y+1.0, 0.1, 1.0 );

			/// ball shape
			if( length(uv-vec2(x,(0.3+0.7*rand(vec2(x,-0.1)))*y-1.0)) < rad ){
				/// dark at bottom and top
				r += clamp( 1.0-abs(y-1.0), 0.0, 1.0 );
			}
		}
	}

	/// get rid of artifacts, look much better than clamp
	r= 1.0 - 1.0 / (1.0 + 4.0*r/iterationsNum );

	/// return
	r *= 3.0;
	fragColor = vec4(
		clamp(r,0.0,1.0),
		clamp(r-1.0,0.0,1.0),
		clamp(r-2.0,0.0,1.0),
		1.0);
}

/*
Base raytracer: http://fhtr.blogspot.com/2013/12/opus-2-glsl-ray-tracing-tutorial.html
Cube intersection function: https://tavianator.com/fast-branchless-raybounding-box-intersections/
Cube normal function: http://ray-tracing-conept.blogspot.com/2015/01/ray-box-intersection-and-normal.html
Implemented suggestions from: iapafoto, abje, KylBlz
*/

float sphere(vec3 ray, vec3 dir, vec3 center, float radius) {
    vec3 rc = ray - center;
    float c = dot(rc, rc) - (radius * radius);
    float b = dot(dir, rc);
    float d = b * b - c;
    float t = -b - sqrt(abs(d));
    float st = step(0.0, min(t, d));
    return mix(-1.0, t, st);
}

float cube(vec3 ray, vec3 dir, vec3 bmin, vec3 bmax) {
    vec3 ddir = 1.0 / dir;
    vec3 t1 = (bmin - ray) * ddir;
    vec3 t2 = (bmax - ray) * ddir;
    vec3 tmin = vec3(min(t1.x, t2.x), min(t1.y, t2.y), min(t1.z, t2.z));
    vec3 tmax = vec3(max(t1.x, t2.x), max(t1.y, t2.y), max(t1.z, t2.z));
    float tmin_max = max(tmin.x, max(tmin.y, tmin.z));
    float tmax_min = min(tmax.x, min(tmax.y, tmax.z));
    return tmax_min >= tmin_max ? tmin_max : -1.0;
}

vec3 cubeNml(vec3 i, vec3 bmin, vec3 bmax) {
    float epsilon = 0.01;
    vec3 c = abs(i-bmin);
    vec3 f = abs(i-bmax);
    return normalize(step(c, vec3(epsilon)) - step(f, vec3(epsilon)));
}

vec3 background(float t, vec3 rd) {
    vec3 light = normalize(vec3(sin(t), 0.2, cos(t)));
    float sun = max(0.0, dot(rd, light));
    float sky = max(0.0, dot(rd, vec3(0.0, 1.0, 0.0)));
    float ground = max(0.0, -dot(rd, vec3(0.0, 1.0, 0.0)));
    return (pow(sun, 256.0) + 0.2 * pow(sun, 2.0)) * vec3(2.0, 1.6, 1.0) +
        pow(ground, 0.5) * vec3(0.4, 0.3, 0.2) +
        pow(sky, 1.0) * vec3(0.5, 0.6, 0.7);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (-1.0 + 2.0 * fragCoord.xy / iResolution.xy) * vec2(iResolution.x / iResolution.y, 1.0);
    vec3 ro = 1.5 * vec3(0.0, 0.0, -3.0);
    vec3 rd = normalize(vec3(uv, 2.0));

    vec3 transmit = vec3(1.0);
    vec3 light = vec3(0.0);

    float epsilon = 0.001;
    int bounce_count = 8;

    // Cube
    vec3 cmin;
    vec3 cmax;
    {
        vec3 size = vec3(0.25);
        float orbit_radius = 2.25;
        float orbit_speed = 1.334;
        vec3 center = vec3(sin(iTime * orbit_speed) * orbit_radius, 0.0, cos(iTime * orbit_speed) * orbit_radius);
        cmin = center - size;
        cmax = center + size;
    }
    float cmat = 0.8;

    // Sphere
    vec3 p = vec3(0.0, 0.0, 0.0);
    float radius = 1.75;
    float smat = 0.9;

    for (int i = 0; i < bounce_count; i++) {
        float c = cube(ro, rd, cmin, cmax);
        float s = sphere(ro, rd, p, radius);

        if (c > 0.0 && (s <= 0.0 || (s > 0.0 && s > c))) { // Ray hit the cube
            ro += rd * c;
            vec3 nml = cubeNml(ro, cmin, cmax);
            transmit *= cmat;
            rd = reflect(rd, nml);
            ro += rd * epsilon;
        } else if (s > 0.0 && (c <= 0.0 || (c > 0.0 && c > s))) { // Ray hit the sphere
            ro += rd * s;
            vec3 nml = normalize(p - ro);
            transmit *= smat;
            rd = reflect(rd, nml);
            ro += rd * epsilon;
        } else {
            light += transmit * background(iTime, rd);
            break;
        }
    }

	fragColor = vec4(light, 1.0);
}

