   idFin = 16, idLeg = 17, idAxl = 18, idWhl = 19, idNose = 20, idCkpit = 21,
   idPlt = 22;
const float pi = 3.14159;

float VorDist (vec2 p)
{
  vec3 dv;
  vec2 ip, fp, g, b;
  ip = floor (p);
  fp = fract (p);
  dv = vec3 (8.);
  b.x = 0.;
  for (float gy = -1.; gy <= 1.; gy ++) {
    for (float gx = -1.; gx <= 1.; gx ++) {
      g = vec2 (gx, gy);
      dv.z = length (g + 0.9 * Hashv2v2 (ip + g) - fp);
      b.y = step (dv.z, dv.y) * (dv.z - dv.y);
      dv.xy += b + step (dv.z, dv.x) * (dv.zx - dv.xy - b);
    }
  }
  return dv.y - dv.x;
}

float GrndHt (vec2 p)
{
  float s;
  s = Noisefv2 (0.3 * p.yx);
  p += 1.5 * sin (2. * pi * s) +
     0.1 * sin (2. * pi * Noisefv2 (2. * p.xy));
  return 2. * smoothstep (0.2, 0.35 + 0.3 * s, VorDist (0.03 * p));
}

float GrndRay (vec3 ro, vec3 rd)
{
  vec3 p;
  float dHit, h, s, sLo, sHi;
  s = 0.;
  sLo = 0.;
  dHit = dstFar;
  for (int j = 0; j < 120; j ++) {
    p = ro + s * rd;
    h = p.y - GrndHt (p.xz);
    if (h < 0.) break;
    sLo = s;
    s += max (0.3, 0.35 * h);
    if (s > dstFar) break;
  }
  if (h < 0.) {
    sHi = s;
    for (int j = 0; j < 5; j ++) {
      s = 0.5 * (sLo + sHi);
      p = ro + s * rd;
      if (p.y > GrndHt (p.xz)) sLo = s;
      else sHi = s;
    }
    dHit = 0.5 * (sLo + sHi);
  }
  return dHit;
}

vec3 GrndNf (vec3 p)
{
  float h;
  h = GrndHt (p.xz);
  vec2 e = vec2 (0.01, 0.);
  return normalize (vec3 (h - GrndHt (p.xz + e.xy), e.x,
     h - GrndHt (p.xz + e.yx)));
}

float FlyerDf (vec3 p, float dMin)
{
  vec3 q, qq;
  float d, wr;
  q = p;
  wr = -0.2 + q.z / fusLen;
  d = PrRoundBoxDf (q, vec3 (0.07 * (1. - 0.8 * wr * wr),
     0.11 * (1. - 0.6 * wr * wr), 1.) * fusLen, 0.05 * fusLen);
  q -= vec3 (0., 0.1, 0.3) * fusLen;
  d = max (d, - PrRoundBoxDf (q, vec3 (0.05, 0.1, 0.15) * fusLen,
     0.03 * fusLen)); 
  if (d < dMin) { dMin = min (dMin, d);  idObj = idObjGrp + idFus;  qHit = q; }
  q = p;  q -= vec3 (0., 0.08, 0.3) * fusLen;
  d = PrRoundBoxDf (q, vec3 (0.05, 0.02, 0.15) * fusLen, 0.03 * fusLen); 
  if (d < dMin) { dMin = min (dMin, d);  idObj = idObjGrp + idCkpit;  qHit = q; }
  q = p;  q.z = abs (q.z - 0.33 * fusLen) - 0.08 * fusLen;
  q -= vec3 (0., 0.17, 0.) * fusLen;
  d = PrSphDf (q, 0.04 * fusLen); 
  if (d < dMin) { dMin = min (dMin, d);  idObj = idObjGrp + idPlt;  qHit = q; }
  q = p - fusLen * vec3 (0., 0.03, 0.8);
  q.x = abs (q.x) - 0.1 * fusLen;
  d = PrCapsDf (q, 0.02 * fusLen, 0.15 * fusLen);
  if (d < dMin) { dMin = min (dMin, d);  idObj = idObjGrp + idPipe;  qHit = q; }
  q = p - fusLen * vec3 (0., 0.03, 1.05);
  d = PrCapsDf (q, 0.05 * fusLen, 0.02 * fusLen);
  if (d < dMin) { dMin = d;  idObj = idObjGrp + idNose;  qHit = q; }
  q = p - fusLen * vec3 (0., 0.09, 0.2);
  qq = q;  qq.y = abs (qq.y) - 0.21 * fusLen;
  wr = q.x / wSpan;
  d = PrFlatCylDf (qq.zyx, 0.24 * (1. - 0.2 * wr * wr) * fusLen,
     0.01 * (1. - 0.8 * wr * wr) * fusLen, wSpan);
  if (d < dMin) { dMin = min (dMin, d);  idObj = idObjGrp + idWing;  qHit = q; }
  q = p - fusLen * vec3 (0., 0.09, 0.25);
  q.xz = abs (q.xz) - fusLen * vec2 (0.5, 0.1);
  d = PrCylDf (q.xzy, 0.01 * fusLen, 0.21 * fusLen);
  if (d < dMin) { dMin = min (dMin, d);  idObj = idObjGrp + idStrut;  qHit = q; }
  q = p - fusLen * vec3 (0., 0.15, 0.25);
  q.x = abs (q.x) - 0.1 * fusLen;
  d = PrCylDf (q.xzy, 0.01 * fusLen, 0.15 * fusLen);
  if (d < dMin) { dMin = min (dMin, d);  idObj = idObjGrp + idStrut;  qHit = q; }
  float tSpan = 0.35 * wSpan;
  q = p - fusLen * vec3 (0., 0., - 0.9);
  wr = q.x / tSpan;
  d = PrFlatCylDf (q.zyx, 0.15 * (1. - 0.25 * wr * wr) * fusLen,
     0.007 * (1. - 0.2 * wr * wr) * fusLen, tSpan);
  q.x = abs (q.x);
  d = max (d, 0.02 * fusLen - 1.5 * q.x - q.z);
  if (d < dMin) { dMin = min (dMin, d);  idObj = idObjGrp + idHstab;  qHit = q; }
  float fSpan = 0.32 * wSpan;
  q = p - fusLen * vec3 (0., 0., - 0.87);
  q.yz = Rot2D (q.yz, 0.15);
  wr = q.y / fSpan;
  d = PrFlatCylDf (q.zxy, 0.15 * (1. - 0.3 * wr * wr) * fusLen,
     0.007 * (1. - 0.3 * wr * wr) * fusLen, fSpan);
  d = max (d, - q.y);
  if (d < dMin) { dMin = min (dMin, d);  idObj = idObjGrp + idFin;  qHit = q; }
  q = p - fusLen * vec3 (0., -0.25, 0.5);
  q.x = abs (q.x) - 0.14 * fusLen;
  q.xy = Rot2D (q.xy, -0.55);  q.yz = Rot2D (q.yz, 0.15);
  d = PrCylDf (q.xzy, 0.013 * fusLen, 0.12 * fusLen);
  if (d < dMin) { dMin = d;  idObj = idObjGrp + idLeg;  qHit = q; }
  q = p - fusLen * vec3 (0., -0.34, 0.515);
  q.x = abs (q.x) - 0.22 * fusLen;
  d = PrCylDf (q.yzx, 0.01 * fusLen, 0.035 * fusLen);
  if (d < dMin) { dMin = d;  idObj = idObjGrp + idAxl;  qHit = q; }
  q.x -= 0.01 * fusLen;
  d = PrCylDf (q.yzx, 0.1 * fusLen, 0.015 * fusLen);
  if (d < dMin) { dMin = d;  idObj = idObjGrp + idWhl;  qHit = q; }
  return dMin;
}

float ObjDf (vec3 p)
{
  float dMin;
  dMin = dstFar;
  idObjGrp = 1 * 256;
  dMin = FlyerDf (flyerMat[0] * (p - flyerPos[0]), dMin);
  idObjGrp = 2 * 256;
  dMin = FlyerDf (flyerMat[1] * (p - flyerPos[1]), dMin);
  return 0.9 * dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 100; j ++) {
    d = ObjDf (ro + dHit * rd);
    dHit += d;
    if (d < 0.001 || dHit > dstFar) break;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec3 e = vec3 (0.001, -0.001, 0.);
  v = vec4 (ObjDf (p + e.xxx), ObjDf (p + e.xyy), ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (vec3 (v.x - v.y - v.z - v.w) + 2. * v.yzw);
}

float PropelDf (vec3 p, float dMin)
{
  vec3 q;
  float d;
  q = p - fusLen * vec3 (0., 0.02, 1.07);
  d = PrCylDf (q, 0.3 * fusLen, 0.007 * fusLen);
  if (d < dMin) { dMin = d;  qHitTransObj = q; }
  return dMin;
}

float TransObjDf (vec3 p)
{
  float dMin;
  dMin = dstFar;
  dMin = PropelDf (flyerMat[0] * (p - flyerPos[0]), dMin);
  dMin = PropelDf (flyerMat[1] * (p - flyerPos[1]), dMin);
  return dMin;
}

float TransObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 30; j ++) {
    d = TransObjDf (ro + dHit * rd);
    dHit += d;
    if (d < 0.001 || dHit > dstFar) break;
  }
  return dHit;
}

vec4 FlyerCol (int ig, int id, vec3 n)
{
  vec3 col, qqHit, nn;
  float spec, b;
  spec = 0.2;
  qqHit = qHit / fusLen;
  col = (ig == 2) ? vec3 (1., 0., 0.) : vec3 (0., 1., 0.);
  if (id == idFus) {
    qqHit.yz -= vec2 (-0.1, -0.7);
    col = mix (vec3 (0.5, 0.5, 1.), col,
       (1. - SmoothBump (0.06, 0.09, 0.01, length (qqHit.yz))) *
       (1. - SmoothBump (-0.01, 0.03, 0.01, length (qqHit.yz))));
    if (n.z > 0.9 && qqHit.y < -0.03) col *= 0.3;
  } else if (id == idWing) {
    b = wSpan / (8. * fusLen);
    b = mod (qqHit.x + 0.5 * b, b) - 0.5 * b;
    col *= 1. + 0.1 * SmoothBump (-0.01, 0.01, 0.002, b);
    if (qqHit.y * n.y > 0.) {
      qqHit.x = abs (qqHit.x) - 0.8;
      qqHit.z -= 0.03;
      col *= mix (vec3 (0.5, 0.5, 1.), col,
         (1. - SmoothBump (0.08, 0.12, 0.01, length (qqHit.xz))) *
         (1. - SmoothBump (-0.01, 0.03, 0.01, length (qqHit.xz))));
    }
  } else if (id == idFin || id == idHstab) {
    col *= 1. - 0.6 * SmoothBump (-0.062, -0.052, 0.002, qqHit.z);
  } else if (id == idPipe || id == idNose) {
    col = vec3 (0.8, 0.8, 0.);
    spec = 0.4;
  } else if (id == idStrut || id == idLeg) {
    col = 0.6 * col + vec3 (0.4);
  } else if (id == idAxl) {
    col = vec3 (0.3, 0.2, 0.);
  } else if (id == idCkpit) {
    col = vec3 (0.2, 0.15, 0.05);
  } else if (id == idPlt) {
    col = vec3 (0.1, 0.07, 0.);
    if (n.z > 0.7) {
      col *= 2.;
      qqHit.x = abs (qqHit.x) - 0.015 * fusLen;
      col *= (1. - 0.9 * SmoothBump (0.003, 0.01, 0.001, length (qqHit.xy)));
    }
  } else if (id == idWhl) {
    if (length (qqHit.yz) < 0.07) col = vec3 (0.4, 0.4, 0.4);
    else {
      col = vec3 (0.02);
      spec = 0.;
    }
  }
  return vec4 (col, spec);
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.02 * fusLen;
  for (int j = 0; j < 30; j ++) {
    h = ObjDf (ro + rd * d);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += 0.03 * fusLen;
    if (sh < 0.05) break;
  }
  return 0.3 + 0.7 * sh;
}

float GrndSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.1;
  for (int j = 0; j < 30; j ++) {
    h = ObjDf (ro + rd * d);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += 0.15;
    if (sh < 0.05) break;
  }
  return 0.6 + 0.4 * sh;
}

vec3 SkyCol (vec3 ro, vec3 rd)
{
  vec3 col;
  float sd, f;
  rd.y = abs (rd.y);
  ro.xz += 2. * tCur;
  sd = max (dot (rd, sunDir), 0.);
  col = vec3 (0.1, 0.2, 0.5) + 0.2 * pow (1. - max (rd.y, 0.), 8.) +
     0.35 * pow (sd, 8.) + 0.65 * min (pow (sd, 256.), 0.3);
  f = Fbm2 (0.05 * (ro.xz + rd.xz * (50. - ro.y) / rd.y));
  col = mix (col, vec3 (1.), clamp (0.1 + 0.8 * f * rd.y, 0., 1.));
  return col;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4;
  vec3 vn, col, colW, roo;
  float dstObj, dstGrnd, dstWat, dstPropel, sh;
  int ig, id;
  bool isRefl;
  roo = ro;
  dstGrnd = GrndRay (ro, rd);
  dstWat = (rd.y < 0.) ? - ro.y / rd.y - 0.1 : dstFar;
  dstObj = ObjRay (ro, rd);
  dstPropel = TransObjRay (ro, rd);
  if (min (dstObj, dstGrnd) < dstPropel) dstPropel = dstFar;
  isRefl = false;
  if (dstWat < min (min (dstGrnd, dstObj), dstFar)) {
    ro += rd * dstWat;
    roo = ro;
    vn = VaryNf (0.8 * ro, vec3 (0., 1., 0.), 0.1);
    rd = reflect (rd, vn);
    ro += 0.1 * rd;
    dstGrnd = GrndRay (ro, rd);
    dstObj = ObjRay (ro, rd);
    isRefl = true;
  }
  if (dstObj < min (dstGrnd, dstFar)) {
    ro += rd * dstObj;
    vn = ObjNf (ro);
    ig = idObj / 256;
    id = idObj - 256 * ig;
    col4 = FlyerCol (ig, id, ((ig == 2) ? flyerMat[1] : flyerMat[0]) * vn);
    if (idObj == 256 + idWing || idObj == 256 + idHstab) {
      vn.yz = Rot2D (vn.yz, -0.6 * qHit.z / fusLen);
      vn = VaryNf (100. * ro, vn, 0.05);
    } else if (idObj == 256 + idFin) {
      vn.xz = Rot2D (vn.xz, -0.6 * qHit.z / fusLen);
    }
    sh = ObjSShadow (ro, sunDir);
    col = col4.rgb * (0.2 + 0.2 * max (dot (vn.xz, - normalize (sunDir.xz)), 0.) +
       0.6 * sh * max (dot (vn, sunDir), 0.)) +
       sh * col4.a * pow (max (0., dot (sunDir, reflect (rd, vn))), 128.);
  } else if (dstGrnd < dstFar) {
    ro += dstGrnd * rd;
    vn = VaryNf (2.3 * ro, GrndNf (ro), 2.);
    vn = VaryNf (15. * ro.zyx, vn, 0.5);
    sh = GrndSShadow (ro, sunDir);
    col = vec3 (1.);
    col = col * (0.1 + 0.2 * max (dot (vn.xz, - normalize (sunDir.xz)), 0.) +
       0.1 * max (vn.y, 0.) + 0.8 * sh * max (dot (vn, sunDir), 0.)) +
       sh * 0.3 * pow (max (0., dot (sunDir, reflect (rd, vn))), 128.);
    col = mix (col, SkyCol (roo, rd), max (pow (dstGrnd / dstFar, 4.) - 0.1, 0.));
  } else col = SkyCol (ro, rd);
  if (isRefl) col *= 0.95;
  if (dstPropel < dstFar) col = vec3 (0.1) * (1. - 0.3 * SmoothBump (0.25, 0.27,
     0.006, length (qHitTransObj.xy) / fusLen)) + 0.7 * col;
  return pow (clamp (col, 0., 1.), vec3 (0.8));
}

vec3 TrackPath (float z)
{
  return vec3 (30. * sin (0.035 * z) * sin (0.012 * z) * cos (0.01 * z) +
     26. * sin (0.0032 * z),
     4.5 + 2. * SmoothBump (0.4, 0.7, 0.1, mod (0.006 * z, 1.)), z);
}

void FlyerPM (float t, float vu)
{
  vec3 fpF, fpB, vel, acc, va, ort, cr, sr;
  float vy, dt, rlFac;
  dt = 0.2;
  flPos = TrackPath (t * flyVel);
  fpF = TrackPath ((t + dt) * flyVel);
  fpB = TrackPath ((t - dt) * flyVel);
  vel = (fpF - fpB) / (2. * dt);
  vy = vel.y;
  vel.y = 0.;
  acc = (fpF - 2. * flPos + fpB) / (dt * dt);
  acc.y = 0.;
  va = cross (acc, vel) / length (vel);
  vel.y = vy;
  rlFac = (vu == 0.) ? 0.3 : 0.5;
  ort = vec3 (0., atan (vel.z, vel.x) - 0.5 * pi,
     rlFac * length (va) * sign (va.y));
  cr = cos (ort);
  sr = sin (ort);
  flMat = mat3 (cr.z, - sr.z, 0., sr.z, cr.z, 0., 0., 0., 1.) *
     mat3 (1., 0., 0., 0., cr.x, - sr.x, 0., sr.x, cr.x) *
     mat3 (cr.y, 0., - sr.y, 0., 1., 0., sr.y, 0., cr.y);
}

vec3 GlareCol (vec3 rd, vec3 sd, vec2 uv)
{
  vec3 col;
  vec3 e = vec3 (1., 0., 0.);
  if (sd.z > 0.) col = 0.05 * pow (sd.z, 8.) *
     (1.5 * e.xyy * max (dot (normalize (rd + vec3 (0., 0.3, 0.)), sunDir), 0.) +
      e.xxy * SmoothBump (0.04, 0.07, 0.07, length (uv - sd.xy)) +
      e.xyx * SmoothBump (0.15, 0.2, 0.07, length (uv - 0.5 * sd.xy)) +
      e.yxx * SmoothBump (1., 1.2, 0.07, length (uv + sd.xy)));
  else col = vec3 (0.);
  return col;
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, vd, u, col;
  vec2 canvas, uv, ori, ca, sa;
  float az, el, f, s;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  sunDir = normalize (vec3 (1., 1., -1.));
  dstFar = 300.;
  fusLen = 0.7;
  wSpan = 1.2 * fusLen;
  flyVel = 5.;
  FlyerPM (tCur + 3., 1.);
  flyerPos[0] = flPos;  flyerPos[0].x -= 3.;
  flyerMat[0] = flMat;
  FlyerPM (tCur + 3.5, 1.);
  flyerPos[1] = flPos;  flyerPos[1].x += 3.;
  flyerMat[1] = flMat;
  rd = normalize (vec3 (uv, 2.));
  if (mPtr.z > 0.) {
    FlyerPM (tCur + 3.25, 0.);
    ro = flPos;
    az = clamp (-1.5 * pi * mPtr.x, - pi, pi);
    el = clamp (-1.3 * pi * mPtr.y, - 0.25 * pi, 0.25 * pi);
    ori = vec2 (el, az);
    ca = cos (ori);
    sa = sin (ori);
    vuMat = mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x) *
            mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y);
    rd = rd * vuMat * flMat;
  } else {
    s = mod (0.02 * tCur, 2.);
    FlyerPM (tCur + 4. * SmoothBump (0.3, 0.8, 0.15, mod (s, 1.)), 0.);
    ro = flPos;
    ro.y += sin (0.1 * tCur);
    vd = normalize (flyerPos[0] + SmoothBump (0.95, 1.95, 0.05, s) *
       (flyerPos[1] - flyerPos[0]) - ro);
    u = - vd.y * vd;
    f = 1. / sqrt (1. - vd.y * vd.y);
    vuMat = mat3 (f * vec3 (vd.z, 0., - vd.x), f * vec3 (u.x, 1. + u.y, u.z), vd);
    rd = vuMat * rd;
  }
  col = ShowScene (ro, rd) + GlareCol (rd, vuMat * sunDir, 1.5 * uv);
  fragColor = vec4 (col, 1.);
}

float PrSphDf (vec3 p, float s)
{
  return length (p) - s;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrCapsDf (vec3 p, float r, float h)
{
  return length (p - vec3 (0., 0., clamp (p.z, - h, h))) - r;
}

float PrFlatCylDf (vec3 p, float rhi, float rlo, float h)
{
  return max (length (p.xy - vec2 (rhi *
     clamp (p.x / rhi, -1., 1.), 0.)) - rlo, abs (p.z) - h);
}

float PrRoundBoxDf (vec3 p, vec3 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

vec2 Rot2D (vec2 q, float a)
{
  return q * cos (a) + q.yx * sin (a) * vec2 (-1., 1.);
}

const vec4 cHashA4 = vec4 (0., 1., 57., 58.);
const vec3 cHashA3 = vec3 (1., 57., 113.);
const float cHashM = 43758.54;

vec4 Hashv4f (float p)
{
  return fract (sin (p + cHashA4) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  const vec2 cHashVA2 = vec2 (37.1, 61.7);
  const vec2 e = vec2 (1., 0.);
  return fract (sin (vec2 (dot (p + e.yy, cHashVA2),
     dot (p + e.xy, cHashVA2))) * cHashM);
}

float Noisefv2 (vec2 p)
{
  vec2 i = floor (p);
  vec2 f = fract (p);
  f = f * f * (3. - 2. * f);
  vec4 t = Hashv4f (dot (i, cHashA3.xy));
  return mix (mix (t.x, t.y, f.x), mix (t.z, t.w, f.x), f.y);
}

float Fbm2 (vec2 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    f += a * Noisefv2 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n)) * (1. / 1.9375);
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  const vec3 e = vec3 (0.1, 0., 0.);
  g = vec3 (Fbmn (p + e.xyy, n), Fbmn (p + e.yxy, n), Fbmn (p + e.yyx, n)) -
     Fbmn (p, n);
  return normalize (n + f * (g - n * dot (n, g)));
}

