const float pi = 3.14159;

#define NBLK 12.

vec3 lbPos, vnBlk;
vec2 qBlk;
float dstFar, tCur, bEdge, tCyc;
int idObj;
bool doSh;
const int idGr = 1, idPln = 2, idConv = 3, idSup = 4, idAx = 5, idBas = 6,
   idWhl = 7, idSpl = 8, idBl = 9;

float GearWlDf (vec3 p, float rad, float wlThk, float tWid, float nt, float aRot, 
   bool bev, float dMin)
{
  vec3 q;
  float d, s;
  q = p;
  d = max (length (q.xy) - rad, abs (q.z) - wlThk);
  if (d < dMin) {
    q.xy = Rot2D (q.xy, aRot);
    q.xy = Rot2D (q.xy, floor (nt * atan (q.y, - q.x) / (2. * pi) + 0.5) *
       2. * pi / nt);
    if (bev) q.xy *= 1.2 - 0.2 * q.z / wlThk;
    s = q.x - 2. * clamp (1.5 * tWid + 0.5 * q.x * step (0., q.x) - abs (q.y),
      0., tWid);
    d = max (d, - rad - 0.95 * s);
  }
  return min (dMin, d);
}

vec4 BPos (float t)
{
  vec3 p;
  float a;
  t = mod (t, tCyc);
  if (t < 5.) {
    a = 0.;
    p = vec3 (-1.018 + 2.118 * t / 5., bEdge, 0.);
  } else if (t < 10.) {
    a = 0.5 * pi * (t - 5.) / 5.;
    p = vec3 (1.1, bEdge + 1. * sin (a), 1. - 1. * cos (a));
  } else if ( t < 15.) {
    a = 0.5 * pi;
    p = vec3 (1.1 - 2.118 * (t - 10.) / 5., 1. + bEdge, 1.);
  } else if (t < 17.5) {
    a = 0.5 * pi;
    p = vec3 (-1.018, 1. + bEdge, 1. - 1. * (t - 15.) / 2.5);
  } else {
    t -= 17.5;
    a = -0.5 * pi * t;
    p = vec3 (-1.018, 1. + bEdge - t * t, 0.);
  }
  return vec4 (p, a);
}

float GearDf (vec3 p)
{
  vec3 q;
  float dMin, angRot, wlThk, tWid, nt, rad;
  dMin = dstFar / 0.3;
  angRot = 0.1 * pi * tCur;
  rad = 0.3;
  wlThk = rad / 7.;
  tWid = rad / 10.;
  nt = 20.;
  dMin = GearWlDf (- (p - vec3 (-1.05, -0.21, 1.3)), rad, wlThk, tWid, nt,
     angRot, true, dMin);
  dMin = GearWlDf ((p - vec3 (-1.05 + 0.85 * rad, -0.21, 1.3 + 0.85 * rad)).yzx,
     rad, wlThk, tWid, nt, angRot + pi / nt, true, dMin);
  angRot = -0.1 * pi * tCur;
  rad = 0.39;
  wlThk = rad / 15.;
  tWid = rad / 16.;
  nt = 36.;
  dMin = GearWlDf ((p - vec3 (0.1, bEdge, 1.)).yzx, rad, wlThk, tWid, nt,
     angRot, false, dMin);
  dMin = GearWlDf (- (p - vec3 (0.1, -0.21, 1.555)).zyx, rad * 2./3., wlThk, tWid,
     nt * 2./3., angRot * 3./2., false, dMin);
  return dMin * 0.3;
}

float ObjDf (vec3 p)
{
  vec4 a4;
  vec3 q, bPos;
  float dMin, d, angRot, r, a;
  dMin = dstFar;
  q = p - vec3 (1.13 + bEdge, bEdge, 1.);
  r = length (q.yz);
  q.yz = Rot2D (q.yz, -0.5 * pi * tCur / 5.);
  a = (r > 0.) ? atan (q.z, - q.y) / (2. * pi) : 0.;
  q.yz = Rot2D (q.yz, 2. * pi * (floor (8. * a + 0.5)) / 8.);
  q.z = abs (q.z);
  d = max (min (min (abs (r - 1.01) - 0.1, r - 0.3),
     max (r - 1., dot (q.yz, vec2 (sin (0.8 * 2. * pi / 32.),
     cos (0.8 * 2. * pi / 32.))))), abs (q.x) - 0.03);
  if (d < dMin) { dMin = d;  idObj = idWhl; }
  d = PrOBoxDf (p - vec3 (0., 0.98, 1.), vec3 (1.12, 0.02, 0.1));
  d = min (d, PrOBoxDf (p - vec3 (-1.018, 0.98, 0.5), vec3 (0.1, 0.02, 0.5 - bEdge)));
  if (d < dMin) { dMin = d;  idObj = idPln; }
  d = PrFlatCylAnDf (p - vec3 (-0.05, -0.21, 0.), 1., 0.2, 0.01, 0.1);
  if (d < dMin) { dMin = d;  idObj = idConv; }
  q = p - vec3 (-0.05, -0.21, 0.);
  q.x = abs (q.x) - 1.;
  d = PrCylDf (q, 0.19, 0.12);
  if (d < dMin) { dMin = d;  idObj = idSpl; }
  q = p - vec3 (0.65, -0.14, 1.);
  q.x = abs (q.x) - 0.3;
  d = PrRoundBoxDf (q, vec3 (0.01, 1.08, 0.06), 0.02);
  q = p - vec3 (-0.05, -0.68, 0.);
  q.xz = abs (q.xz) - vec2 (1., 0.2);
  d = min (d, PrRoundBoxDf (q, vec3 (0.04, 0.55, 0.01), 0.02));
  d = min (d, PrRoundBoxDf (p - vec3 (-1.05, -0.14, 1.),
     vec3 (0.04, 1.08, 0.01), 0.02));
  q = p - vec3 (-0.33, -0.68, 1.555);
  q.x = abs (q.x) - 0.3;
  d = min (d, PrRoundBoxDf (q, vec3 (0.01, 0.55, 0.04), 0.02));
  if (d < dMin) { dMin = d;  idObj = idSup; }
  q = p - vec3 (0.65, bEdge, 1.);
  d = PrCylDf (q.yzx, 0.04, 0.62);
  d = min (d, PrCylDf (p - vec3 (0.95, -0.21, 0.), 0.03, 0.27));
  d = min (d, min (d, PrCylDf (p - vec3 (-1.05, -0.21, 0.55), 0.03, 0.82)));
  d = min (d, PrCylDf ((p - vec3 (-0.36, -0.21, 1.555)).yzx, 0.03, 0.51));
  if (d < dMin) { dMin = d;  idObj = idAx; }
  d = PrOBoxDf (p - vec3 (0., -1.2, 0.9), vec3 (1.6, 0.05, 1.4));
  if (d < dMin) { dMin = d;  idObj = idBas; }
  if (! doSh) {
    d = PrSphDf (p - lbPos, 0.04);
    if (d < dMin) { dMin = d;  idObj = idBl; }
  }
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 150; j ++) {
    d = ObjDf (ro + dHit * rd);
    dHit += d;
    if (d < 0.0005 || dHit > dstFar) break;
  }
  return dHit;
}

float GearRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 250; j ++) {
    d = GearDf (ro + dHit * rd);
    dHit += d;
    if (d < 0.0005 || dHit > dstFar) break;
  }
  return dHit;
}

vec3 GearNf (vec3 p)
{
  const vec3 e = vec3 (0.0005, -0.0005, 0.);
  vec4 v = vec4 (GearDf (p + e.xxx), GearDf (p + e.xyy),
     GearDf (p + e.yxy), GearDf (p + e.yyx));
  return normalize (vec3 (v.x - v.y - v.z - v.w) + 2. * v.yzw);
}

vec3 ObjNf (vec3 p)
{
  const vec3 e = vec3 (0.0005, -0.0005, 0.);
  vec4 v = vec4 (ObjDf (p + e.xxx), ObjDf (p + e.xyy),
     ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (vec3 (v.x - v.y - v.z - v.w) + 2. * v.yzw);
}

float BlkHit (vec3 ro, vec3 rd)
{
  vec4 a4;
  vec3 rm, rdm, u, v, tm, tp;
  float dMin, dn, df;
  dMin = dstFar;
  for (float k = 0.; k < NBLK; k ++) {
    a4 = BPos (tCur + tCyc * k / NBLK);
    rm = ro - a4.xyz;
    rdm = rd;
    rm.zy = Rot2D (rm.zy, a4.w);
    rdm.zy = Rot2D (rdm.zy, a4.w);
    v = rm / rdm;
    tp = bEdge / abs (rdm) - v;
    tm = - tp - 2. * v;
    dn = max (max (tm.x, tm.y), tm.z);
    df = min (min (tp.x, tp.y), tp.z);
    if (df > 0. && dn < min (df, dMin)) {
      dMin = dn;
      vnBlk = - sign (rdm) * step (tm.zxy, tm) * step (tm.yzx, tm);
      u = (v + dn) * rdm;
      qBlk = vec2 (dot (u.zxy, vnBlk), dot (u.yzx, vnBlk));
      vnBlk.zy = Rot2D (vnBlk.zy, - a4.w);
    }
  }
  return dMin;
}

float BlkHitSh (vec3 ro, vec3 rd, float rng)
{
  vec4 a4;
  vec3 rm, rdm, v, tm, tp;
  float dMin, dn, df;
  dMin = dstFar;
  for (float k = 0.; k < NBLK; k ++) {
    a4 = BPos (tCur + tCyc * k / NBLK);
    rm = ro - a4.xyz;
    rdm = rd;
    rm.zy = Rot2D (rm.zy, a4.w);
    rdm.zy = Rot2D (rdm.zy, a4.w);
    v = rm / rdm;
    tp = bEdge / abs (rdm) - v;
    tm = - tp - 2. * v;
    dn = max (max (tm.x, tm.y), tm.z);
    df = min (min (tp.x, tp.y), tp.z);
    if (df > 0. && dn < min (df, dMin)) dMin = dn;
  }
  return smoothstep (0., rng, dMin);
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.02;
  for (int j = 0; j < 50; j ++) {
    h = ObjDf (ro + rd * d);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += 0.03 + 0.03 * d;
    if (sh < 0.05) break;
  }
  return sh;
}

float GearSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.02;
  for (int j = 0; j < 30; j ++) {
    h = GearDf (ro + rd * d);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += 0.05;
    if (sh < 0.05) break;
  }
  return sh;
}

float GlowCol (vec3 ro, vec3 rd, float dstHit)
{
  vec3 ld;
  float d, wGlow;
  wGlow = 0.;
  ld = lbPos - ro;
  d = length (ld);
  ld /= d;
  if (d < dstHit) wGlow += pow (max (dot (rd, ld), 0.), 1024.);
  return clamp (0.5 * wGlow, 0., 1.);
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec3 roo, ltDir, vn, col, q;
  float dstObj, dstGear, dstBlk, spec, sh, s, r, a;
  int idObjT;
  bool isLit, isBg;
  tCyc = 18.5;
  bEdge = 0.08;
  spec = 0.1;
  doSh = false;
  dstObj = ObjRay (ro, rd);
  idObjT = idObj;
  isLit = true;
  isBg = false;
  roo = ro;
  dstGear = GearRay (ro, rd);
  if (dstGear < min (dstObj, dstFar)) {
    dstObj = dstGear;
    idObj = idGr;
  } else idObj = idObjT;
  dstBlk = BlkHit (ro, rd);
  if (dstBlk < min (dstObj, dstFar)) {
    dstObj = dstBlk;
    ro += rd * dstObj;
    vn = vnBlk;
    col = vec3 (1., 0., 0.);
    qBlk = abs (qBlk);
    if (max (qBlk.x, qBlk.y) > 0.8 * bEdge) {
      col = vec3 (0.9);
      spec = 0.5;
    }
  } else if (dstObj < dstFar) {
    ro += rd * dstObj;
    idObjT = idObj;
    if (idObj == idGr) vn = GearNf (ro);
    else vn = ObjNf (ro);
    idObj = idObjT;
    if (idObj == idWhl) {
      col = vec3 (0.9, 0.7, 0.3);
      q = ro - vec3 (1.1 + bEdge + 0.03, bEdge, 1.);
      r = length (q.yz);
      q.yz = Rot2D (q.yz, -0.5 * pi * tCur / 5.);
      a = (r > 0.) ? atan (q.z, - q.y) / (2. * pi) : 0.;
      a = mod (64. * a + 0.5, 1.);
      if (r > 0.99) vn.yz = Rot2D (vn.yz, - sin (a - 0.5));
      if (r > 0.92) col *= 0.7 + 0.3 * SmoothBump (0.05, 0.95, 0.01, a);
      spec = 0.5;
    } else if (idObj == idGr) {
      col = vec3 (0.8, 0.7, 0.3);
      spec = 0.5;
    } else if (idObj == idSpl) {
      if (abs (ro.z) > 0.1) {
        col = vec3 (0.8, 0.8, 0.85);
        spec = 0.5;
      } else col = vec3 (0.4, 0.4, 0.3);
    } else if (idObj == idAx) {
      col = vec3 (0.8, 0.8, 0.85);
      spec = 0.5;
    } else if (idObj == idPln) {
      col = (abs (vn.y) > 0.99) ? vec3 (0.5, 0.7, 0.2) : vec3 (0.5, 0.2, 0.7);
      spec = 0.;
    } else if (idObj == idConv) {
      q = ro - vec3 (-0.05, -0.21, 0.);
      col = vec3 (0.8, 0.8, 0.2);
      if (abs (q.x) < 1. && abs (vn.y) > 0.99 && sign (vn.y) != sign (q.y))
         col *= 1. - 0.9 * SmoothBump (0.45, 0.55, 0.03,
         mod (10. * (q.x - sign (q.y) * mod (tCur, 20.) * 2.1 / 5.), 1.));
    } else if (idObj == idSup) {
      col = vec3 (0.6, 0.4, 0.4);
    } else if (idObj == idBas) {
      q = ro;
      q.z -= 0.9;
      q.xz = abs (q.xz) - vec2 (1.55, 1.35);
      col = (max (q.x, q.z) > 0.) ? vec3 (0.2, 0.1, 0.) : vec3 (0., 0.1, 0.05);
      spec = 0.;
    } else if (idObj == idBl) {
      col = (0.7 + 0.5 * dot (rd, normalize (lbPos - ro))) * vec3 (1., 1., 0.7);
      isLit = false;
    }
  } else isBg = true;
  if (! isBg) {
    if (isLit) {
      ltDir = normalize (lbPos - ro);
      doSh = true;
      sh = min (ObjSShadow (ro, ltDir), GearSShadow (ro, ltDir));
      sh = 0.5 + 0.5 * min (sh, BlkHitSh (ro + 0.01 * ltDir, ltDir, 5.));
      col = col * (0.05 + 0.1 * max (vn.y, 0.) + 0.1 * max (- dot (vn, ltDir), 0.) +
         0.9 * sh * max (dot (vn, ltDir), 0.)) +
         spec * sh * pow (max (0., dot (ltDir, reflect (rd, vn))), 32.);
    } 
  } else col = vec3 (0., 0., 0.05) * (1. + 0.9 * rd.y);
  col = mix (col, vec3 (0.7, 0.7, 0.4), GlowCol (roo, rd, dstObj));
  col = pow (clamp (col, 0., 1.), vec3 (0.7));
  return col;
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd;
  vec2 canvas, uv, ori, ca, sa;
  float el, az;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  dstFar = 30.;
  az = 0.;
  el = -0.15 * pi;
  if (mPtr.z > 0.) {
    az += 2. * pi * mPtr.x;
    el += 0.7 * pi * mPtr.y;
  } else {
    az += 3. * pi * sin (0.01 * pi * tCur);
    el += 0.17 * pi * sin (0.07 * pi * tCur);
  }
  el = clamp (el, -0.4 * pi, 0.4 * pi);
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  vuMat = mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
          mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
  rd = vuMat * normalize (vec3 (uv, 4.));
  ro = vuMat * vec3 (0., 0., -8.);
  ro.z += 0.9;
  lbPos = vec3 (1.8 * cos (0.4 * tCur), 2.5 + 0.3 * sin (0.3 * tCur),
     0.9 + 1.6 * sin (0.4 * tCur));
  fragColor = vec4 (ShowScene (ro, rd), 1.);
}

float PrOBoxDf (vec3 p, vec3 b)
{
  return length (max (abs (p) - b, 0.));
}

float PrRoundBoxDf (vec3 p, vec3 b, float r)
{
  return length (max (abs (p) - b, 0.)) - r;
}

float PrSphDf (vec3 p, float s)
{
  return length (p) - s;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrFlatCylAnDf (vec3 p, float b, float r, float w, float h)
{
  p.x -= b * clamp (p.x / b, -1., 1.);
  return max (abs (length (p.xy) - r) - w, abs (p.z) - h);
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

vec2 Rot2D (vec2 q, float a)
{
  return q * cos (a) + q.yx * sin (a) * vec2 (-1., 1.);
}

// Edit the numbers below and press play:

// =========================================

// Shape: 0 = sphere, 1 = box, 2 = torus,
//        3 = union, 4 = substraction, 5 = intersection
int shape = 0;

// Infinite: 0 = no, 1 = yes
int infinite = 1;

// =========================================

// Distance Estimation function
float DE(vec3 p){

    // Infinite
    if(infinite == 1){
      p = mod(p, 2.0) - 0.5 * 2.0;
    }

    // Scaling based on a cosinus wave
    float scale = ((cos(iTime) + 2.5) / 2.7);
    p = p / scale;

    float r;

    // Sphere
    if(shape == 0){
      r = length(p) - 0.7;
    }

    // Box
    if(shape == 1){
      r = length(max(abs(p) - 0.5, 0.0));
    }

    // Torus
    if(shape == 2){
      vec2 q = vec2(length(p.xy) - 0.5, p.z);
      r = length(q)-0.1;
    }

    // Union
    if(shape == 3){
      r = min(length(p) - 0.7, length(max(abs(p) - 0.55, 0.0)));
    }

    // Substraction
    if(shape == 4){
      r = max(-(length(p) - 0.7), length(max(abs(p) - 0.55, 0.0)));
    }

    // Intersection
    if(shape == 5){
      r = max(length(p) - 0.7, length(max(abs(p) - 0.5, 0.0)));
    }

    // End of scale
    return r * scale;
}

// Main function (called for each pixel)
// Simple algorithm: all rays are parallel.
void mainImage(out vec4 fragColor,vec2 fragCoord){

    // Adjust to canvas size
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    // Create a ray that goes forward
    vec3 r = normalize(vec3(uv, 1.0));

    // The origin o is the camera's position
    vec3 o;

    // Infinite: Make the camera move
    if(infinite == 1){
        o = vec3(0.0, iTime, iTime);
    }

    // Not infinite: place the camera at (0,0,-2)
    if(infinite == 0){
        o = vec3(0.0, 0.0, -2.0);   
    }

    // Raymarching loop
    //float t = 0.0;
    //for(int i = 0; i < 99; ++i){
    //    t += DE(o + r * t) * 0.3;
    //}

    float t = 0.0;
    vec3 p;

    for(int i = 0; i < 99; i++)
    {
        p = (o + r * t);// * 0.3;
    	float d = DE(p);
        t += d;
        //if(d < .01){
        //    break;
        //}
    }

    // Fog
    float fog = 1.0 / (1.0 + t * t * 0.3);

    // Color the current pixel according to the fog
    fragColor = vec4(vec3(fog), 1.0);
}

#define ITER_L 20

#define number float
#define complex highp vec2
#define i_complex highp ivec2
#define mobius highp ivec4


complex thetransform[4];
complex passive_t[4];
highp number PI=3.14159265359;
highp number l = 1./3.*sqrt(3.);
highp number halfl;
highp int dcount=0;

highp int sides = 4;

highp number t = 0.;

complex conj(complex a) {
	return vec2(a.x, -a.y);
}

complex mul(complex a, complex b)
{
	return vec2(a.x*b.x - a.y*b.y, a.x*b.y + a.y*b.x);
}

complex expo(complex a)
{
	number l = exp(a.x);
	return l*vec2(cos(a.y), sin(a.y));
}

highp number abs_sq(complex xy)
{
	return xy.x*xy.x + xy.y*xy.y;
}

complex invert(complex xy)
{
	number a = abs_sq(xy);
	return (1./a)*conj(xy);
}


complex doshift(complex z, complex a) {
	return mul(z - a, invert(vec2(1, 0) - mul(conj(a), z)));
}

complex transform(complex z) {
	return mul(mul(z, thetransform[0]) + thetransform[1],
	    invert(mul(z, thetransform[2]) + thetransform[3]));
}

vec4 getpixel(complex pos) {
	pos = 1.05*pos;
	if (abs_sq(pos) >= 1.) return vec4(0.5, 0.5, 0.5, 0.5);
	//if (abs_sq(pos) <= 0.0002) return vec4(1, 0, 0, 1);
	//pos = transform(pos);
	int col = dcount;

	complex rv = expo(vec2(0, PI*2./float(sides)));
	complex dv = vec2(l, 0);
	int ctr = 0;
	int flipctr = 0;
	for (int i=0; i<ITER_L; i++) {
		dv = mul(dv, rv);
		complex newpos = doshift(pos, dv);
		if (abs_sq(newpos) >= abs_sq(pos)) {
			ctr++;
			if (ctr >= sides) break;
		} else {
			ctr = 0;
			pos = -newpos;
			if (i%2 == 0) flipctr++;
			col++;
		}
	}

	if ((col + flipctr) % 2 == 0) pos.x *= -1.;
	if (flipctr % 2 == 0) pos.y *= -1.;

	number shift = mod(t, 8.);
	if (shift >= 4.) {
		shift -= 4.;
		//pos = -pos;
	}
	if (shift >= 2.) {
		shift -= 2.;
		pos = vec2(pos.y, -pos.x);
		col++;
	}
    shift = 1. - cos(shift * PI * .5);
	shift -= 1.; shift *= halfl;
	pos = doshift(pos, vec2(halfl, 0));
	pos = doshift(pos, vec2(shift, 0));
	complex newpos = doshift(pos, vec2(-l, 0));
	if (abs_sq(newpos) < abs_sq(pos)) {
		col++;
		pos = newpos;
	}

	vec4 backgtiles = vec4(vec3(col%2), 1);
    vec4 thecolor = vec4(0,0,0,1);
	if (abs_sq(pos) <= halfl*halfl*0.5) thecolor.rgb = vec3(1, 1, 1);

	return 0.9*thecolor + 0.1*backgtiles;
}

void mainImage( out vec4 fragColor, in vec2 pos )
{
    halfl = (1. - sqrt(1. - l*l))/l;
    pos.xy -= max(vec2(0), iResolution.xy - iResolution.yx)/2.;
    pos = pos / min(iResolution.x, iResolution.y);
    pos = pos*2. - vec2(1,1);

    t = iTime * 1.4;
	fragColor = getpixel(pos);
}

#define E 40.0
#define PI 3.1415926535897932384626433832795

float random (in vec2 st) { 
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))
                 * 43758.5453123);
}

float noise (in vec2 st) {
    vec2 i = floor(st);
    vec2 f = fract(st);

    // Four corners in 2D of a tile
    float a = random(i);
    float b = random(i + vec2(1.0, 0.0));
    float c = random(i + vec2(0.0, 1.0));
    float d = random(i + vec2(1.0, 1.0));

    // Smooth Interpolation
    vec2 u = f*f*(3.0-2.0*f);

    // Mix 4 coorners porcentages
    return mix(a, b, u.x) + 
            (c - a)* u.y * (1.0 - u.x) + 
            (d - b) * u.x * u.y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = vec2(fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y ;

    uv *= 2.;

    float n = noise(uv);

	float t00 = sin (iTime);
	float t01 = sin (iTime+ 2.*PI/3.);
	float t02 = sin (iTime+4.*PI/3.);

	float t10 = cos (iTime);
	float t11 = cos (iTime+2.*PI/3.);
	float t12 = cos (iTime+4.*PI/3.);

	float t2 = sin (iTime/1.2);
	float t3 = cos (iTime/.8);

	vec2 p0 = vec2 (t00*t2, t10*t2) ;
	vec2 p1 = vec2 (t01*t2, t11*t2) ;
	vec2 p2 = vec2 (t02*t2, t12*t2) ;


	vec2 p3 = vec2 (t00*t3, t10*t3) ;
	vec2 p4 = vec2 (t01*t3, t11*t3) ;
	vec2 p5 = vec2 (t02*t3, t12*t3) ;

	float a = 1.0/distance (uv, p0);
	float b = 1.0/distance (uv, p1);
	float c = 1.0/distance (uv, p2);
	float d = 1.0/distance (uv, p3);
	float e = 1.0/distance (uv, p4);
	float f = 1.0/distance (uv, p5);

	float g = 1.0*n - pow (1.0/(a+b+c+d+e+f), E)*pow (15.0, E*0.7);

	vec4 col = vec4 (a*d, b*e, c*f, 1.0) * g;

    fragColor = mix(vec4(uv, 0.5+0.5*sin(iTime),1.0), col, 0.5);
}

#define NUM_PARTICLES 500.0
#define GLOW 0.4

vec3 Orb(vec2 uv, vec3 color, float radius, float offset)
{        
    float t = (iTime+200.)/5.;
    vec2 position = vec2(sin(offset * t)*sin(offset * t)+ sin(iTime),
                         cos(offset * t)*cos(offset * t)+ cos(iTime));

    if (sin(offset * t) < 0.&& cos(offset * t) > 0.) {

        position = vec2(-sin(offset * t)*sin(offset * t)+sin(iTime),
                         cos(offset * t)*cos(offset * t)+cos(iTime));
    } else if (cos(offset * t) < 0. && sin(offset * t) > 0.) {

        position = vec2(sin(offset * t)*sin(offset * t)+sin(iTime),
                         -cos(offset * t)*cos(offset * t)+cos(iTime));

    } else if (cos(offset * t) < 0. && sin(offset * t) < 0.) {

        position = vec2(-sin(offset * t)*sin(offset * t)+sin(iTime),
                         -cos(offset * t)*cos(offset * t)+cos(iTime));
    }

    position *= sin((t + offset )*2. )* offset;

    radius = radius * sqrt(abs(position.x * position.y)) ;

    float dist = radius / distance(uv, position);

    return color * pow(dist, 1.0 / GLOW);
}

    vec2 rotate(vec2 v, float alpha)
{
