// From Stackoveflow
// http://stackoverflow.com/questions/15095909/from-rgb-to-hsv-in-opengl-glsl
vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

// https://github.com/msfeldstein/glsl-map/blob/master/index.glsl
float map(float value, float inMin, float inMax, float outMin, float outMax) {
    return outMin + (outMax - outMin) * (value - inMin) / (inMax - inMin);
}

// My own additions to complex number math
#define cx_sub(a, b) vec2(a.x - b.x, a.y - b.y)
#define cx_add(a, b) vec2(a.x + b.x, a.y + b.y)
#define cx_abs(a) length(a)
vec2 cx_to_polar(vec2 a) {
    float phi = atan(a.y / a.x);
    float r = length(a);
    return vec2(r, phi); 
}

vec3 cx_exp(vec2 z) { 
    return vec3(exp(z.x), vec2(cos(z.y), sin(z.y)));
}

vec2 cx_exp_jk(vec2 z) { 
    return exp(z.x) * vec2(vec2(cos(z.y), sin(z.y)));
}

// Complex power
// Let z = r(cos Î¸ + i sin Î¸)
// Then z^n = r^n (cos nÎ¸ + i sin nÎ¸)
vec2 cx_pow(vec2 a, float n) {
    float angle = atan(a.y, a.x);
    float r = length(a);
    float real = pow(r, n) * cos(n*angle);
    float im = pow(r, n) * sin(n*angle);
    return vec2(real, im);
}

vec2 f(vec2 z) {
	return (7.0 * z + 2.0 - cx_mul(cx_cos(PI * z), (5.0 * z + 2.0))) / 4.0;
}

// Collatz and Self Similarity by Inigo Quilez
// https://www.youtube.com/watch?v=GJDz4kQqTV4
vec2 f3(vec2 z) {
    vec2 a = 7.0 * z + 2.0;

    //vec3 e = cx_exp(PI * z);
	//vec2 b = e.x * cx_mul(e.yz, (5.0 * z + 2.0));

    vec2 e = cx_exp_jk(PI * z);
	vec2 b = cx_mul(e, (5.0 * z + 2.0));

    return (a - b) / 4.0;
}

