  getSmallCells(e,In);//tiled cells
 vec2 z=animate(func(c.x,c.yz))*2.;
 float h=atan(z.y,z.x)/(2.*pi),l=length(z),
 s=abs(fract(l)-.5)-.25;s=step(0.,s)*s*4.;s=1.-s*s;
 vec2  r=abs(fract(z)-.5)-.25;r=step(0.,r)*r*4.;r=1.-r*r*r*r;
