void mainImage(out vec4 fragColor,vec2 fragCoord)
{
    float m 		= min(iResolution.x,iResolution.y);
    if(fragCoord.x >= m || fragCoord.y >= m)
    {
        // in order to speed up the computation,
        // directly put in black the pixels outside
        // the square m x m
        fragColor = vec4(0.0,0.0,0.0,1.0);
        return;
    }
	vec2 uv 		= (fragCoord.xy / m - vec2(0.5,0.5))*10.0;
    vec3 P 			= vec3(uv,-5.0);
    vec3 d      	= vec3(0.0,0.0,1.0);
    float alpha1	= iTime*TWOPI/13.0;
    float alpha2	= iTime*TWOPI/5.0;
    P.zx		   *= ROTATION(alpha1);
    P.xy		   *= ROTATION(alpha2);
    d.zx 		   *= ROTATION(alpha1);
    d.xy		   *= ROTATION(alpha2);

    float i = mod(iTime/3.0 /* change every 3 seconds */,16.0);
    float p,q;
    if(i <= 1.0) 		{p=2.0;q=3.0;}
    else if(i <= 2.0)	{p=2.0;q=5.0;}
    else if(i <= 3.0)	{p=2.0;q=7.0;}
    else if(i <= 4.0)	{p=3.0;q=2.0;}
    else if(i <= 5.0)	{p=3.0;q=4.0;}
    else if(i <= 6.0)	{p=3.0;q=5.0;}
    else if(i <= 7.0)	{p=3.0;q=7.0;}
    else if(i <= 8.0)	{p=4.0;q=3.0;}
    else if(i <= 9.0)	{p=4.0;q=5.0;}
    else if(i <= 10.0)	{p=4.0;q=7.0;}
    else if(i <= 11.0)	{p=5.0;q=2.0;}
    else if(i <= 12.0)	{p=5.0;q=3.0;}
    else if(i <= 13.0)	{p=5.0;q=4.0;}
    else if(i <= 14.0)	{p=5.0;q=6.0;}
    else if(i <= 15.0)	{p=5.0;q=7.0;}
	else				{p=6.0;q=5.0;}

    fragColor = compute(p,q,P,d);
} // mainImage()

const float hit_tol = 1e-5;

struct hit
{
    vec3 p;
    float d;
    bool hit;
    int mat;
};

struct ray
{
    vec3 s;
    vec3 d;
};

void reset(inout hit h)
{
    h.p = vec3(0.);
    h.d = 1e10;
    h.hit = false;
    h.mat = 0;
}

void sdf_sphere(inout hit h, vec3 center, float rad)
{
    float d = length(h.p - center) - rad;
    h.d = d;
}

void sdf_plane(inout hit h, vec3 pos, vec3 n)
{
    float d = dot(h.p - pos, n);
    h.d = d;
}

void sdf_cyl(inout hit h, vec3 pos, vec3 ax, float rad)
{
    vec3 rel = h.p - pos;
    float ap = dot(ax, rel);
    vec3 axp = ap * ax;
    float r = length(rel - axp);
    float d = r - rad;
    h.d = d;
}

void csg_intersect(inout hit h0, in hit h1)
{
    if (h0.d >= h1.d)
        return;
    h0 = h1;
}

void csg_subtract(inout hit h0, in hit h1)
{
    if (h0.d >= -h1.d)
        return;
    h0 = h1;
    h0.d = -h0.d;
}

void csg_union(inout hit h0, in hit h1)
{
    if (h0.d <= h1.d)
        return;
    h0 = h1;
}

void csg_blend(inout hit h0, in hit h1, float k)
{
    // in the case where h0.d and h1.d are the same, this is 0.5. As h0.d gets larger, this
    // decreases, and v.v. When the two diverge by more than k, this is either 0 or 1.
    float blend = clamp(0.5 + 0.5 * (h1.d - h0.d) / k, 0.0, 1.0);
    // when blend is 0.5, this works out to d - k / 4; you get a radius of k / 4
    // when you're exactly the same distance from the two hits.
    float d = mix(h1.d, h0.d, blend) - k * blend * (1.0 - blend);
    h0.d = d;
}

void sdf(inout hit h, vec3 pos)
{
    h.p = pos;

    hit h0, h1;
    h0.p = pos;
    h1.p = pos;

    sdf_sphere(h, vec3(0., 0., 10.), 2.5 + 0.4 * sin(1.4 * iTime));

    sdf_cyl(h0, vec3(0., 0., 10.), vec3(sin(iTime), 0., cos(iTime)), 1.);
	sdf_sphere(h1, vec3(0., 0., 10.), 4.5);
    csg_intersect(h0, h1);

    csg_blend(h, h0, 1.); h.mat = 1;

    sdf_plane(h0, vec3(-4., 0., 0.), vec3(1., 0., 0.)); h0.mat = 0; csg_union(h, h0);
    sdf_plane(h0, vec3(4., 0., 0.), vec3(-1., 0., 0.)); h0.mat = 0; csg_union(h, h0);
    sdf_plane(h0, vec3(0., -3., 0.), vec3(0., 1., 0.)); h0.mat = 2; csg_union(h, h0);

    h.hit = h.d < hit_tol;
}

void send(inout hit h, ray r)
{
    float t = 0.5;
    for (int i = 0; i < 128; i++)
    {
        sdf(h, r.s + t * r.d);
        if (h.hit)
        	return;
        t += h.d;
        continue;
    }
    h.hit = false;
}

float shadow(ray r, vec3 light)
{
    float t = 0.5;
    float shadow = 1.;
    float dist = length(light - r.s);
    hit lh;
    for (int i = 0; i < 64; i++)
    {
        if (t >= dist)
            break;
        reset(lh);
        sdf(lh, r.s + t * r.d);
        if (lh.hit)
            return 0.;
        t += lh.d;
        shadow = min(shadow, 8. * lh.d / t);
    }
    return clamp(shadow, 0., 1.);
}

float ao(in hit h, vec3 n)
{
	float occ = 0.0;
    float sca = 1.0;
    hit aoh;
    for (int i = 0; i < 8; i++)
    {
        float hr = 0.6 * float(i) / 5.0 + 0.01;
        vec3 aopos = n * hr + h.p;

        reset(aoh);
        sdf(aoh, aopos);

        occ += (aoh.d - hr) * -sca;
        sca *= 0.90;
    }
    return clamp(1.0 - 0.2 * occ, 0.0, 1.0 );    
}

vec4 light(hit h, vec3 n, vec3 light, vec4 color)
{
    ray r;
    r.d = normalize(light - h.p);
    r.s = h.p;
    float sh = shadow(r, light);

    vec4 diffuse = clamp(dot(n, r.d), 0., 1.) * color;

    vec3 refl = normalize(2. * dot(r.d, n) * n - r.d);
    vec3 view = normalize(-h.p);
    float spec = h.mat == 1
        ? pow(clamp(dot(view, refl), 0., 1.), 10.)
        : 0.;
    vec4 specc = spec * vec4(1., 1., 1., 1.);

    return clamp(sh * (diffuse + spec),
                 vec4(0., 0., 0., 1.),
                 vec4(1., 1., 1., 1.));
}

vec3 normal(in vec3 pos)
{
    vec2 e = vec2(1.0, -1.0) * 0.5773 * 0.0005;
    vec3 n = vec3(0., 0., 0.);
    hit h;
    sdf(h, pos + e.xyy); n += e.xyy * h.d;
    sdf(h, pos + e.yyx); n += e.yyx * h.d;
    sdf(h, pos + e.yxy); n += e.yxy * h.d;
    sdf(h, pos + e.xxx); n += e.xxx * h.d;
    return normalize(n);
}

void mainImage(out vec4 color, in vec2 fragCoord)
{
    float mw = max(iResolution.x, iResolution.y);
    vec2 uv = 2.0 * (fragCoord.xy - iResolution.xy / 2.0) / mw;
    ray r;
    r.s = vec3(-uv, -0.8);
    r.d = normalize(-r.s);

    hit h;
    reset(h);
    send(h, r);
    if (!h.hit)
    {
        color = vec4(0.08, 0, 0.1, 1.0);
    }
    else
    {
        vec4 mat =
            h.mat == 0 ? vec4(0., .6, 1., 1.) :
            h.mat == 1 ? vec4(1., 1., 1., 1.) : 
        	h.mat == 2 ? vec4(0., .6, .9, 1.) :
        	vec4(0.);
        vec4 amb = vec4(0.05, 0.03, 0.01, 1.0);
        vec3 norm = normal(h.p);
        vec4 dif = light(h, norm, vec3(0, 10., 7.), vec4(1., .3, .6, 1.));
        vec4 dif2 = light(h, norm, vec3(2., 7., 4.), vec4(0.4, 0.4, 0.1, 1.));
        float aos = ao(h, norm);
        color = (amb + aos * (dif + dif2)) * mat;
    }

    float fog = pow(clamp(length(h.p - r.s) / 40., 0., 1.), 1.2);
    color =
        (1. - fog) * color +
        fog * vec4(0.7, 0.66, 0.68, 1.);
}

//a sphere intersection function from iq
//https://www.shadertoy.com/view/4djSDy
vec2 sphIntersect( in vec3 ro, in vec3 rd)
{

	float b = dot( ro, rd );
	float c = dot( ro, ro )-81.0;
	float h = b*b - c;
	if( h<0.0 ) return vec2(-1.0,-2.0);
	return vec2(-b - sqrt( h ),-b+sqrt(h));
}

vec4 insidebox(vec3 pos, vec3 dir) {
    vec3 lens = (1.0-pos*sign(dir))/abs(dir)*step(abs(pos),vec3(1.0));
    float len = min(min(lens.x,lens.y),lens.z);
    return vec4(len,lens);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy * 2.0 - iResolution.xy) / iResolution.y;

    vec3 pointlight = vec3(4.0,0.0,-10.0);

    vec3 pos = vec3(cos(iTime)*3.0,sin(iTime)*3.0,-20.0);
    vec3 dir = normalize(vec3(uv,1.5));

    vec2 len = sphIntersect(pos,dir);
   	pos += len.x*dir;
    vec3 normal = normalize(pos);
    vec4 len2 = insidebox(mod(pos,2.4)-1.2, dir);
    if (len2.x > 0.0) {
        normal = -step(len2.yzw,len2.xxx)*sign(dir);
    }
    if(len.y > len.x) {
        fragColor = vec4(abs(normal),1.0);

        vec3 lightnorm = (pointlight-pos);
        float lightdist = length(lightnorm);
        lightnorm /= lightdist;

        fragColor = vec4(max(dot(normal,lightnorm),0.1));

    } else {

		fragColor = vec4(0.0,0.0,sqrt(uv.y*0.25+0.25),1.0);
        return;
    }

	//fragColor = vec4(fract(pos+0.001),1.0);
}

