        // material        
		col = 0.45 + 0.35*sin( vec3(0.05,0.08,0.10)*(m-1.0) );
        if( m<1.5 )
        {

            float f = mod( floor(5.0*pos.z) + floor(5.0*pos.x), 2.0);
            col = 0.3 + 0.1*f*vec3(1.0);
        }

        // lighitng        
        float occ = calcAO( pos, nor );
		vec3  lig = normalize( vec3(-0.4, 0.7, -0.6) );
		float amb = clamp( 0.5+0.5*nor.y, 0.0, 1.0 );
        float dif = clamp( dot( nor, lig ), 0.0, 1.0 );
        float bac = clamp( dot( nor, normalize(vec3(-lig.x,0.0,-lig.z))), 0.0, 1.0 )*clamp( 1.0-pos.y,0.0,1.0);
        float dom = smoothstep( -0.1, 0.1, ref.y );
        float fre = pow( clamp(1.0+dot(nor,rd),0.0,1.0), 2.0 );
		float spe = pow(clamp( dot( ref, lig ), 0.0, 1.0 ),16.0);

        dif *= softshadow( pos, lig, 0.02, 2.5 );
        dom *= softshadow( pos, ref, 0.02, 2.5 );

		vec3 lin = vec3(0.0);
        lin += 1.30*dif*vec3(1.00,0.80,0.55);
		lin += 2.00*spe*vec3(1.00,0.90,0.70)*dif;
        lin += 0.40*amb*vec3(0.40,0.60,1.00)*occ;
        lin += 0.50*dom*vec3(0.40,0.60,1.00)*occ;
        lin += 0.50*bac*vec3(0.25,0.25,0.25)*occ;
        lin += 0.25*fre*vec3(1.00,1.00,1.00)*occ;
		col = col*lin;

    	col = mix( col, vec3(0.8,0.9,1.0), 1.0-exp( -0.0002*t*t*t ) );
    }

	return vec3( clamp(col,0.0,1.0) );
}

mat3 setCamera( in vec3 ro, in vec3 ta, float cr )
{
	vec3 cw = normalize(ta-ro);
	vec3 cp = vec3(sin(cr), cos(cr),0.0);
	vec3 cu = normalize( cross(cw,cp) );
	vec3 cv = normalize( cross(cu,cw) );
    return mat3( cu, cv, cw );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 mo = iMouse.xy/iResolution.xy;
	float time = 15.0 + iTime;


    vec3 tot = vec3(0.0);
#if AA>1
    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (-iResolution.xy + 2.0*(fragCoord+o))/iResolution.y;
#else    
        vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;
#endif

		// camera	
        vec3 ro = vec3( -0.5+3.5*cos(0.1*time + 6.0*mo.x), 1.0 + 2.0*mo.y, 0.5 + 4.0*sin(0.1*time + 6.0*mo.x) );
        vec3 ta = vec3( -0.5, -0.4, 0.5 );
        // camera-to-world transformation
        mat3 ca = setCamera( ro, ta, 0.0 );
        // ray direction
        vec3 rd = ca * normalize( vec3(p.xy,2.0) );

        // render	
        vec3 col = render( ro, rd );

		// gamma
        col = pow( col, vec3(0.4545) );

        tot += col;
#if AA>1
    }
    tot /= float(AA*AA);
#endif


    fragColor = vec4( tot, 1.0 );
}

void mainImage( out vec4 O, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.y;
    float t = iTime * 1.7;
    t += fract(sin(uv.x+1.0))*2.0;
    float e = 1.0+sin(uv.x*3.0)*2.6;
    float k =  cos(t-e) + 7.0;
    O = vec4( abs( cos(t + sin(0.5 * t + (uv.x+t*0.001) * k) ) ));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord.xy/iResolution.xy;
    vec3 color = vec3(0.0);

    float left = - smoothstep( cos(iTime) / 2.0, cos(iTime) / 2.0 + 0.7, st.x) + 1.0;
    float top = cos(st.x);
    float bottom = cos(st.y * 1000.0 * cos(iTime/2.0));

    color = top * bottom * left * vec3( cos(iTime)+st.x , cos(iTime)+ st.y, 1.0 ); 
    fragColor = vec4(color,1.0);

}

struct result{
 float dist;
 vec3 color;
};

// ray marching
const int max_iterations = 256;
const float stop_threshold = 0.001;
const float grad_step = 0.02;
const float clip_far = 1000.0;

// math
const float PI = 3.14159265359;
const float DEG_TO_RAD = PI / 180.0;

float myTime;

vec3 getColor(float _material){
     	if(_material == 0.0){
         	return vec3(1, 0, 1);   
        }
        else  if(_material == 1.0){
         	return  vec3(1, 1, 0);   
        }
        else if(_material == 2.0){
         	return vec3(1, 0, 0);   
        }
        else if(_material == 3.0){
         	return vec3(0, 1, 0);   
        }
        else if(_material == 4.0){
         	return vec3(0, 0, 2);   
        }
       else if(_material == 5.0){
         	return vec3(0.2, 1, 0.5);   
        }

    return vec3(0);
}


// iq's distance function
result sdSphere( vec3 pos, float r, float _m ) {
	return result( length( pos ) - r, getColor(_m)) ;
}

result sdBox( vec3 p, vec3 b, float _m ) {
  vec3 d = abs(p) - b;
  return result(min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0)), getColor(_m));
}



result minWithMaterial(float _d1, float _m1, float _d2, float _m2){
    if(_d1 < _d2){
     return result(_d1, getColor(_m1));   
    }
    return result(_d1, getColor(_m2));
}


result minWithMaterial(vec2 _o1, vec2 _o2){
    return minWithMaterial(_o1.x, _o1.y, _o2.x, _o2.y);
}

result minWithMaterial(float _d1, float _m1, vec2 _o2){
    return minWithMaterial(_d1, _m1, _o2.x, _o2.y);
}

result minWithMaterial(vec2 _o1, float _d2, float _m2){
    return minWithMaterial(_o1.x, _o1.y, _d2, _m2);
}

result minWithMaterial(result a, result b){
    if(a.dist < b.dist){
        return a;
    }
    return b;
}



float k = 10.0;
result smin( float _d1, float _m1, float _d2, float _m2, bool blend)
{
    float res = exp( -k*_d1 ) + exp( -k*_d2 );
    float d = -log( res )/k;
    return result(d, abs(_d1-d) > abs(_d2-d) ? getColor(_m2) : getColor(_m1));
}

result smin( vec2 a, vec2 b, bool blend )
{
    return smin(a.x, a.y,b.x, b.y, blend);
}

result smin( vec2 a, float b, float _m2, bool blend )
{
    return smin(a.x, a.y,b, _m2, blend);
}

result smin( float a, float _m1, vec2 b, bool blend )
{
    return smin(a, _m1,b.x, b.y, blend);
}

result smin(result a, result b, bool blend){   

    float res = exp( -k*a.dist ) + exp( -k*b.dist );
    float d = -log( res )/k;    
    vec3 color;

    if( abs(a.dist-d) > abs(b.dist-d) ){
    	 color =  (a.color*(1.0-a.dist/(a.dist + b.dist))) + (b.color*(a.dist/(a.dist + b.dist)));    
    }
    else{ 
        color =  (b.color*(1.0-b.dist/(a.dist + b.dist))) + (a.color*(b.dist/(a.dist + b.dist)));    
    }

    if(blend){ 
        return result(d, color);    
    }
    else{
        return result(d, abs(a.dist-d) > abs(b.dist-d) ? b.color :a.color);    
    }
}

result smin(result a, result b){ 
    return smin(a,b,false);  
}

result myMax( float _d1, float _m1, float _d2, float _m2 )
{
    if(_d1 > _d2){
        return result(_d1, getColor(_m1));
    }
    return result(_d2, getColor(_m2));
}

result myMax( vec2 a, vec2 b )
{    
    return myMax(a.x, a.y, b.x, b.y);
}

result myMax( vec2 a, float b, float _m2 )
{
    return myMax(a.x, a.y,b, _m2);
}

result myMax( float a, float _m1, vec2 b )
{
    return myMax(a, _m1,b.x, b.y);
}

result myMax(result a, result b){   
    if(a.dist > b.dist)
 		return  result(a.dist, (a.color*(1.0-a.dist/(a.dist + b.dist))) + (b.color*(a.dist/(a.dist + b.dist))));
    return  result(b.dist, (b.color*(1.0-b.dist/(a.dist + b.dist))) + (a.color*(b.dist/(a.dist + b.dist))));    
}

// get distance in the world
result dist_field( vec3 pos ) {

    result box = sdBox( pos, vec3(0.5) + sin(myTime*2.0)/35.0, 4.0);    
    box = myMax( box.dist, 4.0, -sdSphere( pos, 0.6, 0.0).dist +  cos(myTime*3.0)/35.0, 4.0 );

    result sphereInside = sdSphere( pos + vec3( sin(myTime*4.0) ,0.0,0.0), 0.25 ,1.0) ;    

    result boxVerticalInside =  sdBox( pos + vec3(0.0, cos(myTime*4.0),0.0), vec3(0.25),2.0) ;
    boxVerticalInside = minWithMaterial(boxVerticalInside,sdSphere( pos + vec3(0.0, cos(myTime*4.0),0.0), 0.3 + sin(myTime*5.0)*0.015 ,3.0));    

    result boxHorizontalLoop =  sdBox( pos + vec3(cos(myTime*4.0),0.0, sin(myTime*4.0)), vec3(0.25) ,3.0) ;


    boxHorizontalLoop = myMax(boxHorizontalLoop.dist,boxHorizontalLoop.color.x, -sdSphere( pos + vec3(cos(myTime*4.0),0.0, sin(myTime*4.0)), 0.3 + sin(myTime*5.0)*0.015, 0.3 ).dist, 0.3);        

    result sphereVerticalLoop =  sdBox( pos + vec3(0.0, sin(myTime*4.0), cos(myTime*4.0)), vec3(0.25) ,4.0) ;
    sphereVerticalLoop = myMax(sphereVerticalLoop.dist, 5.0, sdSphere( pos + vec3(0.0, sin(myTime*4.0), cos(myTime*4.0)), 0.3 + sin(myTime*5.0)*0.015, 0.3 ).dist, 0.3);    


  	return smin(box, smin(sphereInside, smin(boxVerticalInside, smin(boxHorizontalLoop,sphereVerticalLoop))));

    //return minWithMaterial(box, minWithMaterial(sphereInside, minWithMaterial(boxVerticalInside, minWithMaterial(boxHorizontalLoop,sphereVerticalLoop))));
}

// get gradient in the world
vec3 gradient( vec3 pos ) {
	const vec3 dx = vec3( grad_step, 0.0, 0.0 );
	const vec3 dy = vec3( 0.0, grad_step, 0.0 );
	const vec3 dz = vec3( 0.0, 0.0, grad_step );
	return normalize (
		vec3(
			dist_field( pos + dx ).dist - dist_field( pos - dx ).dist,
			dist_field( pos + dy ).dist - dist_field( pos - dy ).dist,
			dist_field( pos + dz ).dist - dist_field( pos - dz ).dist			
		)
	);
}


// phong shading
vec3 shading( vec3 v, vec3 n, vec3 eye, vec3 _material ) {
	// ...add lights here...

	float shininess = 16.0;

	vec3 final = vec3( 0.0 );

	vec3 ev = normalize( v - eye );
	vec3 ref_ev = reflect( ev, n );

	// light 0
	{
		vec3 light_pos   = vec3( 20.0, 20.0, 20.0 );
		vec3 light_color = vec3( 1.0, 0.7, 0.7 );

		vec3 vl = normalize( light_pos - v );

		float diffuse  = max( 0.0, dot( vl, n ) );
		float specular = max( 0.0, dot( vl, ref_ev ) );
		specular = pow( specular, shininess );

		final += light_color * ( diffuse + specular ); 
	}

	// light 1
	{
		vec3 light_pos   = vec3( -20.0, -20.0, -20.0 );
		vec3 light_color = vec3( 0.3, 0.7, 1.0 );

		vec3 vl = normalize( light_pos - v );

		float diffuse  = max( 0.0, dot( vl, n ) );
		float specular = max( 0.0, dot( vl, ref_ev ) );
		specular = pow( specular, shininess );

		final +=  (_material*0.5) + light_color * ( diffuse + specular ); 
	}

	return final;
}


// ray marching
result ray_marching( vec3 origin, vec3 dir, float start, float end ) {    	
    float depth = start;  
	for ( int i = 0; i < max_iterations; i++ ) {        
        vec3 p = origin + dir * depth;        

        result evaluate = dist_field( p );        

		float dist =evaluate.dist / length( gradient( p ) );

		if ( abs( dist ) < stop_threshold ) {
			return result(depth, evaluate.color);
		}
		depth += dist * 0.9;
		if ( depth >= end) {
			return result(end, evaluate.color);
		}

	}

	return result(end, getColor(-1.0));
}



// get ray direction
vec3 ray_dir( float fov, vec2 size, vec2 pos ) {
	vec2 xy = pos - size * 0.5;

	float cot_half_fov = tan( ( 90.0 - fov * 0.5 ) * DEG_TO_RAD );	
	float z = size.y * 0.5 * cot_half_fov;

	return normalize( vec3( xy, -z ) );
}


// camera rotation : pitch, yaw
mat3 rotationXY( vec2 angle ) {
	vec2 c = cos( angle );
	vec2 s = sin( angle );

	return mat3(
		c.y      ,  0.0, -s.y,
		s.y * s.x,  c.x,  c.y * s.x,
		s.y * c.x, -s.x,  c.y * c.x
	);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    

        myTime = iTime/2.0;


	// default ray dir
	vec3 dir = ray_dir( 45.0, iResolution.xy, fragCoord.xy );

	// default ray origin
	vec3 eye = vec3( 0.0, 0.0, 5.0 +sin(myTime) *0.0 );


	// rotate camera
	mat3 rot = rotationXY( ( iMouse.xy - iResolution.xy * 0.5 ).yx * vec2( 0.01, -0.01 ) );
	dir = rot * dir;
	eye = rot * eye;

    // ray marching
	result depth = ray_marching( eye, dir, 0.0, clip_far );          

	if (  depth.dist >= clip_far ) {
		fragColor = vec4( 0.3, 0.4, 0.5, 1.0 );
        return;
	}

	// shading
	vec3 pos = eye + dir * depth.dist;
	vec3 n = gradient( pos );
	fragColor = vec4( shading( pos, n, eye, depth.color ), 1.0 );

}

#define tH (iTime*0.5)
#define tD (iTime*5.0)
