//------------------------------------------------------------------------>
//	sqrt(a^2 + b^2) = c == radius = length(a,b)
float rr(vec2 uv){ 
    return (length(uv));
    //	   (sqrt((uv.x*uv.x)+(uv.y*uv.y)));
}
//  atan(a/b) = phi == angle
float phi(vec2 uv){ return atan(uv.x/uv.y);}
//  atan(b/a) = O == ?? 
float O(vec2 uv){ return atan(uv.y/uv.x);}
//------------------------------------------------------------------------>
// deform UV
vec2 v1(vec2 uv){return vec2(sin(uv.x),sin(uv.y));}
vec2 v2(vec2 uv){float r = rr(uv);r*=r;return (1.0/(r))*uv;}
vec2 v3(vec2 uv){float r = rr(uv);r*=r; return vec2(uv.x*sin(r)-uv.y*cos(r),uv.x*cos(r)-uv.y*sin(r));}
vec2 v4(vec2 uv){float r = rr(uv);return vec2((1.0/r)*(uv.x-uv.y)*(uv.x+uv.y),2.0*uv);}
vec2 v5(vec2 uv){float r = rr(uv);float o = O(uv); return vec2(o/PI,r-1.0);}
vec2 v6(vec2 uv){float r = rr(uv);float o = O(uv); return r*vec2(sin(o+r),cos(o-r));}
vec2 v7(vec2 uv){float r = rr(uv);float o = O(uv); return r*vec2(sin(o*r),-cos(o*r));}
vec2 v8(vec2 uv){float r = rr(uv);float o = O(uv); return (o/PI)*vec2(sin(PI*r),cos(PI*r));}
vec2 v9(vec2 uv){float r = rr(uv);float o = O(uv); return (1.0/r)*vec2(cos(o)+sin(r),sin(o)-cos(r));}
vec2 v10(vec2 uv){float r = rr(uv);float o = O(uv); return vec2(sin(o)/r,r*cos(o));}
vec2 v11(vec2 uv){float r = rr(uv);float o = O(uv); return vec2(sin(o)*cos(r),cos(o)*sin(r));}
vec2 v12(vec2 uv){float r = rr(uv);float o = O(uv);	return vec2(sin(r) / o, cos(o) / r); }
vec2 v13(vec2 uv){float r = rr(uv);float o = O(uv);	return vec2(sin(ceil(r) / o), cos(ceil(o) / r)); }
vec2 v14(vec2 uv){float r = rr(uv);float o = O(uv);	return vec2(r - o, o - r); }
vec2 v15(vec2 uv){float r = rr(uv);float o = O(uv);	return uv / vec2(cos(r / o * PI), sin(o / r * PI)); }
vec2 v16(vec2 uv){float r = rr(uv);float o = O(uv);	return uv * vec2(sin(r / o * PI), sin(o / r * PI)); }
