// mandelbrot whit diffrent break conditions
float fractal(vec2 p, int ma){
    vec2 f = p;
    for (int i=0;i<ma;i++){
    	float x = f.x*f.x - f.y*f.y + p.x;
        float y = f.x*f.y + f.x*f.y + p.y;
        //if(x*y*p.x*p.y>=4.0){return 1.0;}
        //if(length(f)>4.0){return 1.0;}
        //if(length(f)>4.0){break;}
        //if(dot(f,f)>=4.0){break;}
    	f = vec2(x,y);
    }
    f*=mat2(cos(tD),sin(tD),-sin(tD),cos(tD)); // rotate the Mandelbrot
	return (sKey>0.5) ? ((aKey>0.5) ? 1.0-length(f*p)*0.9
                         			: 1.0-length(p-f)*0.9 )
        			  : ((aKey>0.5) ? f.x/f.y-length(f)*9.0
                         			: f.x*f.y-length(f)*2.0 );// *2 = clear // *0 = not clear
}
// Shuffle the iChannel0 on the mandelbrot
vec4 fractalTex(vec2 p, int ma){
    vec2 f = p;

    for (int i=0;i<ma;i++){
    	float x = f.x*f.x - f.y*f.y + p.x;
        float y = f.x*f.y + f.x*f.y + p.y;
        //if(x*y*p.x*p.y>=4.0){return vec4(0.0,0.0,0.0,1.0);}
        //if(length(f)>4.0){return vec4(0.0,0.0,0.0,1.0);}
        //if(length(f)>3.0){break;}
        if(dot(f,f)>4.0){break;}
        f = vec2(x,y);
    }
    f*=-mat2(cos(tH),sin(tH),-sin(tH),cos(tH));
	return texture(iChannel0, f);
}
