void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	time = iTime * 0.5;
    vec2 _p = (-iResolution.xy + 2.0*fragCoord.xy) / iResolution.y;
    vec3 ray = normalize(vec3(_p, 1.0));
    vec3 cam = vec3(0.0, 0.0, 0.0);
    bool hit = false;
    getCamPos(cam, ray);

    float depth = 0.0, d = 0.0, iter = 0.0;
    vec3 p;

    for( int i = 0; i < 80; i ++)
    {
    	p = depth * ray + cam;
        d = map(p);

        if (d < 0.001) {
			hit = true;
            break;
        }

		depth += d * 0.5 ;
		iter++;

    }

    vec3 col = vec3(1.0 - iter / 80.0);

    if(hit)
    col = pow(col, vec3(
        0.2 + 0.5 * sin(p.z * 33.0),
        0.7 + 0.5 * sin(p.z * 10.0),
        0.6)).bgr;

    fragColor = vec4(sqrt(col), hit? length(p.xy) : 0.0 );

}

