vec3 doBackground( vec2 uv )
{
    uv *= .8;
    uv.x = uv.x + 1.5;
    float noise = fractalNoise2d(uv);
    float plasma = .8*noise*(sin(uv.x)+cos(uv.y))/.2 + .5;

    vec3 col = vec3((sin(plasma)+1.)*.1,
                    (sin(plasma+.2)+1.)*.1,
                    (sin(plasma+.2)+1.)*.1);

    return mix(col, vec3(0.25, 0.24, .45), 0.1);
}

float hash( float n ) { return fract(sin(n)*753.5453123); }

float noise( vec3 x )
{
    vec3 p = floor(x);
    vec3 f = fract(x);
    f = f*f*(3.0-2.0*f);

    float n = p.x + p.y*157.0 + 113.0*p.z;
    return mix(mix(mix( hash(n+  0.0), hash(n+  1.0),f.x),
                   mix( hash(n+157.0), hash(n+158.0),f.x),f.y),
               mix(mix( hash(n+113.0), hash(n+114.0),f.x),
                   mix( hash(n+270.0), hash(n+271.0),f.x),f.y),f.z);
}

//------------------------------------------------------------------------
// Modelling 
//
// Defines the shapes (a sphere in this case) through a distance field, in
// this case it's a sphere of radius 1.
//------------------------------------------------------------------------
float sdSphere( vec3 p )
{

    float n = exp(noise(vec3(p * 5.))) ;

    return length(p) - (1.*( sin(iTime)/4. + 1.) + n * 0.13)*.9;
}

//------------------------------------------------------------------------
// Material 
//
// Defines the material (colors, shading, pattern, texturing) of the model
// at every point based on its position and normal. In this case, it simply
// returns a constant yellow color.
