float calcSoftshadow( in vec3 ro, in vec3 rd );

vec3 doLighting( in vec3 pos, in vec3 nor, in vec3 rd, in float dis, in vec3 mal )
{
    //vec3 lin = vec3(0.0);

    // key light
    //-----------------------------
    //vec3  lig = normalize(vec3(1.0,0.7,0.9));
    //float dif = max(dot(nor,lig),0.0);
    //float sha = 0.0; if( dif>0.01 ) sha=calcSoftshadow( pos+0.01*nor, lig );
    //lin += dif*vec3(4.00,4.00,4.00)*sha;

    // ambient light
    //-----------------------------
    //lin += vec3(0.50,0.50,0.50);


    // surface-light interacion
    //-----------------------------
    //vec3 col = mal*lin;


    vec3 lin = vec3(0.0);

    // key light
    //-----------------------------
    vec3  view = normalize(-rd);
    vec3  lig1 = normalize(vec3(1.0,0.7,0.9));
    vec3  lig2 = normalize(vec3(1.0,0.9,0.9)*-1.);
    vec3  lig3 = normalize(vec3(1.4,2.4,1.3));

    float spc1 = gaussianSpecular(lig1, view, nor, 0.95)*0.5;
    float dif1 = max(0., orenNayarDiffuse(lig1, view, nor, -20.1, 1.0));
    float sha1 = 0.0; if( dif1>0.02 ) sha1=calcSoftshadow( pos+0.01*nor, lig1 );
    vec3  col1 = vec3(1.,4.2,2.);
    lin += col1*spc1+dif1*col1*sha1;

    float spc2 = gaussianSpecular(lig2, view, nor, 0.95);
    float dif2 = max(0., orenNayarDiffuse(lig2, view, nor, -20.1, 1.0));
    float sha2 = 0.0; if( dif2>0.02 ) sha2=calcSoftshadow( pos+0.01*nor, lig2 );
    vec3  col2 = vec3(2.0,1.05,1.55);
    lin += col2*spc2+dif2*col2*sha2;

    float spc3 = gaussianSpecular(lig3, view, nor, 0.95);
    float dif3 = max(0., orenNayarDiffuse(lig3, view, nor, -20.1, 1.0));
    float sha3 = 0.0; if( dif3>0.02 ) sha3=calcSoftshadow( pos+0.01*nor, lig3 );
    vec3  col3 = vec3(0.50,1.05,2.15);
    lin += col3*spc3+dif3*col3*sha3;

    // ambient light
    //-----------------------------
    lin += vec3(0.05);


    // surface-light interacion
    //-----------------------------
    vec3 col = mal*lin;

    // fog    
    //-----------------------------
	col *= exp(-0.01*dis*dis);

    return col;
}

float calcIntersection( in vec3 ro, in vec3 rd )
{
	const float maxd = 20.0;           // max trace distance
	const float precis = 0.001;        // precission of the intersection
    float h = precis*2.0;
    float t = 0.0;
	float res = -1.0;
    for( int i=0; i<90; i++ )          // max number of raymarching iterations is 90
    {
        if( h<precis||t>maxd ) break;
	    h = sdSphere( ro+rd*t );
        t += h;
    }

    if( t<maxd ) res = t;
    return res;
}

vec3 calcNormal( in vec3 pos )
{
    const float eps = 0.002;             // precision of the normal computation

    const vec3 v1 = vec3( 1.0,-1.0,-1.0);
    const vec3 v2 = vec3(-1.0,-1.0, 1.0);
    const vec3 v3 = vec3(-1.0, 1.0,-1.0);
    const vec3 v4 = vec3( 1.0, 1.0, 1.0);

	return normalize( v1*sdSphere( pos + v1*eps ) + 
					  v2*sdSphere( pos + v2*eps ) + 
					  v3*sdSphere( pos + v3*eps ) + 
					  v4*sdSphere( pos + v4*eps ) );
}

float calcSoftshadow( in vec3 ro, in vec3 rd )
{
    float res = 1.0;
    float t = 0.0005;                 // selfintersection avoidance distance
	float h = 1.0;
    for( int i=0; i<40; i++ )         // 40 is the max numnber of raymarching steps
    {
        h = sdSphere(ro + rd*t);
        res = min( res, 64.0*h/t );   // 64 is the hardness of the shadows
		t += clamp( h, 0.02, 2.0 );   // limit the max and min stepping distances
    }
    return clamp(res,0.0,1.0);
}

mat3 calcLookAtMatrix( in vec3 ro, in vec3 ta, in float roll )
{
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(sin(roll),cos(roll),0.0) ) );
    vec3 vv = normalize( cross(uu,ww));
    return mat3( uu, vv, ww );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (-iResolution.xy + 2.0*fragCoord.xy)/iResolution.y;
    vec2 m = iMouse.xy/iResolution.xy;

    //-----------------------------------------------------
    // camera
    //-----------------------------------------------------

    // camera movement
    vec3 ro, ta;
    doCamera( ro, ta, iTime, m.x );

    // camera matrix
    mat3 camMat = calcLookAtMatrix( ro, ta, 0.0 );  // 0.0 is the camera roll

	// create view ray
	vec3 rd = normalize( camMat * vec3(p.xy,2.0) ); // 2.0 is the lens length

    //-----------------------------------------------------
	// render
    //-----------------------------------------------------

	vec3 col = doBackground(p);

	// raymarch
    float t = calcIntersection( ro, rd );
    if( t>-0.5 )
    {
        // geometry
        vec3 pos = ro + t*rd;
        vec3 nor = calcNormal(pos);

        // materials
        vec3 mal = doMaterial( pos, nor );

        col = doLighting( pos, nor, rd, t, mal );
	}

	//-----------------------------------------------------
	// postprocessing
    //-----------------------------------------------------
    // gamma
	col = pow( clamp(col,0.0,1.0), vec3(0.4545) );

    fragColor = vec4( col, 1. );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 nuv = uv * 2.0 - 1.0;

    float bpm = 1.0 / 60.0 * 108.0;
    float t = iTime;
    float bt = t * bpm * 3.1415926535 * 2.0;

    fragColor.x = cos(bt);

    vec3 col0 = vec3(0.9, 0.6, 0.3);
    vec3 col1 = vec3(0.9, 0.7, 0.3);

    fragColor.xyz = col0 * 0.5;
    fragColor.xyz += col1 * pow(abs(cos(bt * 0.5)), 3.0) * 0.3;

    vec3 col2 = vec3(1.0, 1.0, 1.0);

    vec2 nuvd = nuv * vec2(cos(bt * length(nuv)), sin(bt * length(nuv) * 0.2));

    fragColor.xyz += col2 * (1.0 - length(nuvd));

    float s0 = texture(iChannel1, uv * 0.05 + vec2(sin(bt / 32.0) * 0.5, sin(bt / 96.0) * 0.2)).x;
    s0 = pow(s0, 2.0);
    s0 = step(pow(s0, 4.0), 0.001 * step(fract(bt), 0.1 * sin(bt) * length(nuv)));
    s0 = s0 * pow(texture(iChannel0, vec2(0.1, 0.25)).x, 2.0) * 4.0;

    fragColor.xyz += col2 * s0 * 0.2;
}

/*
	Title: Simple Sierpinski fractal
	Tags: fractal
	Description: Sierpinski triangle created with the bitwise AND operation.
*/

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float size = min(iResolution.x, iResolution.y);
    vec2 p = fragCoord.xy - (iResolution.xy - size) / 2.0;

    vec3 col = vec3(0.0);
    if(p.x >= 0.0 && p.y >= 0.0 && p.x < size && p.y < size) {
        vec2 z = p / exp2(fract(iTime));
    	uint res = uint(z.x) & uint(z.y);

        vec3 fg = vec3(1.0, 0.0, 0.0);
        vec3 bg = 1.5 * vec3(0.0, 0.0, 1.0) / log2(float(res));
    	col = res == 0U ? fg : bg;
    } 

    fragColor = vec4(col,1.0);
}

