void basis(in vec3 n, out vec3 f, out vec3 r) {
	float a = 1./(1. + n.z);
    float b = -n.x*n.y*a;
    f = vec3(1. - n.x*n.x*a, b, -n.x);
   	r = vec3(b, 1. - n.y*n.y*a , -n.y);
}
vec3 norm3(sampler2D iChannel, in vec3 comp, in vec3 px, in vec2 uv) {
	float s01 = texComp(iChannel, uv + px.xy, comp), s21 = texComp(iChannel, uv + px.zy, comp),
		s10 = texComp(iChannel, uv + px.yx, comp), s12 = texComp(iChannel, uv + px.yz, comp);
	vec3 va = normalize(vec3(px.xy*4., s21 - s01)),
		vb = normalize(vec3(px.yx*4., s12 - s10));
	return cross(va, vb).xzy;
}


////////////////////// Quadric functions //////////////////////
float sd(in vec3 l, in qdr q) {
    //this isnt actually the distance
    return length(sqrt(q.c.xyz) * (l - q.l)) - sqrt(-q.c.w);
}
vec2 map(in vec3 l, in qdr q) {
	//https://en.wikipedia.org/wiki/Quadric#Projective_geometry ???
    return (l.xz - l.y) - (q.l.xz - q.l.y) ;
}
vec3 nrm(in vec3 l, in qdr q) {
    return normalize(q.c.xyz*q.c.xyz * (l - q.l));
}
seg rayseg(in ray r, in qdr q) {
	vec3 o = r.o - q.l;
    float a = dot(q.c.xyz, r.d * r.d),
    	  b = dot(q.c.xyz * 2., o * r.d),
    	  c = dot(q.c.xyz, o * o) + q.c.w;
    if (a != 0.) {
        float h = b * b - 4. * a * c;
        if (h < 0.) return nullSeg;
        h = sqrt(h);
        return seg(vec2(-b-h, -b+h) * (.5 / a), ivec2(q.d, -q.d));
    }
    return seg(vec2(-c / b), ivec2(q.d));
}


////////////////////// Trace functions //////////////////////
vec2 lt(in seg s) {
    if (s.t.x < s.t.y && s.t.x > 0.) return vec2(s.t.x, float(s.d.x));
    else if (s.t.y > 0.) return vec2(s.t.y, float(s.d.y));
    return nullT;
}
void lt(inout hit o, in hit h) {
	if (h.d < o.d) o = h;
}

