#define _trcPOM(r, j) hit h = trace(r, o); if (h.d >= zfar) return h; sa += h.d*.01; vec3 l = v30; vec2 uvw = v20; float depth = 0., height = 0., ld = 0., mar = 0., p = heightScale * sa / -dot(r.d, h.n); p += ms * sign(p); for (int i = 0; i < 32; i++) { mar += p; l = h.l + r.d*mar; uvw = map(l, o); depth = -sd(l, o); height = heightScale - heightScale * texComp(heightMap, fract(uvw*scale), comp); if (depth > height || depth < 0.) break; ld = height - depth; } if (depth < height || depth < 0.) return nullHit; float cd = depth - height, lerp = 1. - clamp(ld / (ld + cd), 0., 1.); mar -= p*lerp; l = h.l + r.d*mar; h.l = l; h.d += mar; vec3 px = 1.0001 / iResolution.yyy * vec3(-1., 0., 1.); uvw = map(l, o) 
hit tracePOM(in ray r, in qdr o, in sampler2D heightMap, in vec3 comp, in float scale, in float heightScale) {
    float ms = .05, sa = .05;
    _trcPOM(r, o);
	mat3 rot = mat3(v30, nrm(l, o), v30);
    basis(rot[1], rot[0], rot[2]);
	h.n = norm3(heightMap, comp, px, fract(uvw*scale)) * vec3(-1., 1., -1.) * inverse(rot);
	return h;
}


////////////////////// Scene //////////////////////
hit traceScene(in ray r) {
	hit ret = nullHit;
    // sort by number of pixels that will be seen to reduce work
    for (int i = 0; i < 5; i++) {
        for (int j = 0; j < 5; j++) {
			lt(ret, tracePOM(r, qdr0, iChannel0, vec3(1.,0.,0.), .1, 1.));
    		qdr0.l.y -= 10.;
            qdr0.c.x += eps;

            lt(ret, tracePOM(r, qdr1, iChannel0, vec3(1.,0.,0.), .1, 1.));            
    		qdr1.l.x -= 10.;
            qdr1.c.y += eps;

            lt(ret, tracePOM(r, qdr2, iChannel0, vec3(1.,0.,0.), .1, 1.));
    		qdr2.l.y -= 10.;
            qdr2.c.z += eps;
        }
        qdr0.l.y += 50.;
        qdr0.c.x -= eps*5.;
        qdr0.l.z -= 10.;

        qdr1.l.x += 50.;
        qdr1.c.y -= eps*5.;
        qdr1.l.z -= 10.;

        qdr2.l.y += 50.;
        qdr2.c.z -= eps*5.;
        qdr2.l.x -= 10.;
    }
    return ret;
}


////////////////////// Dynamic material implementation //////////////////////
mat mqdr(in ray r, inout hit h) {
    float lp = 1000./(1.+h.d*h.d);
    return mat(
		v30,
		vec3(max(.05*lp, lp*-dot(h.n, r.d))) * tex(iChannel1, (h.l.xz-h.l.y)*.1).rgb,
		v20, v20,
		_mqdr.r, 0., _mqdr.d);
}
mat dbg(in ray r, inout hit h) {
    return mat(
		v30,
		max(v30, h.n),
		v20, v20,
		_dbg.r, 0., _dbg.d);
}

//updates dynamic materials (ghetto, find better solution)
void updateMaterials(inout hit h, in ray r) {
	if (h.m.d < 40) return;
	else if (h.m.d == _mqdr.d) h.m = mqdr(r, h);
	else if (h.m.d == _dbg.d) h.m = dbg(r, h);
}

//here we go
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord.xy / iResolution.xy * 2. - 1.;
	uv.y *= iResolution.y / iResolution.x;

	//update camera
    float sg = sin(iTime*.5), cg = cos(iTime*.4);
	vec3 l = vec3(41.*cg, 21.*(1.-cg*sg), 31.*sg);
    mat3 b = mat3(v30, v30, normalize(vec3(-25.) - l));
    basis(b[2], b[0], b[1]);
    vec3 d = b * normalize(vec3(uv, 1.));

    //begin tracing
	hit res;
	ray r = ray(l, d, _air.a, _air);
	//trace scene
	res = traceScene(r);
	//update dynamic materials
	updateMaterials(res, r);

    //material color
	vec3 final = r.c * res.m.e;
    //sky color and fog
    final = mix(final, vec3(.3,.7,1.)+max(-.3, dot(d, vec3(0., -sc45, sc45))), res.d*res.d*.00008);
    float eye = clamp(pow(dot(uv, uv), 2.), 0., 1.);
    fragColor = vec4(pow(final*EXPOSURE, vec3(GAMMA+eye)), 1.);
}

#define TIME mod(iTime, 26.0)
#define drum pow(1.0 - 2.0 * mod(TIME, 0.5), 5.0)

vec2 un(vec2 a, vec2 b)
{
	return a.x < b.x ? a : b;
}

float sdBox( vec3 p, vec3 b )
{
  vec3 d = abs(p) - b;
  return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}

float udRoundBox( vec3 p, vec3 b, float r )
{
  return length(max(abs(p)-b,0.0))-r;
}

float sdCappedCylinder( vec3 p, vec2 h )
{
  vec2 d = abs(vec2(length(p.xz),p.y)) - h;
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

float sdTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

float length8( vec2 p )
{
	p = p*p; p = p*p; p = p*p;
	return pow( p.x + p.y, 1.0/8.0 );
}

float sdTorus88( vec3 p, vec2 t )
{
  vec2 q = vec2(length8(p.xz)-t.x,p.y);
  return length8(q)-t.y;
}

float sdHexPrism( vec3 p, vec2 h )
{
    vec3 q = abs(p);
    return max(q.z-h.y,max((q.x*0.866025+q.y*0.5),q.y)-h.x);
}

float sdCylinder( vec3 p, float r )
{
  return length(p.xz)-r;
}

float udBox( vec3 p, vec3 b )
{
  return length(max(abs(p)-b,0.0));
}

float sdSphere( vec3 p, float r)
{
  return length(p)-r;
}

mat3 rot(float x, float y, float z)
{
	float cx = cos(x);
	float sx = sin(x);
	float cy = cos(y);
	float sy = sin(y);
	float cz = cos(z);
	float sz = sin(z);
	mat3 xm = mat3(1, 0, 0,
					0, cx, -sx,
					0, sx, cx);
	mat3 ym = mat3(cy, 0, sy,
			  		0, 1, 0,
			  		-sy, 0, cy);
	mat3 zm = mat3(cz, -sz, 0,
					sz, cz, 0,
					0, 0, 1);
	return xm * ym * zm;
}

float specular(vec3 normal, vec3 light, vec3 viewdir, float s)
{
	float nrm = (s + 8.0) / (3.1415 * 8.0);
	float k = max(0.0, dot(viewdir, reflect(light, normal)));
    return  pow(k, s);
}



float smin( float a, float b)
{
	float k = 0.5;
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

float smink( float a, float b, float k )
{
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}
vec2 sun(vec2 a, vec2 b)
{
	float sm = smin(a.x,b.x);
	float ca = abs(sm -a.x);
	float cb = abs(sm -b.x);

	return ca < cb ? vec2(sm, a.y) : vec2(sm, b.y);
}

vec2 sunk(vec2 a, vec2 b, float k)
{
	float sm = smink(a.x,b.x, k);
	float m = min(a.x, b.x);
	float ca = abs(sm -a.x);
	float cb = abs(sm -b.x);
	return ca < cb ? vec2(sm, a.y) : vec2(m, b.y);
}


#define TEX 0.2
#define SIZE 8.0
#define PI 3.1415

#define MAT_WALL 1.0
#define MAT_SPIN 2.0
#define MAT_GROUND 3.0


vec2 ground(vec3 p) {
	p -= vec3(0,0,5);
	p.y += 10.0*TIME;
	float s = 1.0;
	vec3 q = mod(p, s) - s * 0.5;
	q.z = p.z;

	q.z += sin(p.x) * 0.5;
	q.z += sin(p.y) * 0.5;
	float d = udRoundBox(q, vec3(s * 0.3), s * 0.3);
	return vec2(d, MAT_GROUND);
}

vec2 spin(vec3 p, vec3 rd, float dir) {
	p.x = p.x == 0.0 ? 0.00001 : p.x;

	float len = length(p.xy);
	float lenSize = 6.0;
	float lenY = mod(len, lenSize) - lenSize * 0.5;
	float lenPart = floor(len / lenSize);
	if (lenPart < 0.5 || lenPart > 2.5) {
		return vec2(99999,  MAT_SPIN);
	}
	float angle = atan(p.y, p.x);
	float numParts =  floor( 5.5*lenPart);
	float partSize = PI / numParts;
	angle += PI;
	float r = dir * sign(cos(lenPart*PI))* TIME * 0.4 / lenPart;
	float part = floor(mod(angle + r + (lenPart < 1.5 ? partSize / 2.0 : 0.0), PI * 2.0) / partSize);

	angle = mod(0.3*lenPart + angle + r, partSize) + partSize * 0.5 * max(0.0, numParts - 1.0);


	vec3 newPos = vec3(len * cos(angle), lenY * sin(angle), p.z);
	float d = sdSphere(newPos, 1.0);
	return vec2(d, MAT_SPIN);
}



vec2 map(vec3 p, vec3 rd) {
	vec2 res = spin(p ,rd, 1.0);
	res = un(ground(p - vec3(0, 0, 0)), res);
 	return res;
}

vec4 roofLight(vec3 p) {
	float music = drum; 
	float s = 15.0;
	p.y += 10.0*TIME;
	vec3 q = mod(p, s) - s * 0.5;
	q.z = p.z;
	q *= rot(0.0,0.0,TIME * 5.0 + p.x*0.1 + p.z*0.1);
	vec3 lightCol = vec3(0.5 + 0.5 * sin(p.x), 0.5 + 0.5 * sin(p.y), 0.1);
	float dis = sdCappedCylinder(q.zxy - vec3(-10, 0, 0), vec2(0.05, 5.0));
	float distanceToL = max(0.0001, dis);
	vec3 point = lightCol * 5.0/(0.1*distanceToL + 0.3*distanceToL*distanceToL);

	return vec4(point, distanceToL);
}

vec4 sunLight(vec3 pos) {
	vec3 lightCol = vec3(1.0,0.8, 0.5*(cos(TIME) + 1.0));

	float music = drum;
	float mdis = sdSphere(pos - vec3(0.0,0.0,0.5*sin(10.0*TIME)), 0.8 + 0.8 * music);


	float distanceToL = max(0.0001, mdis);
	vec3 point = lightCol * (80.0 + 80.0 * music)/(distanceToL*distanceToL);

	return vec4(point, distanceToL);
}


vec3 evaluateLight(vec3 pos, inout float dis)
{
	vec4 sun = sunLight(pos);
	vec4 rl = roofLight(pos);
	dis = min(sun.w, rl.w);
	return sun.xyz + rl.xyz;
}

void addLightning(inout vec3 color, vec3 normal, vec3 eye, vec3 pos) {
	vec3 lpos = vec3(0, 0,0);

	float dis = length(lpos - pos);
	vec3 invLight = normalize(lpos - pos);
	float diffuse = max(0.0, dot(invLight, normal));
	float spec = specular(normal, -invLight, normalize(eye - pos), 220.0);

	float str = 1.0/(0.1 + 0.01*dis + 0.1*dis*dis);
	float tmp = 0.0;
	str = 1.0;
	color =  color * (0.0 + 0.8*diffuse*evaluateLight(pos, tmp).xyz) + spec*str;
}



vec3 getNormal(vec3 p, vec3 rd)
{
	vec3 normal;
    vec3 ep = vec3(0.01, 0, 0);
    normal.x = map(p + ep.xyz, rd).x - map(p - ep.xyz, rd).x;
    normal.y = map(p + ep.yxz, rd).x - map(p - ep.yxz, rd).x;
    normal.z = map(p + ep.yzx, rd).x - map(p - ep.yzx, rd).x;
    return normalize(normal);

}

float occlusion(vec3 p, vec3 normal, vec3 rd)
{
	float o = clamp(2.0*map(p + normal * 0.5, rd).x, 0.0, 1.0);
	return 0.2 + 0.8*o;
}



vec3 raymarch(vec3 ro, vec3 rd, inout vec3 finalPos, vec3 eye) {
	float t = 0.0;
	const int maxIter = 100;
	const float maxDis = 300.0;
	float d = 0.0;
	vec3 p = vec3(-1.0, -1.0, -1.0);
	vec3 col = vec3(0);
	const int jumps = 3;
	float ref = 1.0;
	vec3 scatteredLight = vec3(0.0);
	float transmittance = 1.0;
	for (int j = 0; j < jumps; j++) {
		for (int i = 0; i < maxIter; i++) {
			p = ro + rd * t;

			vec2 res = map(p, rd);
			d = res.x;
			float fogAmount = 0.01;
			float lightDis = -1.0;
			vec3 light = evaluateLight(p, lightDis);
			d = min(min(d, 1.0), max(lightDis, 0.05));
			vec3 lightIntegrated = light - light * exp(-fogAmount * d);
			scatteredLight += transmittance * lightIntegrated;
			transmittance *= exp(-fogAmount * d);

			t += d;
			float m = res.y;
			bool end = i == maxIter - 1 ||t > maxDis;
			if (d < 0.01 || end) {
				vec3 c = vec3(1);
				vec3 normal = getNormal(p, rd);
				if (m == MAT_WALL) {
					c = vec3(1,0,0);
				} else if (m == MAT_SPIN) {
					c = vec3(0.5);
				} else if (m == MAT_GROUND) {
					vec3 q = floor(p);
					c = vec3(0.3,0.3,1);
				}

				c *= occlusion(p, normal, rd);
				addLightning(c, normal, eye, p);
				if (end) {
					transmittance = 0.0;
				}
				col = mix(col, transmittance * c + scatteredLight, ref);
				if (m == MAT_SPIN) {
					ref *= 0.8;
				} else {
					ref = 0.0;
				}
				rd = reflect(rd, getNormal(p, rd));
				ro = p + rd*0.05;
				t = 0.0;
				break;
			}
			if (t > maxDis) {
				break;
			}
		}

		if (ref < 0.1) {
			break;
		}
	}
	finalPos = p;
	return col;
}





