/**/

// Used to draw top borders on top of hex
float hexDist(vec2 p){p=abs(p);
 return max(dot(p,vec2(p322,.5)),p.y)-1.;}

//return  center of nearest hex from p;
vec2 nearestHexCenter(in vec2 p) {
 //integer coords in hex center grid
 vec2 hex_int=floor(vec2(p.x/p322,p.y));
 float sy=step(2.,mod(hex_int.x+1.,4.));
 hex_int+=mod(vec2(hex_int.x, hex_int.y + sy),2.);
 vec2 r=hex_int*vec2(p322,1.);//cartesian hexagon center
 p=p-r;
 //figure out which side of line we are on 
 //and modify hex center if necessary
 if(dot(abs(p),vec2(p322,.5))>1.){
  hex_int+=sign(p)*vec2(2,1);
  return hex_int*vec2(p322,1);
 }
 return r;}

//Flip normal if necessary to have positive dot product with d
vec2 alignNormal(vec2 n,vec2 d){return n*sign(dot(n,d));}
//Intersect a ray with a hexagon wall with normal n
vec3 rayHexIntersect(vec2 o,vec2 d, vec2 n){
 return vec3(n,(1.-dot(n,o))/dot(n,d));}

//Choose the vector whose z coordinate is minimal
vec3 rayMin(vec3 a,vec3 b){return mix(a,b,step(b.z,a.z));}

//Return the cell height for the given cell center
float height_for_pos(vec2 p){
 p*=.1;p+=p.x+p.y;
 //shift origin a bit randomly
 //p+=vec2(2.*sin(iTime*.3+.2),2.*cos(iTime*0.1+0.5));
 //cosine of distance from origin, modulated by Gaussian
 float q=dot(p,p),x=sqrt(q);
 return 6.*cos(x*.2+iTime)*exp(-q/128.);   
}

//return fully saturated hue.rgb of [h]; 0=1=red, 1/3=green, 2/3=blue 
vec3 hue(float h){return clamp(abs(mod(h*6.+vec3(0,4,2),6.)-3.)-1.,0.,1.);}
//return color.rgb of c.xyz .x=hue .y=saturation .z=variance       
vec3 hsv2rgb(vec3 c){return c.z*mix(vec3(1),hue(c.x),c.y);}
//return color.rgb of c.xyz .x=hue .y=saturation .z=lightness
vec3 hsl2rgb(vec3 c){return c.z+c.y*(hue(c.x)-.5)*(1.-abs(2.*c.z-1.));}
